css:
@-moz-document domain("hipchat.com") {
p { 
    margin: 0px!important;
}
body.chat #main_chat { 
    background-color: #000!important;
}
body.char #main_chat #chats div.chat_display {
    background-color: #000!important; 
}
body.chat .chatBlock a {
  color: #6897BB!important;
}
#chats {
    background-color: #000!important;
}
#chats div.chatText {
    background-color: #000!important;
}
#chats * { 
    font-family: Consolas, Menlo!important;
    font-size: 13px!important;
}
#chats p {
    font-family: Consolas, Menlo!important;
    font-size: 13px!important;
}
#chats div.chatBlock {
    border: #FFC66A solid 1px!important;
    border-radius: 8px!important;
    margin: 16px 4px!important;
}
#chats div.chatBlock p,#chats div.chatBlock div {
    margin: 0px!important;
}
#chats div.chatBlock p.non-first {
    margin-top: 0px!important;
}
#chats div.chatBlock td {
    padding: 0px!important;
    display: block!important;
}
#chats div.me {
    background-color: #444!important;
    border-top: #FFC66A solid 1px!important;
    color: #fff!important;
}
#chats div.them {
    background-color: #000!important;
    color: #fff!important;
}
#chats td.messageBlock {
    border-left: 0px!important;
}
#chats div.me td.messageBlock {
    margin-bottom: 0px!important;
    border-left: 0px!important;
}
#chats div.me td.nameBlock {
    background-color: #444!important;
    border-right:0px!important;
    border-top:0px!important;
    text-align: normal!important;
}
#chats table {
    padding: 8px!important;
}
#chats td.nameBlock {
    width: 100%!important;
    background-color: #000!important;
    border-top: 0px!important;
    border-right: 0px!important;
    vertical-align: normal!important;
    font-weight: normal!important;
    text-align: left!important;
    border-bottom: 1px solid #CC7832!important;
}
#chats td.nameBlock p {
    color: #CC7832!important;
    width: 100%!important;
    overflow:hidden!important;
    white-space:nowrap!important;
    text-overflow:ellipsis!important;
    font-weight: 700!important;
}
#chats div.me td.nameBlock p {
    color: #fff!important;
}
#chats div.infoBlock p.timeBlock {
    color: #888!important;
    text-align: left!important;
    float: none!important;
    font-size: 13px!important;
}
#chats div.infoBlock td.nameBlock {
    display: none!important;
}
#chats td.messageBlock > p.timeBlock {
    color: #888!important;
}
#chats p.timeBlock,#chats p.timeBlock a {
    color: #AAA!important;
    text-align: left!important;
}
#chats div.systemMessage,#chats div.systemMessage-yellow {
    background-color: #000!important;
    border-top: 0px!important;
}
#chats div.systemMessage td.nameBlock,#chats div.systemMessage-yellow td.nameBlock {
    color: inherit;
    background-color: #000!important;
    border-top: #FFC66A solid 1px!important;
    border-right: 0px!important;
}
#chats div.systemMessage td.messageBlock,#chats div.systemMessage-yellow td.messageBlock {
    border-left: 0px!important;
}
#chats div.systemMessage-gray {
    border-top: #FFC66A solid 1px!important;
}
#chats div.systemMessage-gray td.messageBlock {
    background-color: #000!important;
    border-left: 0px!important;
    color: #fff!important;
}
#chats div.systemMessage-gray td.nameBlock {
    background-color: #000!important;
    border-top: 0px!important;
    border-right: 0px!important;
}
#chats div.welcomeMessage {
    background-color: #000!important;
    border-top: 0px!important;
    border: 0!important;
}
#chats div.welcomeMessage td.nameBlock {
    color: inherit;
    background-color: inherit;
    border-top: 0px!important;
    border-right: 0px!important;
    display: none!important;
}
#chats div.welcomeMessage td.messageBlock {
    margin-bottom: 0px!important;
    border-left: 0px!important;
}
#chats div.chatState {
    padding:0px!important;
    display: none!important;
}
#chats span.atTag {
    border: 0px!important;
    background: inherit!important;
    color: #6897BB!important;
}
#chats span.atTagMe {
    background: inherit!important;
}
#chats div.image {
    margin: 0px!important;
}
#chats div.dateDivider {
    background: #000!important;
    color: #888!important;
    margin: 4px 4px 4px 14px!important;
    padding: 3px 0!important;
    line-height: 20px!important;
    font-weight: normal!important;
    font-family: Consolas, Menlo!important;
    font-size: 13px!important;
    text-align: left!important;
    border-top: 0px!important;
}
#chats div.chatDivider {
    background-color:inherit;
    color: inherit;
    height: 0px!important;
    border-top: 0px!important;
}
#chats div.chatBlock div.preview {
    background: normal!important;
}
.chatBlock table {
    border-radius: 8px!important;
    border: 0px!important;
}
.me table {
    background-color: #444!important;
}
.chat_display {
    background-color: #000!important;
}
div#chats {
    background-color: #000!important;
}
#chats div.infoBlock {
    border: 0px!important;
}
.chatBlock table {
    padding: 8px!important;
}
#chats .systemMessage td.nameBlock p {
    color: #A5C25C!important;
}
#chats .welcomeMessage p {
    color: #888!important;
}
.prettyprint .kwd {
    color: #CC7832!important;
}
.prettyprint .typ {
    color: #FFC66A!important;
}
.prettyprint .pln {
    color: #FFF!important;
}
.prettyprint .pun {
    color: #FFF!important;
}
.prettyprint .lit {
    color: #6897BB!important;
}
.prettyprint .str {
    color: #A5C25C!important;
}
.prettyprint .com {
    color: #808080!important; 
}

.prettyprint .atn {
    color: #FFF!important;
}

.prettyprint .tag {
    color: #CC7832!important;
}

.prettyprint .atv {
    color: #A5C25C!important;
}
#chats td.messageBlock pre {
    margin: 0px;
}
}
