css:
@-moz-document domain(strims.pl) {
Body {background:#f3f3f3!important;}

.content {background-color: white; border: 0 solid; border-radius: 3px 3px 3px 3px;box-shadow: 0 3px 5px -3px #A8A8A8;margin-bottom: 22px;margin-top: 22px;padding: 20px 10px 20px 10px!important;} /* treści */

div#contents_add_strim_suggest {display:none;} /* ukrycie proponowanych */

.contents_filter { padding: 10px;} /* ikonka z filtrem /s/ */

#top_bar, #top_bar_more { background: black; padding-top: 5px; height: 25px;} /* górny pasek */

samp { background-color: transparent !important;opacity: 1 !important;text-shadow: 0px 0px 7px black;transition: 0.3s all;}
samp * { opacity: 1 !important;}
samp:hover {text-shadow: none;}

#template.fixed #top{background: url(http://i.imgur.com/eBY5jVP.png); }
#top_bookmarks, #top_bookmarks a{background: white;}

#top_bookmarks a.selected{border: none;background: black;color: white;}
#top{border-bottom: 3px solid black;}
#top_section a{color:white;}
#top_inner_right{padding-left: 10px;padding-right: 21px;background: white;border-left: 3px solid black;border-top: 3px solid black;border-right: 3px solid black;}

#logo{background: url(http://i.imgur.com/kJCRrQ0.png);}
#logo img{opacity: 0.0;}
}
