css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("inoreader.com") {

/*Colors
	- Orange font - light : orange
	- Orange font - dark: orangered
	- BG - darkbrown: saddlebrown
	- BG - main - brown: sandybrown
	- BG - main - dark:
	- BG - articles - dark: 
	- Hover - articles
	- Selected article - black: black
	- Selected article - brown: tan
End colors*/


/*===background colors===*/
    
/*Article BG colors*/
    
    /*BG of Article - Open*/ .article_full_contents {background-color: #303030 !important}
    /*BG of article - lining - current, open*/ .ar.article_subscribed.ar_showed.article.article_current {background-color: black !important}
    /*BG of article - current, collapsed, unread*/ .ar.article_unreaded.article_subscribed.article_current.article_current_collapsed {background-color: black !important}
    /*BG of article - current, collapsed, read*/ .ar.article_subscribed.article.article_current.article_current_collapsed {background-color: black !important}
    /*BG of article - stripe*/ .article_stripe {background-color: orange}
    /*BG of Article - read, collapsed, unselected*/ .ar.article.article_subscribed {background-color: #100000 !important}
    
/* Panel BG colors*/
    
    /*BG of Side Pane - folder-selected*/ .parent_div_inner.parent_div_inner_selected {background-color: black !important}
    /*end of feed main reading area*/ #no_more_div {background-color: #291400 !important} 
/*main panel BG color after marking all read*/ .reader_pane_view_style_0 {background-color: #291400 !important}
/*header-footer
    
    /*main header/footer bg*/ #subscriptions_buttons {background-color: black !important}
    /*main header/footer bg*/ .open_sans {background-color: black !important; color: orangered !important}
    /*header-left side panel*/ #sb_tree_part {background-color: black}
    /*header-BG-buttons*/ .inno_toolbar_button {background-color: black !important; color: orangered !important}
    /*header-toolbar-spacers*/ .inno_toolbar_spacer {background-color:black !Important}
    
    
/*===font colors===*/

/*main articles*/
    /* open article title color*/ .article_title_link.bluelink {color: orangered !important} 
    /* color of closed articles*/ .article_header_title {color: orangered !important} 
    /*Font-articles-link colors*/ .bluelink {color:orangered !important}
    /*Font of no more article area*/ .no_more_articles {color: orangered !important}
     /*Font for closed article summary*/ .article_short_contents {color: white !important} 
    /*Font-article-paragraphs*/ .article_content {color: white !important}
    
    
/* side pane font colors*/
    /*unread folder color*/ .tree_node.tree_link_folder.tree_link.normal.tree_unreaded.tree_unseen {color: orangered !important} 
    /*unread folder*/ .tree_node.tree_link_folder.tree_link.tree_unreaded.selected.tree_unseen {color: orangered !important} 
    /*color of text for unread feed */ .tree_node.tree_link.normal.tree_unreaded {color: orangered !important}
    /* color of text for unread counts*/ .unread_cnt {color: orangered !important; 
                       font-weight: bold !important; 
                        font-size: 12px !important;
                        position: relative !important;
                        width: 20px !important;
                        height: 20px !important;
                        border-style: solid !important;
                        border-width: 1px !important;
                        border-color: #663300 !important;
                        }
    /*Font-sidepane-unread-feed2*/ .tree_node.tree_link.normal.tree_unreaded {color: orangered}
    
   
/*hover effects*/
    /*main article hover*/.ar:hover {background-color: black !important} 
    /*main article hover*/.ar_article:hover {background-color: black !important} 
    /*main article hover*/ .ar_header:hover {background-color: black !important}
    /*Read article hover*/ .ar.article.article_subscribed:hover {background-color: black !important}
    /*timestamp hover*/ .arrow_div:hover {background-color: black !important} 
    /* hover side pane feeds*/ .parent_div_inner:hover {background-color: black !important}
}
