css:
@-moz-document domain("my.secondlife.com") {

#premium, 
#web_column, 
#recommendations, 
#premium_upgrade, 
* [href*="googleads"], 
* [class*="advertisement"], 
#google-dfp-placement-wrapper, 
#header_inner ul#navigation li:nth-child(3).separator, 
#header_inner ul#navigation li.top_level a[href*="whatis?"] { 
display: none !important; 
}

#wrapper { 
background-image: url("http://i.imgur.com/xEwxW5W.png") !important; 
background-repeat: repeat-y !important; 
}

#main { 
width: 785px !important; 
}

#invite_friends_widget a[href*="/referrals/new"] { 
display: block !important; 
border-bottom: 1px solid #DFDFDF !important; 
position: relative !important; 
top: -31px !important; 
left: -10px !important; 
padding: 7px 35px !important; 
width: 140px !important; 
color: transparent !important; 
}

#invite_friends_widget { 
color: transparent !important; 
}

#invite_friends_widget a[href*="/referrals/new"]:hover:before { 
content: 'Invite your friends to explore Second Life. ' !important; 
background: #3A3A3A !important; 
-webkit-border-radius: 20px !important; 
white-space: nowrap !important; 
border: 1px solid #D6D6D6 !important; 
box-shadow: 0 1px 2px rgba(0, 0, 0, 0.7) !important; 
border-radius: 20px !important; 
position: relative !important; 
padding: 4px 8px !important;  
color: #75AE9B !important; 
font-size: 12px !important;  
z-index: 20 !important; 
left: -46px !important; 
top: 40px !important; 
font-weight: normal !important; 
}

#main .tabs.tabs3, 
.ui-tabs .ui-tabs-nav, 
#main .tabs.tabs2 { 
position: fixed; 
top: 40px;
margin: 0 auto; 
float: none; 
z-index: 9999;
width: 785px;
}

.ui-tabs .ui-tabs-nav li a { 
border-bottom: 1px solid #D3D3D3; 
}

#web.signed_in #wrapper #inner #main {
padding-top: 29px;
}

.thumbnail_link { 
float: none !important; 
}

.thumbnail_link img.thumbnail.small { 
display: none !important; 
width: 670px !important; 
cursor: default !important; 
}

.thumbnail_link img.thumbnail.large { 
display: inline !important; 
width: 670px !important; 
cursor: default !important; 
}

#header { 
position: fixed !important; 
width: 996px !important; 
z-index: +999999 !important; 
}

#inner { 
padding-top: 40px !important; 
}

#sidebar { 
position: fixed !important; 
}


#main { 
float: right !important; 
}

#profile span { 
text-transform: capitalize !important; 
}

#fl_image_zoom.simple_overlay img, 
#sl_image_zoom.simple_overlay img, 
#pick_image_zoom.simple_overlay img, 
#group_image_zoom.simple_overlay img { 
width: auto; 
}

#web.signed_in #wrapper #inner div#main .profile_header.clearfix .content .profile_actions.current_user a[href^="/settings/profile"]:before {
content: ""; 
display: inline-block; 
background: url("http://i.imgur.com/TVrYoAF.png"); 
background-position: -44px -204px; 
width: 15px; 
height: 13px;
margin-right: 2px; 
}

}

/*Other domains for easy finding through userstyles.org*/

@-moz-document domain("my.secondlife.com"), domain("marketplace.secondlife.com") { 
}
