css:
@-moz-document domain('wikipedia.org'), domain('wiktionary.org'), domain('wikimediafoundation.org') {

div#content .metadata,
div#content #toctitle .toctoggle,
div#content .navbox-title .collapseButton,
div#content .navbox-title .plainlinks,
div#content .mw-editsection, div#content .mw-editsection-like,
body {
  font-family: Ubuntu, Optima, Calbri, sans-serif;
}
 
div#content #firstHeading, div#content h1, div#content h2,
div#content #toc h2, div#content .toc h2,
.mw-headline,
#bodyContent,
.mw-body-content, .mw-content-text {
  font-family: Palatino, Times New Roman, Times, Georgia, serif;
}
 
pre, code, tt, kbd, samp, input[type='text'], textarea,
.mw-code {
  font-family: 'Ubuntu Mono', 'Envy Code R', 'Inconsolata', 'Anonymous Pro', 'Consolas', 'Andale Mono', 'Courier New', Courier, monospace;
}
 
#firstHeading {
  font-size: 250%;
}
#bodyContent {
  font-size: 100%;
  text-align: justify;
  hyphens: auto;
  -webkit-hyphens: auto;
}

}
