css:
@-moz-document url-prefix("http://www.nicovideo.jp/") {
/*追加*/
#topVideoInfo > .translation{display:none!important;}
#playlist,.owner #editorMenu, .ownerThreadEditor #editorMenu{display:none!important;}

/* kusa5用 */
#content {
  display: flex !important;
  flex-direction: column !important;
}
#playerContainerWrapper {
  order: 2;
}
#kusa5 {
  height: 410px !important;
  width: 640px !important;
  margin: 0 0 54px 0 !important;
}
#kusa5 > video {
  height: calc(100% - 50px) !important;
}
#kusa5 > .controle-panel {
  transform: none !important;
}

* {
  border-radius: 0 !important;
  box-shadow: none !important;
  text-shadow: none !important;
}

body {
  font-size: 12px !important;
  min-width: 0 !important;
  color: #393f3f !important;
}

body,
.parentVideoInfo {
  background: #fff !important;
}

p {
  margin: 0 !important;
  line-height: 1.375 !important;
}
a {
  color: #292f2f !important;
  text-decoration: underline !important;
}

a.watch:link {
  color: #596979 !important;
  font-weight: bold;
}
a.watch:visited {
  color: #292F2F !important;
}
[id$="Ads"],
[class$="Ads"]:not(.googleAds) {
  display: none !important;
}

/*■■■■
         siteHeader
              　　　■■■■*/

#siteHeader > #siteHeaderInner {
  min-width: 976px !important;
  max-width: 976px !important;
}
body.size_normal > #siteHeader > #siteHeaderInner {
  min-width: 1200px !important;
  max-width: 1200px !important;
}
#siteHeaderInner > ul > li > a {
  color: #fff !important;
}
/*プレミアム消し*/
#siteHeaderInner > .siteHeaderMenuList > #siteHeaderNotification > #siteHeaderNotificationPremium {
  display: none !important;
}

/*■■■■　
           content
              　　　■■■■*/
#content {
  background: transparent !important;
}
body:not(.full_with_browser) #content,
body:not(.full_with_browser) #bottomContentTabContainer {
  padding-left: 4px !important;
  padding-right: 4px !important;
}
body.size_normal:not(.full_with_browser) #content,
body.size_normal:not(.full_with_browser) #videoHeader,
body.size_normal:not(.full_with_browser) #bottomContentTabContainer,
body.size_normal:not(.full_with_browser) #outline,
body.size_normal:not(.full_with_browser) .outer {
  width: 1200px !important;
}
body.size_small:not(.full_with_browser):not(.videoExplorer) #content,
body.size_medium:not(.full_with_browser) #content,
body.size_medium:not(.full_with_browser) #videoHeader,
body.size_medium:not(.full_with_browser) #bottomContentTabContainer,
body.size_medium:not(.full_with_browser) #outline,
body.size_medium:not(.full_with_browser) .outer {
  width: 976px !important;
}

/*######### videoHeaderOuter ########*/
#videoHeaderOuter {
  padding: 0 !important;
}
/**** videoHeader ****/
#videoHeader {
  z-index: 1 !important;
  width: 974px !important;
  padding: 0 !important;
  margin: auto auto 6px
}
/* 説明欄開いてる時 */
#videoHeader.infoActive {
  margin-top: 90px !important;
}
/* 説明欄閉じてる時 */
#videoHeader:not(.infoActive) {
  margin-top: 74px !important;
}

#videoHeaderMenu {
  position: absolute !important;
       top: -78px !important;
      left: 56px !important;
  width: 478px !important;
  height: 48px !important;
  margin: 0 !important;
  background: url("data:image/png;base64,") no-repeat !important;
  background-position: left bottom !important;
}

/* 説明欄閉じてる時 */
#videoHeader:not(.infoActive) > #videoHeaderMenu {
            top: -62px !important;
}

/* 検索バー */
#videoHeaderMenu > .searchContainer > form > .searchText {
  background: transparent !important;
  width: 270px !important;
}
#videoHeaderMenu > .searchContainer > form > .searchText > .searchOptionButton {
  height: 14px !important;
  line-height: 14px !important;
  background: #333 !important;
  text-indent: 0 !important;
  overflow: hidden !important;
  position: absolute !important;
       top: -16px !important;
  z-index: 1 !important;
  color: #fff !important;
  border: 0 !important;
  font-size: 12px !important;
  text-decoration: none !important;
}
#videoHeaderMenu > .searchContainer > form > .searchText > .searchKeywordIcon {
  width: 5em !important;
  padding: 2px 3px 0px !important;
  left: 0 !important;
  box-sizing: initial !important;
}
#videoHeaderMenu > .searchContainer > form > .searchText > .searchKeywordIcon:before {
  content: "キーワード ";
}

#videoHeaderMenu > .searchContainer > form > .searchText > .searchTagIcon {
  width: 2em !important;
  left: calc(5em + 8px)  !important;
  padding: 2px 3px 0px !important;
  box-sizing: initial !important;
}

#videoHeaderMenu > .searchContainer > form > .searchText > .searchTagIcon:before {
  content: "タグ ";
}

#videoHeaderMenu > .searchContainer > form > .searchText > .searchInput {
  width: 236px !important;
  padding: 2px 26px 2px 2px !important;
  border: rgb(204, 204, 204) solid !important;
  border-width: 1px 0 1px 1px !important; 
  font-size: 16px !important;
}
#videoHeaderMenu > .searchContainer > form > .searchOption {
  display: block !important;
  position: absolute !important;
            top: -16px !important;
            left: 0 !important;
  height: 16px !important;
  font-size: 0 !important;
  border: none !important;
  background: transparent !important;
}
#videoHeaderMenu > .searchContainer > form > .searchOption > ul > li > a {
  display: block !important;
}
#videoHeaderMenu > .searchContainer > form > .searchOption > ul > li > a > span {
  float: left !important;
  width: auto !important;
  height: 14px !important;
  padding: 2px 3px 0 !important;
  background: #ddd !important;
  font-size: 12px !important;
}
#videoHeaderMenu > .searchContainer > form > .searchOption > ul > .searchKeyword > a {
  padding: 0 !important;
}
#videoHeaderMenu > .searchContainer > form > .searchOption > ul > .searchKeyword > a > span {
  margin-right: 2px !important;
}
#videoHeaderMenu > .searchContainer > form > .searchOption > ul > .searchKeyword > a > span:after {
  content: "キーワード";
  display: block !important;
  height: 14px !important;
  width: 5em !important;
}
#videoHeaderMenu > .searchContainer > form > .searchOption > ul > .searchTag > a > span:after {
  content: "タグ";
  display: block !important;
  height: 14px !important;
  width: 2em !important;
}

/* タイトル */
#videoHeaderDetail {
  margin: 0 !important;
}

#videoHeaderDetail > .ch_breadcrumb { /*チャンネル用*/
  position: absolute !important;
            top: 19px !important;
}
#videoHeaderDetail:not(.active) > .ch_breadcrumb { /*チャンネル用*/
  display: none !important;
}

#videoHeaderDetail > .videoDetailExpand {
  height: initial !important;
  min-height: 0 !important; /*chrome用*/
  padding: 0 !important;
}
/*説明欄閉じてる時*/
#videoHeaderDetail:not(.active) > .videoDetailExpand {
  top: 2px !important;
}
#videoHeaderDetail > .videoDetailExpand > h2,
#videoHeaderDetail > .videoDetailExpand > h2 > .videoHeaderTitle {
  font-size: 16px !important;
  max-width: initial !important;
}

#videoHeader.infoActive > .toggleDetailExpand {
  position: absolute !important;
            top: -14px !important;
            right: 0 !important;
}
/* 説明欄閉じてる時 */
#videoHeader:not(.infoActive) > .toggleDetailExpand {
  position: absolute !important;
            top: 2px !important;
            right: 0 !important;
  width: 200px !important;
  height: 26px !important;
}
.toggleDetailExpand {
  font-size: 0 !important;
}
.toggleDetailExpand .open {
  width: 200px !important;
  height: 26px !important;
  background: url("data:image/png;base64,") !important;
  display: block;
}
.toggleDetailExpand .close {
  width: 200px !important;
  height: 26px !important;
  background: url("data:image/png;base64,") !important;
  display: block;
}
/*説明欄閉じてる時*/
#videoHeaderDetail:not(.active) + .toggleDetailExpand + .shortVideoInfo {
  margin: 2px 0 6px !important;
}
.shortVideoInfo > .shortDescription {
  max-width: 680px !important;
  color: #696F6F !important;
}
.shortVideoInfo > .readMore.videoDetailOpenButton {
  color: #cc0000 !important;
}

/**** videoDetailInformation ****/
/*アニメーションはいらない*/
#videoHeaderDetail:not(.active) + .toggleDetailExpand + .shortVideoInfo + #videoDetailInformation {
  display: none !important; 
}

#videoDetailInformation {
  width: 950px !important;
  height: initial !important;
  margin: 47px 0 6px !important;
  border: 0 !important;
}
#topVideoInfo {
  margin: 0px 0 0 !important;
}

#topVideoInfo > .userProfile,
#topVideoInfo > .ch_prof {      /*チャンネル用*/
  position: absolute !important;
            top: 20px !important;
            right: 0px !important;
  width: 240px !important;
  height: 52px !important;
  margin: 0 !important;
  padding: 6px !important;
  border: 2px solid #999f9f !important;
  background: #e9efef !important;
  z-index: 1000088888 !important;
}
#topVideoInfo > .userProfile > .userIconContainer,
                               .symbol { /*チャンネル用*/
  width: 50px !important;
  margin-right: 6px !important;
}
#topVideoInfo > .userProfile > .userIconContainer > .userIconLink > .usericon,
                               .symbol > img {
  border: 2px solid #393f3f !important;
}

#topVideoInfo > .userProfile > .profile {
  width: 184px !important;
  height: auto !important;
  margin-top: -1px !important;
}

#topVideoInfo > .userProfile > .profile > h4 > .favVideoOwner {
  display: block !important;
}
#topVideoInfo > .userProfile > .profile > h4 > .favVideoOwner,
                                               .channelFavoriteLink { /*チャンネル用*/
  width: 100% !important;
  height: auto !important;
  margin: 3px 0 !important;
  padding: 0 !important;
  background: #161b1b !important;
  line-height: 1.375 !important;
  border: 0 !important;
  border-radius: 10px !important;
  font-size: 12px !important;
  overflow: hidden !important;
}
#topVideoInfo > .userProfile > .profile > h4 > .favVideoOwner > .favVideoOwnerStar,
                                                                .favoriteChannelStar { /*チャンネル用*/
  display: none !important;
}
#topVideoInfo > .userProfile > .profile > h4 > .favVideoOwner > .isNotFavoritedText,
#topVideoInfo > .userProfile > .profile > h4 > .favVideoOwner > .isFavoritedText,
                                                                .isNotFavoritedText, /*チャンネル用*/
                                                                .isFavoritedText { /*チャンネル用*/
  color: #fff !important;
}
#topVideoInfo > .userProfile > .profile > h4 > .userName {
  padding: 0 !important;
}
#topVideoInfo > .userProfile > .profile > h4 > .favRegisterWindow {
      top: -28px !important;
     left: -50px !important;
width: auto !important;
}
#topVideoInfo > .userProfile > .profile > h4,
#topVideoInfo > .userProfile > .profile > .favRegisterError + div {
  width: auto !important;
  margin: 0 !important;
  padding: 0 !important;
  line-height: 1.2em !important;
  white-space: nowrap !important;
}

#ch_prof > .info {   /*チャンネル用*/
  padding: 0 0 0 59px !important;
}


/* videoDescription description */
.videoDescription.description {
  width: initial !important;
  min-height: 1em !important;
  margin: 0 !important;
  padding: 8px !important;
  border: 2px solid #393f3f !important;
  line-height: 1.375em !important;
}
/* videoMainInfoContainer */
.videoMainInfoContainer {
  padding: 0 !important;
  border: 0 !important;
}
.parentVideoInfo {
  position: relative!important;
  width: initial !important;
  padding: 0 8px 8px!important;
  margin: -2px 0 0 !important;
  border: 2px solid #393f3f !important;
  border-top: 0px solid #ccc !important;
  float: none !important;
}
.parentVideoInfo > .videoThumb {
  width: 52px !important;
  margin-top: 8px !important;
  margin-left: 65px !important;
}
.parentVideoInfo > .videoThumb .videoThumbImg {
  height: 40px !important;
}
.parentVideoInfo > .cct {
  margin: 0 0 0 !important;
  padding: 8px 0 0 !important;
  border-top: 1px solid #ccc !important;
}
.parentVideoInfo >  .cct > .contentsTree {
  padding : 4px !important;
  margin-right: 80px !important;
  margin-left: -127px !important;
  border-radius: 5px !important;
  color: #fff !important;
  font-size: 12px !important;
  background: #020202 !important;
}
.parentVideoInfo > .cct > .contentsTree[style="display:none;"] + .contentsTreeIconLink {
  margin: 4px 0 0 !important;
  font-size: 12px !important;
}
.parentVideoInfo > .parentVideoTitle {
  margin-left: 138px !important;
}


#topVideoInfo .parentVideoInfo + div {
  position: absolute !important;
            top: -16px !important;
  width: 700px !important;
}
#topVideoInfo .parentVideoInfo + div > .videoThumb,
#topVideoInfo .parentVideoInfo + div > .videoInformation > .videoStats,
#topVideoInfo .parentVideoInfo + div > .videoInformation > .ranking > .icon {
  display: none !important;
}
#topVideoInfo .parentVideoInfo + div > .infoHeadOuter {
  float: left !important;
}
#topVideoInfo .parentVideoInfo + div > .infoHeadOuter,
#topVideoInfo .parentVideoInfo + div > .videoInformation {
  height: 1.2em !important;
}
#topVideoInfo .parentVideoInfo + div > .videoInformation:before {
  content: "\00A0…\00A0" ;
}
#topVideoInfo .parentVideoInfo + div > .infoHeadOuter > p {
  margin: 0 !important;
}
#topVideoInfo .parentVideoInfo + div > .infoHeadOuter > p > span > .videoPostedAt {
  font-weight: bold !important;
  color: #393f3f !important;
}
#topVideoInfo .parentVideoInfo + div > .videoInformation > .ranking {
  display: inline-block !important;
  line-height: 1.375 !important;
  border: none !important;
  padding: 0 !important;
  margin: 0 4px 0 0 !important;
}
#topVideoInfo .parentVideoInfo + div > .videoInformation > .ranking > .categoryName {
  font-size: 100% !important;
}
#topVideoInfo .parentVideoInfo + div > .videoInformation > .dicIcon > a > .dic {
  display: inline-block !important;
  width: 20px !important;
  height: 1.2em !important;
  padding: 0 !important;
  overflow: hidden !important;
  text-indent: -9999px !important;
}
#topVideoInfo .parentVideoInfo + div > .videoInformation > .dicIcon > a > .dic.enable {
  background: url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAA0AAAANCAYAAABy6+R8AAAAS0lEQVQokdWSsQ2AQAwDvdNNy07eyVT/ChLwpPiCSNdEPrlIBKSDJAmIkyVAPMRu05T8ASCHdJXe5lGqjOCephrwze6PTe07dX/vBBKDkUrWZdSTAAAAAElFTkSuQmCC") no-repeat !important;
}
#topVideoInfo .parentVideoInfo + div > .videoInformation > .dicIcon > a > .dic.disable {
  background: url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAA0AAAANCAYAAABy6+R8AAAAUklEQVQokbWSwQmAUAxDs9NbrOtkJ3eqB8FD/VgrWsil5JESKiAnkiQBuWW2AtL2AU6TTsh2KyAj4gqt5haqprr7DlqZ2/Mq9LiIV+39nzT9vR0sT5vo3HVXtQAAAABJRU5ErkJggg==") no-repeat !important;
}
/*共有ボタン*/
/*動画の詳細～ボタン*/
.hiddenInfo > .hiddenInfoTabHeader {
  display: none !important;
}
/*動画情報詳細他を常に表示*/
.hiddenInfo > .hiddenInfoTabContent,
.extraVideoInfo,
.supplementary {
  display: block !important;
}
.hiddenInfo > .hiddenInfoTabContent {
  position: absolute !important;
            top: 21px !important;
            right: auto !important;
  height: 1em !important;
  line-height: 1.0em !important;
  width: 950px !important;
  height: 0 !important;
}
.hiddenInfo > .hiddenInfoTabContent > .extraVideoInfo {
  padding: 0 !important;
  background: transparent !important;
}
.hiddenInfo > .hiddenInfoTabContent > .extraVideoInfo > .supplementary {
  margin: 0 !important;
  padding: 0 !important;
}
.hiddenInfo > .hiddenInfoTabContent > .extraVideoInfo > .supplementary > li:not(.community):not(.worldNewArrival):not(.mylistComment):not(.mylist){
  display: none !important;
}


.hiddenInfo > .hiddenInfoTabContent > .extraVideoInfo > .supplementary > .worldNewArrival:before {
  content: "動画説明" !important;
  background: linear-gradient(to bottom, #cbd0d0, #fff) !important;
  margin: 0 2px 0 8px !important;
  padding: 5px 7px 7px !important;
  box-shadow: 0 0 0 1px #fff inset !important;
  border: solid #393f3f !important;
  border-width: 2px 2px 0 !important;
  border-radius: 4px 4px 0 0!important;
 font-size: 12px !important;
  float: left !important;
  position: relative !important;
       top: 5px !important;
  color: #333 !important;
}

.hiddenInfo > .hiddenInfoTabContent > .extraVideoInfo.channelVideo > .supplementary >  .worldNewArrival { /*チャンネル用*/
  position: relative !important;
       top: 16px !important;
}
.hiddenInfo > .hiddenInfoTabContent > .extraVideoInfo > .supplementary > .worldNewArrival {
  display: block !important;
 font-size: 0 !important;
  height: 0 !important;
}
.hiddenInfo > .hiddenInfoTabContent > .extraVideoInfo > .supplementary > .worldNewArrival > .dateFormat,
.hiddenInfo > .hiddenInfoTabContent > .extraVideoInfo > .supplementary > .worldNewArrival > a {
  display: none !important;
}
.hiddenInfo > .hiddenInfoTabContent > .extraVideoInfo > .supplementary > .mylistComment,
.hiddenInfo > .hiddenInfoTabContent > .extraVideoInfo > .supplementary > .mylist {
  position: relative !important;
       top: 3px !important;
  margin: 0 !important;
  padding: 5px 7px 4px !important;
  background: linear-gradient(to bottom, #464c4c, #1b2121) !important;
  border-radius: 4px 4px 0 0 !important;
  float: left !important;
}
.hiddenInfo > .hiddenInfoTabContent > .extraVideoInfo.channelVideo > .supplementary > .mylistComment, /* チャンネル用 */
.hiddenInfo > .hiddenInfoTabContent > .extraVideoInfo.channelVideo > .supplementary > .mylist { /* チャンネル用 */
  top: 19px !important;
}
.hiddenInfo > .hiddenInfoTabContent > .extraVideoInfo > .supplementary > .mylist {
  margin-left: 2px !important;
}
.hiddenInfo > .hiddenInfoTabContent > .extraVideoInfo > .supplementary > .mylistComment > .mylistCommentLink,
.hiddenInfo > .hiddenInfoTabContent > .extraVideoInfo > .supplementary > .mylist > .openMylist  {
  padding: 0 !important;
  color: #fff !important;
  background: none !important;
  text-decoration: none !important;
}
.hiddenInfo > .hiddenInfoTabContent > .extraVideoInfo > .supplementary > .mylistComment > .mylistCommentLink > .icon,
.hiddenInfo > .hiddenInfoTabContent > .extraVideoInfo > .supplementary > .mylist > .openMylist > .icon {
  display: none !important;
}

.hiddenInfo > .hiddenInfoTabContent > .extraVideoInfo > .supplementary > .community > .icon {
  display: none !important;
}

/*生放送中 動画説明閉じてる時は消えてもらう*/
:not(.infoActive) > .nicoliveNotification.onair {
  display: none !important;
}

/*■■■■■■■■■■ 

　　　　　　　　タグ 
　　　　　　　　　
　　　　　　　　　　　　■■■■■■■■■■■*/
#videoTagContainer{
  min-width: 950px !important;
  width: 950px !important;
  min-height: 35px !important;
  height: auto !important;
  margin: 0 !important;
  padding: 0px !important;
}
#videoTagContainer > .tagInner {
  height: auto !important;
}

#videoTagContainer:not(.isEdit) #videoHeaderTagList {
  display: flex !important;
  flex-wrap: wrap;
  padding-left: 56px !important;
  background: url("data:image/png;base64,") no-repeat left center !important;
}


/*登録タグ*/
#videoHeaderTagList > .toggleTagEdit {
  order: 2;
  position: relative !important;
  width: auto !important;
  height: auto !important;
  margin: 0 !important;
  padding: 1px 0 0 !important;
  font-size: 0 !important;
  line-height: 1.375 !important;
  background: transparent !important;
}
#videoHeaderTagList > .toggleTagEdit > .toggleTagEditInner {
  display: inline !important;
}
#videoHeaderTagList > .toggleTagEdit .videoHeaderTagEditLink {
  color: #c00 !important;
  font-size: 12px !important;
  height: 100% !important;
}
/*タグの … 消す*/
#videoHeaderTagList > .moreTagsExist {
  display: none !important;
}
#videoHeaderTagList > .videoHeaderTag {
  display: block !important;
  height: auto !important;
  line-height: 1.375em !important;
  margin: 0 20px 0 0 !important;
  padding: 1px 2px 0 0 !important;
}
/*リアルタイムタグ更新がガタガタしないように*/
#videoHeaderTagList > .videoHeaderTag:not([style=""]) {
  display: none !important;
}
#videoHeaderTagList > .isCategory:before {
  content:"";
  padding-left: 23px !important;
  background: url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAABUAAAAJCAYAAADdA2d2AAAARklEQVQokWNgYGBgOMPA8J9amIFcA////49CYxiMTTEyHxkQ7VpSDUBXg83VWF2KTOOyiGxD8YUbVQ0l5HIMQwc8WeEzEACuuJ8JQiivJgAAAABJRU5ErkJggg==) no-repeat !important;
  background-position: left center !important;
}
/*大百科アイコン、editアイコン位置調整*/
#videoHeaderTagList > .videoHeaderTag > .tagControlContainer,
#videoHeaderTagList > .videoHeaderTag > .tagControlEditContainer {
  padding: 0 !important;
}
#videoHeaderTagList > .videoHeaderTag > .tagControlContainer > .dic > a > .enable {
  width: 13px !important;
  height: 13px !important;
  background: url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAA0AAAANCAYAAABy6+R8AAAAS0lEQVQokdWSsQ2AQAwDvdNNy07eyVT/ChLwpPiCSNdEPrlIBKSDJAmIkyVAPMRu05T8ASCHdJXe5lGqjOCephrwze6PTe07dX/vBBKDkUrWZdSTAAAAAElFTkSuQmCC") no-repeat !important;
}
#videoHeaderTagList > .videoHeaderTag > .tagControlContainer > .dic > a > .disable {
  width: 13px !important;
  height: 13px !important;
  background: url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAA0AAAANCAYAAABy6+R8AAAAUklEQVQokbWSwQmAUAxDs9NbrOtkJ3eqB8FD/VgrWsil5JESKiAnkiQBuWW2AtL2AU6TTsh2KyAj4gqt5haqprr7DlqZ2/Mq9LiIV+39nzT9vR0sT5vo3HVXtQAAAABJRU5ErkJggg==") no-repeat !important;
}
/*タグ編集の辺り左詰め*/
#tagEditContainer {
  margin: 0 !important;
  padding: 0 !important;
}
#tagEditContainer > .addTagForm > .editControlWrapper {
  width: auto !important;
}
#tagEditContainer > .addTagForm > .editControlWrapper > .tagInputText {
  width: auto !important;
  height: auto !important;
  padding: 0 !important;
  border: 1px solid #999f9f !important;
}
#tagEditContainer > .addTagForm > .editControlWrapper > .tagInputText > .newTagsText {
  margin: 0 !important;
}
/*固定ピン消し*/
#videoTagContainerPin {
    display: none !important;
}


/*編集時*/
#videoTagContainer.isEdit .tagInner {
