css:
/* i really want this to be global */

::selection {background-color:transparent;color: #789db6;}
::-moz-selection {background-color:transparent;color: #789db6;}
::-webkit-selection {background-color:transparent;color: #789db6;}

body {
   background:url("http://i.cubeupload.com/cMqW4U.png")repeat fixed  !important;
   background-color: #ffffff !important;
   background-position: 0% 60% !important;
 }

#header .tab.iconic a{
  background-color: #789db6 !important;
  border-radius: 60px !important;  
}

#content {  
   font-family: Consolas; color: #555;
   background: #789db6 !important;

 }


.notification  {
  	background: #789db6 !important; border: none !important; 
	color: #fff !important; font-size: 11px !important;
  }

.notification :link, .notification :active, .notification :visited {
  	color: #fff !important;
    text-decoration: none !important;
    border: none !important;
  }

.new_post_label .new_post_label_icon {
background-image: url('http://img14.imageshack.us/img14/2027/mikan04.png') !important;
background-size: 520px 100px !important;
margin-top: 0px !important;
margin-right: 15px !important;
opacity: 1 !important;
}

#content #new_post .new_post_label {
color: #789db6 !important;
line-height: 1.4em;
font-weight: bold !important;
font-size: .9em !important;

}

#right_column .controls_section li a,

#right_column .controls_section li a .count {

  color: #ffffff !important; 
}

#logo, #logo a {
  position: relative;
  z-index: 1;
  width: 270px !important;
  height: 71px !important;
  background-repeat: no-repeat;
  background: url("http://i.cubeupload.com/Z63z2j.png")no-repeat !important;
}

.controls_section li .posts:before {

  position: absolute !important;
  height: 30px !important;
  line-height: 28px !important;
  width: 100px !important;
  font: inherit !important;
}

a.posts  div.hide_overflow{
  visibility: hidden !important;
}

.controls_section li .posts:before {
  content: "Posts" !important;
}.controls_section li .followers:before {

  position: absolute !important;
  height: 30px !important;
  line-height: 28px !important;
  width: 100px !important;
  font: inherit !important;
}

a.followers  div.hide_overflow{
  visibility: hidden !important;
}

.controls_section li .followers:before {
  content: "Comrades" !important;
}.controls_section li .drafts:before {

  position: absolute !important;
  height: 30px !important;
  line-height: 28px !important;
  width: 100px !important;
  font: inherit !important;
}

a.drafts  div.hide_overflow{
  visibility: hidden !important;
}

.controls_section li .drafts:before {
  content: "Saved Work" !important;
}.controls_section li .activity:before {

  position: absolute !important;
  height: 30px !important;
  line-height: 28px !important;
  width: 100px !important;
  font: inherit !important;
}

a.queue  div.hide_overflow{
  visibility: hidden !important;
}

.controls_section li .queue:before {
  content: "Queue" !important;
}

.selection_nipple.white, .selection_nipple:not([class="selection_nipple white"]) {

  background: none !important;

  width: 0px !important;

  height: 0px !important;

  margin-top: 0px !important;

  border-bottom: solid 11px #789db6 !important;

  border-right: 11px solid transparent !important;

  border-left: 11px solid transparent !important;

}

#right_column.right_column::after {
  background: transparent;
}
div.search_form_row {
  background-color: #789db6;
  color: #fff;
  content: "Search" !important;}
.search_query {
    font-family: consolas; color: #fff;}
  
#return_to_top .return_to_top_icon{
  background: url('http://i.cubeupload.com/cKO7XN.png') no-repeat transparent!important}

#tab_switching .tab_notice, #header .tab .tab_notice, .popover_blogs .tab_notice, .blog_menu .selected_blog .tab_notice {
background-color: #27658f;
  color: #fff;
  border-radius:50px;
}

.like.liked.post_control:after {
background-position: 4px 3px !important;
background-size: auto auto !important;
background-image: url(http://i.cubeupload.com/NpfwEf.png);
}
div.post_control.like::after {
  background-position: 4px 3px !important;
background-size: auto auto !important;
background-image: url(http://i.cubeupload.com/PiJfDT.png);
  opacity:1;
}
.like.liked.post_control:after {
background-position: 4px 3px !important;
background-size: auto auto !important;
background-image: url(http://i.cubeupload.com/NpfwEf.png);
}
