css:
/* i really want this to be global */

::selection {background-color:transparent;color: #789db6;}
::-moz-selection {background-color:transparent;color: #789db6;}
::-webkit-selection {background-color:transparent;color: #789db6;}

body {
font-family: Consolas;
color: #333;
margin: 0;
margin-top: 0px;
margin-right: 0px;
margin-bottom: 0px;
margin-left: 0px;
padding: 0;
padding-top: 0px;
padding-right: 0px;
padding-bottom: 0px;
padding-left: 0px;
background: url(http://i.cubeupload.com/hZ3Mkb.png);
background-image: url(http://i.cubeupload.com/hZ3Mkb.png) repeat;
background-position-x: initial;
background-position-y: initial;
background-size: initial;
background-attachment: initial;
background-origin: initial;
background-clip: initial;
background-color: initial;
position: relative;
}

.commentbox
.commentinfo a {
color: #789db6;
font-size: .9em;
}

span.msg-inner {
  color: #555;
  font-family: consolas;
}

#content {
margin: 0 auto;
margin-top: 0px;
margin-right: auto;
margin-bottom: 0px;
margin-left: auto;
background-color: #fff;
  background: url(http://i.cubeupload.com/hZ3Mkb.png);
width: 1060px;
padding-bottom: 10px;
-webkit-box-shadow: #ccc 0 0 2px 1px;
-moz-box-shadow: #ccc 0 0 2px 1px;
box-shadow: #ccc 0 0 2px 1px;
  border-radius: 25px;
}

#lobby_container #lobby_announcement {
overflow-x: hidden;
padding: 1.1em 1.4em;
line-height: 1.15em;
font-size: .95em;
color: #bbb;
  font-family: consolas;
}

.red_link, .commentbox .bubble_i .msg a, #lobby_container #lobby_announcement a, #lobby_container #announcements .announcement .msg a {
font-weight: normal;
color: #789db6;
}

.blood {
color: #789db6;
}

#lobby_container #lobby-poll #poll-choices .choice .choice_inner .bar {
background-color: #789db6;
opacity: .7;
position: absolute;
height: 2px;
bottom: 0;
left: 0;
z-index: 1;
}

#lobby_container #lobby-poll #poll-descr {
padding: 10px;
padding-top: 10px;
padding-right: 10px;
padding-bottom: 10px;
padding-left: 10px;
  border-radius:10px;
background-color: #eee;
margin-bottom: 10px;
}

a.pretty {
font-weight: normal;
color: #b11;
  font-family: consolas;
}

#lobby_container #join_competition a {
width: 9em;
height: 1em;
line-height: 1em;
display: block;
margin: 5px 0;
margin-top: 5px;
margin-right: 0px;
margin-bottom: 5px;
margin-left: 0px;
  border-radius: 25px;
}

#lobby_container #gamepage {
width: 100%;
min-height: 300px;
position: relative;
margin-bottom: 5px;
padding-top: 5px;
background: url(http://i.cubeupload.com/RdnV8Z.png);
}

#lobby_container .gametable .gamerow:hover {
  background: url(http://i.cubeupload.com/RdnV8Z.png);
}

#lobby_container .gametable .gamerow {
position: relative;
display: block;
width: 100%;
-moz-border-radius: 2px;
-webkit-border-radius: 2px;
-khtml-border-radius: 2px;
border-radius: 2px;
background: url(http://i.cubeupload.com/RdnV8Z.png);
vertical-align: top;
padding: 2px 0;
cursor: pointer;
}

#lobby_container .gametable .gamerow.progress .numplayers .player {
background-color: #789db6;
}
#lobby_container .gametable .gamerow.progress .numplayers .player, #lobby_container .gametable .gamerow.progress .numplayers .noplayer {
border-color: #789db6;
}
#lobby_container #lobby-nav #lobby_name {
color: #789db6;
font-weight: normal;
font-size: .9em;
}
#lobby_container .gametable .gamerow .numplayers .summary {
  color: #789db6;
}

#lobby_container #lobbychat #window #window_i {
height: 205px;
width: 283px;
padding: 7px;
padding-top: 7px;
padding-right: 7px;
padding-bottom: 7px;
padding-left: 7px;
overflow-y: auto;
overflow-x: hidden;
position: relative;
font-size: .9em;
  font-family: consolas;
}

#lobby_container #lobbychat #window {
width: 304px;
border: 1px solid #bbb;
position: relative;
background-color: #fff;
  border-radius: 10px;
}

a.redbutton:hover, #forum_container #board #board_announcement .controls>:hover, .edit_markdown .bottom_controls a.save:hover, .edit_markdown .bottom_controls a.cancel:hover, input[type=submit].redbutton:hover, .edit_markdown .bottom_controls input[type=submit].save:hover, .edit_markdown .bottom_controls input[type=submit].cancel:hover {
background-image: -webkit-gradient(linear,left bottom,left top,color-stop(1,#789db6),color-stop(0,#0F38DB));
background-image: -moz-linear-gradient(center bottom,#e44 100%,#c22 0);
border-color: #ccc;
  border-radius:5px;
}

a.redbutton, #forum_container #board #board_announcement .controls>*, .edit_markdown .bottom_controls a.save, .edit_markdown .bottom_controls a.cancel, input[type=submit].redbutton, .edit_markdown .bottom_controls input[type=submit].save, .edit_markdown .bottom_controls input[type=submit].cancel {
font-family: "HelveticaNeue-Light","Helvetica Neue Light","Helvetica Neue","Helvetica","Tahoma","Geneva","Arial Narrow","Arial",sans-serif;
-moz-border-radius: 15px;
-webkit-border-radius: 15px;
-khtml-border-radius: 15px;
border-radius: 15px;
font-weight: normal;
background-color: #c22;
font-size: 1.1em;
border-color: transparent!important;
color: #fff;
cursor: pointer;
padding: .4em .5em;
background-image: -webkit-gradient(linear,left bottom,left top,color-stop(1,#789db6),color-stop(0,#0F38DB));
background-image: -moz-linear-gradient(center bottom,#d33 100%,#b11 0);
  transition: 0.6s;
    -moz-transition: 0.6s;
    -webkit-transition: 0.6s;
    -o-transition: 0.6s;
}
#lobby_container #lobby_menu #stickied_lobbies li a {
display: block;
padding: 3px 10px 4px;
background-color: #ccc;
color: #777;
font-weight: normal;
height: 26px;
line-height: 27px;
  border-radius:5px;
}
#lobby_container #lobby_menu #stickied_lobbies li.sel a {
color: #fff;
background: url('data:image/svg+xml;base64,PD94bWwgdmVyc2lvbj0iMS4wIiBlbmNvZGluZz0idXRmLTgiâ€¦pZHRoPSIxMDAlIiBoZWlnaHQ9IjEwMCUiIGZpbGw9InVybCgjZ3JhZCkiIC8+PC9zdmc+IA==');
background: -webkit-gradient(linear,50% 0,50% 100%,color-stop(1,#789db6),color-stop(0,#0F38DB));
background: -webkit-linear-gradient(#789db6,#0F38DB);
background: -moz-linear-gradient(#789db6,#0F38DB);
background: -o-linear-gradient(#789db6,#0F38DB);
background: linear-gradient(#789db6,#0F38DB);
}

#navbox #navbox_inner #subnav li.sel a {
color: #555;
  background: url(http://i.cubeupload.com/hZ3Mkb.png);
}

#navbox #navbox_inner #subnav a {
padding: .4em 14px .4em;
padding-top: 0.4em;
padding-right: 14px;
padding-bottom: 0.4em;
padding-left: 14px;
  background: url(http://i.cubeupload.com/hZ3Mkb.png);
}



#navbox #navbox_inner #subnav li.sel {
border-bottom: 0;
padding-bottom: 1px;
position: relative;
background-color: #ddd;
-webkit-border-top-left-radius: 2px;
-moz-border-radius-topleft: 2px;
border-top-left-radius: 2px;
-webkit-border-top-right-radius: 2px;
-moz-border-radius-topright: 2px;
border-top-right-radius: 2px;
-webkit-border-bottom-left-radius: 0;
-moz-border-radius-bottomleft: 0;
border-bottom-left-radius: 0;
-webkit-border-bottom-right-radius: 0;
-moz-border-radius-bottomright: 0;
border-bottom-right-radius: 0;
font-weight: normal;
z-index: 2;
}

#header #header_inner #logo {
display: block;
width: 200px;
height: 40px;
margin: 10px 10px 2px -5px;
background: url(http://i.cubeupload.com/1wBYKk.png) no-repeat;
}
