css:
@-moz-document url-prefix(http://browncardigan.com/),
url-prefix(https://browncardigan.com/),
url-prefix(http://www.browncardigan.com/),
url-prefix(https://www.browncardigan.com/)
{

/* removes the floating header without hiding it completely */

header.fixed {
  	position: static; 
}

/* this next bit restyles the PREV and NEXT buttons so they still float and follow the window but they're less obtrusive */

a.controls.prev {
left: 2px;
}
a.controls.next {
right: 2px;
}

a.controls {
bottom: 10px;
background: #000;
color: #FFF;
font-weight: regular;
text-decoration: none;
font-size: 12px;
height: 50px;
border-radius: 25px;
-moz-border-radius: 25px;
-webkit-border-radius: 25px;
width: 50px;
line-height: 50px;
}
}
