css:
@-moz-document url("http://www.tumblr.com/dashboard"), url("https://www.tumblr.com/dashboard") {
html {
  background: #FF3640 no-repeat center center fixed;
    -webkit-background-size: cover;
    -moz-background-size: cover;
    -o-background-size: cover;
    background-size: cover;
}

body {
  background: rgba(0,0,0,0);
}

div[id="container"] {
  background: url('http://s29.postimg.org/w40sw6yiv/Canada_Chibie.png');
    background-repeat: no-repeat;
    background-attachment: fixed;
    background-position: 80% 100%; 
    z-index: 0;
}

#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
	background-color: rgba(0,0,0,0);
}

.controls_section.user_list li .follow_list_item_blog:before {
	background: -webkit-gradient(linear,left top,right top,color-stop(0%,rgba(44,71,98,0)),color-stop(100%,rgba(44,71,98,0)));
	background: -webkit-linear-gradient(left,rgba(44,71,98,0) 0,rgba(44,71,98,0) 100%);
	background: linear-gradient(to right,rgba(44,71,98,0) 0,rgba(44,71,98,0) 100%);
	border-right: 5px solid rgba(44,71,98,0);
}
      
.radar .radar_footer .radar_avatar:before {
	background: -webkit-gradient(linear,left top,right top,color-stop(0%,rgba(44,71,98,0)),color-stop(100%,rgba(44,71,98,0)));
	background: -webkit-linear-gradient(left,rgba(44,71,98,0) 0,rgba(44,71,98,0) 100%);
	background: linear-gradient(to right,rgba(44,71,98,0) 0,rgba(44,71,98,0) 100%);
}

.controls_section.user_list li:hover .follow_list_item_blog:before, .control_hover .controls_section.user_list li:active .follow_list_item_blog:before {
	background: -webkit-gradient(linear,left top,right top,color-stop(0%,rgba(51,75,99,0)),color-stop(100%,rgba(51,75,99,0)));
	background: -webkit-linear-gradient(left,rgba(51,75,99,0) 0,rgba(51,75,99,0) 100%);
	background: linear-gradient(to right,rgba(51,75,99,0) 0,rgba(51,75,99,0) 100%);
	border-right: 5px solid rgba(51,75,99,0);
}

.selection_nipple {
    visibility: hidden;
}

.search_form_row {
	background-color: white;
}
  
.search_query {
	color: rgba(0, 0, 0, 0.69);
}

.right_column:after {
	background: -moz-linear-gradient(top,rgba(44,71,98,0) 0,rgba(44,71,98,0) 100%);
	background: -webkit-gradient(linear,left top,left bottom,color-stop(0%,rgba(44,71,98,0)),color-stop(100%,rgba(44,71,98,0)));
	background: -webkit-linear-gradient(top,rgba(44,71,98,0) 0,rgba(44,71,98,0) 100%);
	background: -o-linear-gradient(top,rgba(44,71,98,0) 0,rgba(44,71,98,0) 100%);
	background: -ms-linear-gradient(top,rgba(44,71,98,0) 0,rgba(44,71,98,0) 100%);
	background: linear-gradient(top,rgba(44,71,98,0) 0,rgba(44,71,98,0) 100%);
}
    
.pinned_sidebar_footer {
    display: none;
    visibility: hidden;
}

.count {
    display: none;
    visibility: hidden;
}

.radar {
  display:none;
  visibility: hidden;
}
  
.radar_header {
  display:none;
  visibility: hidden;
}
  
.icon_plus:before {
	content: "\f137";
    color: #FF3640;
}

}
