css:
@-moz-document domain("xianguo.com") {
/*文章标题*/
.article-list .art-t { 
    font-family:verdana,文泉驿微米黑,黑体 !important; 
    padding: 8px 60px 8px 27px !important;
    background-color: #F5F5F5 !important;
  }
.article-list .art-t a {
    font-size:20px !important; 
    text-decoration:none !important;
  }
.article-list .art-t a:hover {
    color:#A14242 !important;
  }

/*文章在新标签页打开按钮*/
.article .art-detail-btn {
    top:16px !important;
  }
    
/*文章本页全文预览*/
.article-list li.detail .art-detail-info {
    padding: 8px 0 8px 27px !important;
    line-height: 24px !important;
  }
.article-list li.detail .art-content p {font-size:16px !important;text-align:Justify;}
.article-list li.detail .art-content {padding:16px 30px !important;line-height:26px !important;}
/*文章正文页*/
h2 {font-family:verdana,方正宋黑_GBK,仿宋 !important;}
.article-detail .art-content p ,
.article-detail .art-content p a{
    font-size: 24px !important;
    font-family:arial,文泉驿微米黑,黑体 !important;
  }
a[name="continued"] /*抓取《瘾科技》的“继续阅读”*/ {
    font-size: 24px !important;
    font-family:verdana,文泉驿微米黑,黑体 !important;
    color:black !important;
    text-decoration:none !important;
  }
}
