css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("forums.hannity.com") {
/* Hide Stuff */
div.block.smaller,
ul#sidebar,
div.adtitle,
div.banner_ad,
div.banner_ad2,
div.footer_time,
div.footer_morecopyright,
div#footer_bottom_widget,
div#footer_copyright.shade.footer_copyright,
li#navbar_notice_1.restore
{display: none !important;}
.postbitboxes {border: none !important; box-shadow: none !important;}


/* Main Content */
div#content {width: 100% !important;}
.blocksubhead,
div.sortrow a {color: #7B2B2C !important;}
.subsection {background-color: #FFF !important;}

/* Headers */
h2.blockhead {background-color: #212121 !important;}
h3.blocksubhead, h4.blocksubhead {background-color: #FFF !important;}
h2 span.forumtitle,
h2 span.forumthreadpost,
h2 span.forumlastpost,
div.forumhead,
div.posthead,
div.threadlisthead,
.blockhead,
.searchlisthead,
ul#navtabs,
a.title,
a.navtab
{background-color: #7B2B2C !important;}

/* Straighten Breadcrumb Links */
.breadcrumb a,
.navbit a {color: #7B2B2C; display: inline-flex !important;}

div#navbar {background-color: #2A2B2C !important;}
.announcements, .announcement, .announcerow, .announceinfo {background-color: #DCDCDC !important;}
.announcement a {color: #7B2B2C !important;}
h2 span.threadtitle {color: #212121 !important;}
div#thread_controls a {color: #FFF  !important;}
div#thread_controls.thread_controls.toolsmenu {background-color: #212121 !important;}
div#footer {background-color: #2A2B2C !important;}
div.blocksubhead.sortrow {background-color: #FFF !important;}
div#above_threadlist_controls.above_threadlist_controls.toolsmenu {background-color: #000 !important;}
div#above_threadlist_controls.above_threadlist_controls.toolsmenu a {color: #FFF !important; background-color: #000 !important;}
.polltitle {color: #FFF !important;}

/* Links */
.pmbit a {color: #7B2B2C !important; background-color: #FFF !important;}
body a {color: #7B2B2C}
div.selected_view_section a,
div.blockbody a,
div.subsection a,
a.usercp_blog,
.usermenu a,
div.blockrow.member_blockrow a,
a.textcontrol,
div.group_content.row_content a,
div.album_content.row_content a,
div.title a,
div.inner a,
a.smallfont,
div.user a,
div.navlinks a,
div.wgo_subblock.section a,
div.fulllink a {color: #7B2B2C !important; background-color: #FFF !important;}
.navbar_advanced_search a,
.firstunread {color: #7B2B2C !important;}
h3.threadtitle a,
.loginbutton {color: #7B2B2C !important;}
.pagination span.selected a {background-color: #7B2B2C !important; border: #7B2B2C !important; color: #FFF !important;}
ul#postlist_popups a {background-color: #212121 !important;}

/* Buttons */
input.loginbutton {background-color: #7B2B2C !important;}
.searchbutton {background-color: #7B2B2C !important;}
a#newreplylink_top.newcontent_textcontrol, a#newreplylink_bottom.newcontent_textcontrol {background-color: #212121 !important;}
a#newthreadlink_top.newcontent_textcontrol, a#newthreadlink_bottom.newcontent_textcontrol {background-color: #212121 !important;}
}
