css:
@-moz-document domain("twitter.com") {
.bird-topbar-etched{color: #cc0000 !important; -webkit-animation:flicker 5s infinite;
    animation:flicker 5s infinite;}

.bird-topbar-etched:hover{-webkit-animation:flickerhover 2s forwards; 
    animation:flickerhover 2s forwards ;}

@-webkit-keyframes flicker{
    0%{opacity:1}
    2%{opacity:0.2}
    15%{opacity:0.2}                
    16%{opacity:1}
    18%{opacity:0.2}
    38%{opacity:0.2}                
    40%{opacity:0.8}
    41%{opacity:1}
    43%{opacity:0.2}
    47%{opacity:0.2}                
    48%{opacity:0.9}
    49%{opacity:0.3} 
    70%{opacity:0.3}
    71%{opacity:0.9} 
    72%{opacity:0.6}
    95%{opacity:0.3}
    96%{opacity:0.8}
    97%{opacity:0.3}
    99%{opacity:0.8}
}    
@-moz-keyframes flicker{
     0%{ opacity:1; }
     2%{ opacity:0.2; }
    15%{ opacity:0.2; }                
    16%{ opacity:1; }
    18%{ opacity:0.2; }
    38%{ opacity:0.2; }                
    40%{ opacity:0.8; }
    41%{ opacity:1; }
    43%{ opacity:0.2; }
    47%{ opacity:0.2; }                
    48%{ opacity:0.9; }
    49%{ opacity:0.3; } 
    70%{ opacity:0.3; }
    71%{ opacity:0.9; }
    72%{ opacity:0.6; }
    95%{ opacity:0.3; }
    96%{ opacity:0.8; }
    97%{ opacity:0.3; }
    99%{ opacity:0.8; }
}    
                            
@-webkit-keyframes flickerhover{
    0%{opacity:1}
    2%{opacity:0.2}
    15%{opacity:0.2}                
    16%{opacity:1}
    18%{opacity:0.2}
    38%{opacity:0.2}                
    40%{opacity:1}
    72%{opacity:0.9}
    95%{opacity:1}
    99%{opacity:1}
}  
  
@-moz-keyframes flickerhover{
     0%{ opacity:1; }
     2%{ opacity:0.2; }
    15%{ opacity:0.2; }                
    16%{ opacity:1; }
    18%{ opacity:0.2; }
    38%{ opacity:0.2; }                
    40%{ opacity:1; }
    72%{ opacity:0.9; }
    95%{ opacity:1; }
    99%{ opacity:1; }
} 

  body {
  	font-family: Tahoma, Verdana, Segoe, sans-serif !important;
	-webkit-font-smoothing: antialiased;  
	/*If the following causes any problem with zooming (or accessibility add-ons), remove it*/
	-webkit-text-size-adjust: 100%;
	-moz-text-size-adjust: 100%;
	-ms-text-size-adjust: 100%;
	}

p.js-tweet-text, .content, .stream-item, .tweet-content, .tweet-text.js-tweet-text {
 	/* Specifies trouble-maker divs */ 
	font-family: Tahoma, Verdana, Segoe, sans-serif !important;
}

.nav>li {
	/* Top menu gray/grey color/colour */ 
	color:#b2babf   
}

/*  From the original "Black Header for Twitter" */
.global-nav-inner {
	background: none repeat scroll 0% 0% rgb(0, 0, 0) !important;
}

/* Added: TL shadow. Except on profile */

#timeline, #discover_items {
	box-shadow: 0 10px 9px #3c3c3c;
}
}
