css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("forums.unrealengine.com") {
  
    
  .body_wrapper {
    width: 94%; 
    /*padding: 10px 20px;*/
    padding-left: 3%;
    padding-right: 3%;
    background: none repeat scroll 0% 0% #FFF;
    border-radius: 0px;
  }
  
  .above_body{
   width: 100%;
   margin-bottom: 0px
  }

  body{
  width: 100% ;  
  margin-left: 0px;
  background-color: #E2E3E4;
  }
  
  body a {
    color: #003255;
    text-decoration: none;
  }
  
  .navbar {
    position: relative;
    height: 53px;
    background: none repeat scroll 0% 0% #c2c3c4;
    margin: 10px 0px 0px;
    color: #FFF;
    border: 0px solid #FFF;
    border-bottom-left-radius: 0px;
    border-bottom-right-radius: 0px;
  }
  
  .navtabs {
    background: #E2E3E4;
    padding: 0px 0px 0px 20px;
    height: 26px;
    border: none;
}
  
.navtabs ul {
    border-top: none;
    border-radius: 0px;
  }
  
  .navtabs.navtabs li a.navtab {
    border: none;
    display: block;
    background: #d2d3d4;
    min-width: 60px;
    width: auto !important;
    text-align: center;
    color: #000;
    padding: 0px 10px;   
}
  .navtabs li.selected {
    height: 26px;
    color: #000;
    background-color: #F7931D;
    border: none;
}
  
.navtabs li {
    float: left;
    border-right: none;
}
  
.navtabs li:first-child {
    border-left: none;
}
  
.navtabs li.selected a.navtab {
    color: #000;
    background: #F7931D;
    position: relative;
    top: -4px;
    padding-top: 4px;
    z-index: 10;
    border: none;
}

.globalsearch {
    position: absolute;
    width: 180px;
    right: 20px;
    top: 0px;
    border: none;
    float: right;
    clear: left;
    text-align: right;
    line-height: 1em;
    display: block;
}
  
.globalsearch .textboxcontainer {
    height: 26px;
    border: none !important;
    display: inline-block;
    float: left;
    text-align: right;
}
  
.globalsearch .textboxcontainer span {
    border-top: none;
    border-left: none;
}
  
.globalsearch .buttoncontainer {
    height: 26px;
    border: none;
    display: inline-block;
    background-color: #d2d3d4;
    width: 25px;
    overflow: hidden;
    float: right;
}
  
.globalsearch .buttoncontainer span {
    border-top: none;
    border-left: none;
}

.textbox, textarea, select {
    color: #000;
    background: none repeat scroll 0% 0% #FFF;
    border: 0px;
    padding: 4px;
    outline: 0px none;
    border-radius: 0px;
}
  
.navbar_search input {
    font-size: 11px;
    vertical-align: middle;
    padding: 1px;
    border: none;
}
  
.toplinks ul.isuser li {
  color: black;
}
  
 #pagetitle {
   padding-top: 10px;
   padding-bottom: 5px
   
 }
  
 .postrow has_after_content{
   padding-bottom: 1em;
 }
 
  .newcontent_textcontrol{
    background: #F7931D;
    color: #FFF;
    text-shadow: none;
    box-shadow: none;
    display: block;
    padding: 4px 20px 4px 15px;
    margin-bottom: 10px;
    float: left;
    font-size: 15px; 
    font-weight: bold;
/*    width: 190px; */
    line-height: 20px;
    border-radius: 0px;
    font-family: "calibri";
    text-transform: uppercase;
  }

  .newcontent_textcontrol:hover{
    background: none repeat scroll 0% 0% #c16b07;
    text-shadow: none;
    box-shadow: none;
    display: block;
    padding: 4px 20px 4px 15px;
    margin-bottom: 10px;
    float: left;
    font-size: 15px; 
    font-weight: bold;
/*    width: 190px; */
    line-height: 20px;
    border-radius: 0px;
    font-family: "calibri";
    text-transform: uppercase;
  }
  
  .logo-image{
    margin: 15px 0px 5px 35px;
  }
  
  .toolsmenu {
    background: #E9E9E9;
    width: 100%;
    display: block;
    border: none;
    float: left;
    clear: both;
    margin: 0.5em 0px 0px 0px;
}
  
.popupmenu a.firstunread {
    background-image: url('https://forums.unrealengine.com/images/misc/black_downward_arrow.png');
    background-position: right center;
    background-repeat: no-repeat;
    padding-right: 15px;
}
  
  
  
  .footer {
    clear: both;
    margin-top: 15px;
    text-align: right;
    display: block;
    box-shadow: none;
    background: none repeat scroll 0% 0% #313233;
    padding: 5px 0px;
    border: none;
    font: 13px Tahoma,Calibri,Verdana,Geneva,sans-serif;
  }
  
  .postbitlegacy .posthead{
    border: none;
    background: repeat-x scroll 0% 0% #939597;
  }
  
  .thread_info h4 {
    background: repeat-x scroll 0% 0% #939597;
    border: none;
    box-shadow: none;
    border-top-right-radius: 0px;
    border-top-left-radius: 0px;
  }
  
  .thread_info .blockbody {
    border: none;
    background: #f0f0f0;
    box-shadow: none;
  }
  
  .options_block{
    box-shadow: none;
  }
  
  .postbitlegacy .postfoot .textcontrols span.seperator{
    border-right: none;
    display: inline;
    margin: 0px 5px;
  }
  
  .postbitlegacy .postfoot .textcontrols a.editpost{
    /*font-variant:small-caps;*/
  }
  
  .pagination span a{
    border: none;
  }
  
  .pagination span a:hover{
    border: none;
    background-color: #c0d4dd;
  }
  
  .pagination span.first_last a, .pagination span.prev_next a{
    position: relative;
    top: -1px;
  }
    
  
  .pagination span.selected a{
    background: none repeat-x scroll 0% 0% #f2f6f8;
    color: #4E4E4E;
    border: none;
  }
  
  .fullwidth{
    border: none;
    border-radius: 0px;
  }
  
  /* advanced search */
  .actionbuttons .group .button {
    border: none;
    color: #ffffff;
    background: #F7931D;
    text-transform: uppercase;
  }
  
  .actionbuttons .group .button:hover {
    background: #c16b07;
    color: #ffffff;
   }
  
  #cb_exactname{
    margin-top: 5px;    
  }
  
  #searchtypeswitcher li a {
    display: block;
    padding: 5px 10px;
    margin-right: 5px;
    background-color: #939597;
    float: left;
    clear: right;
    color: #FFF;
    font-weight: bold;
    border-radius: 0px;
  }
  
  #searchtypeswitcher {
    border-bottom: 4px solid #F7931D;
  }
  
  #searchtypeswitcher li a:hover {
    background-color: #c16b07;
}
  
  #searchtypeswitcher li.selected a {
    background-color: #F7931D;
  }
  
h2.searchlisthead {  
    box-shadow: none;
    background: #939597;
    border: none;
}
  
  /* frontpage */
  
  
.forumbit_post .forumrow, .forumbit_nopost .forumhead, .forumbit_nopost .forumrow, .forumbit_post .forumhead, .forumbits .forumhead {
    box-shadow: none;
    border-radius: 0px;
    border: none;
  }
  
  .forumhead + .childforum .L2:first-child .forumrow, .forumhead + .L2 .forumrow {
    background: #f8f8f8;
    border-top: 0px none;
}
  
  .forumbit_nopost .forumbit_nopost .forumrow, .forumbit_post .forumrow {
    color: #4E4E4E;
    display: block;
    width: 100%;
    float: left;
    min-height: 58px;
    position: relative;
    background: #f8f8f8;
    border-top: 0px none;
  }
  
  .threadlisthead {
    font: bold 12px Tahoma,Calibri,Verdana,Geneva,sans-serif;
    margin: 0px;
    width: 100%;
    border: none;
    background: #939597;
    color: #FFF;
    float: left;
    display: block;
    border-top-right-radius: 0px;
    border-top-left-radius: 0px;
    box-shadow: none;
    position: relative;
   }
  
  .threadlist {
    display: block;
    clear: both;
    width: 100%;
    margin-top: 0px;
    float: left;
  }
  
  .threadbit {
    padding: 0px;
    width: 100%;
    display: block;
    clear: both;
    color: #3E3E3E;
    float: left;
    position: relative;
    box-shadow: none;
  }
  
  .threadbit .sticky {
    background: #F2F6F8;
    border: none;
    width: 100%;
    float: left;
    display: block;
    clear: both;
    position: relative;
   }
  
  .threadbit .alt {
    background: none #F2F6F8;
  }
  
  .threadbit .nonsticky, .threadbit .discussionrow {
   background: none #F2F6F8;
  }
  
  .threadbit .nonsticky, .threadbit .deleted, .threadbit .discussionrow, .threadbit .ignored {
    border: none;
  }
  
  .button {
    padding: 2.5px 5px;
    font: bold 11px Tahoma,Calibri,Verdana,Geneva,sans-serif;
    border: none;
    color: #ffffff;
    background: none #F7931D;
    border-radius: 0px;
  }
  
  .button:hover {
    background: none #c16b07;
    color: #ffffff;
  }
  
  .forum_info .blockhead {
    background: #939597;
    border: none;
    margin-top: 5px;
    box-shadow: none;
   }
  
  .forum_info .blockbody {
    border: none;
    background: #F2F2F2;
    box-shadow: none;
  }
  
  .wgo_block {
    border-radius: 0px;
    box-shadow: none;
    border: none;
  }
  
  .wgo_block  .blockhead {
    font-size: 12px;
    font-weight: bold;
    color: #FFF;
    background: #939597;
    border: none;
    border-radius: 0px;
  }
  
  .wgo_block .blockbody {
    border: none;
    background: #f8f8f8;
  }
  
#inlinemod_formctrls .popupctrl {
    top: -5px;
    position: inherit;
    text-decoration: none;
    background: none  #fe6f5e;
    background-color: #fe6f5e; 
    padding-right: 0px;
    color: #ffffff;
    border: none;
    font: bold 11px Tahoma,Calibri,Verdana,Geneva,sans-serif;
    float: left;
    border-radius: 0px;
}
  
#inlinemod_formctrls a.popupctrl:hover {
    background: none  #ff7f6e;
    color: #ffffff;
}
  
.cke_skin_kama .cke_dialog_contents a.cke_dialog_ui_button_ok, .cke_skin_kama .cke_dialog_contents a.cke_dialog_ui_button_cancel, .textcontrols a, a.textcontrol {
    position: relative;
    padding: 3px 5px;
    font: bold 11px/1.23 Tahoma,Calibri,Verdana,Geneva,sans-serif;
    text-decoration: none;
    border: none;
    color: #ffffff;
    background: none #F7931D;
    border-radius: 0px;
    text-transform: uppercase;
}

.textcontrols a:hover, a.textcontrol:hover {
    background: none #c16b07;
    color: #ffffff;
    text-transform: uppercase;
}
  /*
  
  //quickreply
  
  */
  
  
  
  span.cke_skin_kama {
    border-radius: 0px;
    border: none;
    padding: 0px;
    margin: 5px;
  }
  
  .cke_skin_kama .cke_wrapper.cke_ltr, .cke_skin_kama .cke_wrapper.cke_rtl {
    background: #d0d0d0;
    border-radius: 0px;
    padding: 5px 10px 5px 10px;
    margin: 0px;
  }
  
  #postlist{
    padding: 0px 0px 0px 0px;
    
  }
  
  .blockfoot, .blocksubfoot {
    display: block;
    clear: both;
    background: #d0d0d0;
    padding: 5px 0px;
    border: 0px none #6C0;
  }
  
  .formcontrols .section {
    padding: 5px 15px;
  }
  
  .header{
    background-color: #a0a0a0;
  }
  
  .content{
    padding: 10px;
  }
  
  .formcontrols .blockrow {
    background: #d0d0d0;
    border-top: none;
    padding: 0px;
  }
  
  .formcontrols .openclose, .formcontrols .stickunstick, .formcontrols .showsignature {
    margin: 0px;
    padding: 5px;
    padding-top: 0px;
    background: #d0d0d0;
}
  
  /*
  
  // Reply
  
  */
  
  .blockhead{
    border-radius: 0px;
    border: none;
    background: #939597; 
  }
  
  .full{
    padding-top: 5px;
    padding-left: 15px;
    
  }
  
  .blockbody {
    background: none #d0d0d0;
    padding-left: 0px;
    padding-right: 0px;
  }
  
  .section{
    padding-left: 5px;
    padding-right: 5px;
    margin-left: 5px;
    margin-right: 5px;
    
  }
  
  .wysiwyg_block {
    margin-bottom: 10px;
    background: none #d0d0d0;
}
  
  .formcontrols {
    border: none;
    background-color: #d0d0d0;
  }
  
  
  .primary.full {
    max-width: 100%;
    width: 496px;
    float: left;
    background: #f2f2f2;
    margin: 5px;
    margin-left: 15px;
  }
  
  
  /*
  // User profile and activity feed
  
  */
  
  li.activitybit {
    overflow: hidden;
    color: #3E3E3E;
    font-weight: normal;
    font-family: inherit;
    font-size: 11px;
    margin: 0px 0px 10px;
    border: 0px none;
    padding: 10px;
    background: none no-repeat scroll 0% 0% #EAEAEA;
   }
  
   #sidebar_container.member_summary {
    color: #000;
    background: none #939597;
     background-image: none;
    border: none;
    box-shadow: none;
    border-radius: 0px;
   }
  
  #sidebar_container .blockbody.userprof_content {
    border: none;
    border-radius: 0px;
  }
  
  .userprof_moduleinactive{
   background-image: none;
  }
  
  .tabbackground{
    height: 40px;
  }
  
  dd.userprof_module, dd.userprof_moduleinactive{
    border: none;
    border-radius: 0px;
    
  }
  
  #sidebar_container a {
    color: #003255;
  }
  
  .member_content #sidebar_container div.block {
    border: none;
    box-shadow: none;
    border-radius: 0px;  
  }
  
  #sidebar_container .blocksubhead {
   border: none;
  }
  
  .userprof_button {
    background: #fab667;
    color: #FFF;
    text-shadow: none;
    box-shadow: none;
    display: block;
    padding: 4px 20px 4px 15px;
    margin-bottom: 10px;
    font-size: 15px; 
    font-weight: bold;
/*    width: 190px; */
    line-height: 20px;
    border-radius: 0px;
    border: none;
    font-family: "calibri";
    text-transform: uppercase;
}
  

}
