css:
@-moz-document domain("www.readmore.de") {
    
	
	/* Readmore 3.0 CSS enhancements */
    
    
    a {
        transition: all 0.2s ease-in-out 0s;
    }
    
    #c_content .content.details a.bold:hover {
       color: #2B91FF;
       text-decoration: underline; 
    }
    
    #c_content img {
       border: 1px solid #2B91FF;
    }
    
    #c_content img:hover {
       border: 1px solid #FFB400; 
       transition: all 0.5s ease-in-out 0s;
    }
    
    .content_head .l h2.content_title a {
        color: #2B91FF;
        position: relative;
        text-decoration: none;
        transition: all 0.3s ease-in-out 0s;
    }
    .content_head .l h2.content_title a:before {
        content: "";
        position: absolute;
        width: 100%;
        height: 1px;
        bottom: 0px;
        left: 0;
        background-color: #FFB400;
        visibility: hidden;
        -webkit-transform: scaleX(0);
        transform: scaleX(0);
        -webkit-transition: all 0.3s ease-in-out 0s;
        transition: all 0.3s ease-in-out 0s;
    }
    
    .content_head .l h2.content_title a:hover {
       color: #FFB400;
    }
    .content_head .l h2.content_title a:hover:before {
        visibility: visible;
        -webkit-transform: scaleX(1);
        transform: scaleX(1);
    }
    
    #c_left a.gallery_image:hover {
       transition: all 0.5s ease-in-out 0s !important;
    }
        
    #c_left h3 {
        background: #2B91FF;
        -moz-box-sizing: border-box;
        box-sizing: border-box;
        padding: 3px 3px 3px 4px;
        border: 0 none;
        margin-top: -2.5px;
        color: #FFF;
        font-family: Verdana,sans-serif;
        font-size: 13px;
        font-weight: bold;
        transition: all 0.2s ease-in-out 0s !important;
    }
    
    #c_left h3.mod {
        color: #fff;
    }
    
    #c_left .control {
        -moz-box-sizing: border-box;
        box-sizing: border-box;
        background: url(http://abload.de/img/side_left_setup_whitejlslo.png) no-repeat;
        width: 24px;
        height: 18px;
        padding-left: 28px;
        margin-top: 1px;
    }
    /*http://abload.de/img/side_left_setup_yellok9s8u.png*/
    
    #c_left .control:hover {
        background: url(http://abload.de/img/side_left_setup_orangj7sbw.png) no-repeat;
    }
    
    #c_left .control img {
        /* fubar */
       display: none;
    }
        
    #c_left h3 a {
        position: relative !important;
        color: #fff;
        text-decoration: none;
    }
    #c_left h3 a:before {
        background: #fff;
        content: '';
        position: absolute !important;
        width: 100%;
        height: 1px;
        bottom: 0px;
        left: 0px;
        right: 0px;
        visibility: hidden;
        -webkit-transform: scaleX(0) !important;
        transform: scaleX(0) !important ;
        -webkit-transition: all 0.3s ease-in-out 0s !important;
        transition: all 0.3s ease-in-out 0s !important;
    }
    
    #c_left h3 a:hover {
        /*text-decoration: underline;*/
        color: #fff;
    }
    #c_left h3 a:hover:before {
        /*text-decoration: underline;*/
        visibility: visible !important;
        -webkit-transform: scaleX(1) !important;
        transform: scaleX(1) !important;
    }
    
    #c_right h3 a {
        position: relative;
        color: #2B91FF;
        text-decoration: none !important;
    }
    #c_right h3 a:before {
        background: #2B91FF;
        content: '';
        position: absolute;
        width: 100%;
        height: 1px;
        bottom: 0px;
        left: 0px;
        right: 0px;
        visibility: hidden;
        -webkit-transform: scaleX(0);
        transform: scaleX(0);
        -webkit-transition: all 0.3s ease-in-out 0s;
        transition: all 0.3s ease-in-out 0s;
    }
    #c_right h3 a:hover {
        color: #2B91FF;
    }
    #c_right h3 a:hover:before {
        visibility: visible;
        -webkit-transform: scaleX(1);
        transform: scaleX(1);
    }
    
    #c_right h3.mod {
        background: #2B91FF;
        color: #fff;
    }
    #c_right h3.mod span {
        color: #fff;
    }
    #c_right h3.mod a {
        color: #fff;
    }
    #c_right h3.mod a:hover {
        text-decoration: underline;
    }
    #c_right a:hover {
        color: #2B91FF;
        text-decoration: underline;
    }

    #c_right .control {
        float: right;
        -moz-box-sizing: border-box;
        box-sizing: border-box;
        background: url(http://abload.de/img/side_right_setup-grey8xsa8.png) no-repeat;
        width: 24px;
        height: 18px;
        padding-left: 24px;
        margin-top: 1px;
        
    }
    #c_right .control:hover {
        /* fubar */
        background: url(http://abload.de/img/side_right_setup_bluecssux.png) no-repeat;
    }
    
    #c_right .control img {
        /* fubar */
       display: none;
    }
    
    #c_right h3 {
        background: none;
        border-bottom: 0px solid #e9e9e9;
        padding:  2px;
        margin: -3px 0 15px; 
        color: #2b91ff;
        font-size: 14px;
        font-weight: bold;
    }
    
    #c_right ul.matches li a:hover {
        text-decoration: none;
    }

	#c_right .headlines_cat {
		background: none repeat scroll 0 0 #2B91FF;
		color: #FFFFFF;
	}  
    
	#c_right .headlines_cat a {
		color: #FFFFFF;
        font-weight: bold;
        position: relative;
        text-decoration: none;
	}
    #c_right .headlines_cat a:before {
		content: "";
        position: absolute;
        width: 100%;
        height: 1px;
        bottom: 0px;
        left: 0;
        background-color: #FFF;
        visibility: hidden;
        -webkit-transform: scaleX(0);
        transform: scaleX(0);
        -webkit-transition: all 0.3s ease-in-out 0s;
        transition: all 0.3s ease-in-out 0s;
	} 
    
	#c_right .headlines_cat a:hover {
		color: #FFFFFF;
		/*text-decoration: underline;*/
        /*font-weight: bold;*/
	}
    #c_right .headlines_cat a:hover:before {
        visibility: visible;
        -webkit-transform: scaleX(1);
        transform: scaleX(1);
	}
    
	#c_right .headlines_cat span.icon {
        border: 1px solid #2B91FF;
		opacity: 0;
	}
    
	#c_right span.forum a:hover {
		text-decoration: underline;
	}
	
	#c_right span.forum:hover {
		text-decoration: underline;
	}
    
    #c_right span.stream_lang {
        color: #2B91FF; /* #7b7b7b */
    }
    
    #c_right span.stream_lang:hover {
        color: #FFB400;
    }
    
  
    .forum_post.unread {
       border-left: 1px solid #2b91ff;
       border-bottom: 1px solid #2b91ff;
       border-right: 1px solid #2b91ff;
    }
 
    .forum_post {
        border-top: 10px solid #2B91FF;
        border-left: 1px solid #2b91ff;
        border-bottom: 1px solid #2b91ff;
        border-right: 1px solid #2b91ff;
        padding-top: 0;
        margin-bottom: 15px;
    }
    
    .forum_post .post {
       border-left: 1px dotted #535353;
       margin-left: 120px;
       padding-left: 10px;
    }
    
    .forum_post .info {
        width: 120px;
    }
    
    .forum_post .head {
       color: #FFF;
       background: #2B91FF;
       margin-bottom: 8px;
       padding-left: 8px;
       padding-right: 8px;
       padding-bottom: 8px;
       margin-left: -10px;
       margin-right: -10px;
    }
    .forum_post .head a {
       color: #FFF;
    }
    .forum_post .head a:hover {
       text-decoration: underline;
    }

    .forum_post .info span.ind_admin {
       color: #ea421f;
       font-weight: bold;
    }
    .forum_post .info span.ind_thread {
       color: #18bb34;
       font-style: italic;
       font-weight: bold;
    }
    
    
   
/* ########### Spoiler ########### */
    .forum_ed_spoiler {
       padding: 3px;
    }
    .forum_ed_spoiler.open {
       color: #535353;
    }
    .forum_ed_spoiler_trigger.open:before {
       content: "- ";
    }
    .forum_ed_spoiler_trigger:before {
       content: "+ ";
    }
    .forum_ed_spoiler_trigger {
       color: #EA421F; /* oder: #2B91FF */
    }
    .forum_ed_spoiler_trigger:hover {
       text-decoration: underline;
    }
/* ########### Spoiler ########### */	

    #c_right ul li a.block:hover {
		color: #2B91FF;
		text-decoration: underline;
	}
    
    .pagination ul li a:hover {
        border: 1px solid #2B91FF;
    }
	
	.pagination ul li.active a {
		border-color: #FFB400;
	}
    
	.forum_editor ul.controls li input:hover {
		text-decoration: underline;
	}
    
	.forum_thread_reply textarea {
		height: 150px !important;
	}
    
	.forum_post .control_buttons a:hover {
		text-decoration: underline;
	}

/* ########## COMMENTS ########## */
    .user_status.offline {
       background: #c24000;
    }
    .user_status.online {
       background: #00c240;
    }
    
    .comment .comment_head a {
        color: #eee;
    }
    
    .comment .comment_head span {
       float: right;
       font-weight: bold;
       margin-left: -75px;
    }
    
	.comment .comment_head a:hover {
		text-decoration: underline;
	}
    
	.comment .comment_buttons a:hover {
		text-decoration: underline;
	}
     .comment .comment_buttons {
       padding-right: 10px;
    }    
    .forum_ed_quote
    .comment .comment_buttons {
       padding-right: 5px;
    }
    .forum_ed_quote {
        background-color: #f2f2f2 !important;
    }
    
    #c_content textarea {
        height: 150px !important;
    }

    #c_content .content.teaser {
       text-align: justify;
    }
    #c_content .content.details {
       text-align: justify;
    }
    
    .comment {
        /*background: #eee;*/
        border: 1px solid #2B91FF;
        padding-top: 0;
        padding-right: 0;
        margin-bottom: 15px;
    }
    .comment .comment_head {
        color: #eee;
        background: #2B91FF;
        padding: 4px;
        margin-left: -85px;
    }
    .comment .comment_con .comment_text {
        margin-right: 5px;
        min-height: 80px !important;
        color: #333;

    }
    .comment .comment_con {
        min-height: 100px;
    }
    .comment .comment_avatar {
          margin-top: 0px;
          margin-left: 5px;
          
    }
    .comment .comment_avatar img {
          margin-top: 30px;
          margin-left: -8px;
          height: 120%;
    }
    .comment .comment_footer {
        margin-left: -10px;

    }
    .comment_edit_str {
        padding-left: 5px;
    }
/* ########## COMMENTS ########## */     

    .gallery_image {
		border: 1px solid;
	}

    .content_head h2.content_title {
        font-size: 16px;
    }
    .userNicknames {
        color: #eee;
    }
    .rmus-icon:hover {
        transition: all 0.3s ease-in-out 0s;
        border: 1px solid #eee !important;
    }
}
