css:
/*NETFLIX.COM Dark Theme - Draft made in 5 minutes while on support*/

@-moz-document domain(netflix.com) {

body { background-color:#333; }
html {color :#F1F1F1;}
#displaypage-overview-container { background-color:#222 !important; }
#memberReviews { background-color:inherit; }
#fground #bd.main-content { background-color:#333; }
.hd, .mrow .hd h3 a { color:#FFF; }
.agMovie-lulg .boxShot, .agMovie-lubxsm .boxShot { border-color: #600; }
#hd { background-color:#111; border-bottom-color:#600; }
#hd #profilesLauncher .profileImg { border-bottom-color:#600; }
#profilesLauncher { color:#AAA; }
#displaypage-details { color:#999; }
#mr-reviewsList .center { color:#999; }
#episodeColumn ul.episodeList li:hover, #episodeColumn ul.episodeList li.current {
opacity: 0.7;
}
#displaypage-details h3, #displaypage-details h4 { color:#FFF; }

#displaypage-overview #displaypage-overview-details .ratingsInfo { color:#FFF; }
#displaypage-rental-relationship { color:#DDD; }
#displaypage-overview #displaypage-overview-details .certRating, #displaypage-details .maturityRating.certRating .value { color:#000; }
#displaypage-details { color:#CCC; }

.svfb-silver, .dp-btn {
border: 1px solid #555;
background-color: #777;
color:#FFF !important;
background-image: -webkit-gradient(linear, left top, left bottom, from(#777), to(#666));
background-image: -moz-linear-gradient(top, #777, #666);
background-image: -webkit-linear-gradient(top, #777, #666);
background-image: linear-gradient(top, #777, #666);
}
#seasonSelector .selectorLabel.dp-btn {
color:#FFF !important;
}
.dp-btn {
box-shadow: 0 0 8px 0 RGBA(255,255,255,.1 ) !important;
}

.svfb-silver span, .svfb-default span, #mr-writeReviewBtn {
color:#FFF !important;
}
#BobMovie-content {
color:#000;
}
.dmbr {
background-color: #444;
border: solid 1px #222;
}

.taste-container .taste-ribbon {
background-color: #444;
border: solid 1px #222;
}
}
