css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("mydealz.de") {    
    html * {
        font-family:Arial !important;
        font-size: 13px;
    }
    
    .page-content {
        min-width:1024px !important;
    }
    
    h1, .thread-degree h2 * {
        color: black !important;
    }
    
    h1, .thread-degree .degreeValue-unit {
        font-size: 20px !important;
    }
    
    .thread-title a, h2 * {
        font-size: 18px !important;
    }
    
    .pagi-link, .avatar-link {
        font-size: 1em !important;
    }
    
    #main ol .thread-head > p:first-of-type {
        display: none;
    }
    
    .button--type-teaser {
        color: white;
        text-shadow:none;
    }
    
    .section--padded--tight, .section--paddedSlim {
        padding: 10px 20px !important;
    }
    
    li.comments-item {
        padding: 15px 15px 0px 15px !important;
        min-height:115px;
    }
    
    .thread {
        padding:20px 20px 20px 20px !important;
    }
    
    .comments-head {
        padding: 0px 10px 0px 0px;
    }
    
    .comments-footer, .thread-main footer {
        text-align: right !important;
        padding-top:0px !important;
    }
    
    .thread-main footer {
        margin-top:8px !important;
    }
    
    .thread-main > footer > p {
        display: none;
    }
    
    .comments-footer button, .reasons em {
        font-weight: normal;
        font-size: 11px;
    }
    
    .reasons br {
        display:none;
    }
    
    .reasons {
        margin-top:5px;
    }
    
    .pagi-link {
        padding:5px;
    }
    
    .section-data.fGrid > p {
        padding:0;
    }
    
    .button--type-teaser
    {
        background: linear-gradient(to bottom, #97d067 1px, #6bb736 1px, #439719);
        background: -webkit-linear-gradient(top, #97d067 1px, #6bb736 1px, #439719);
        background-color: #439719;
        border-color: #378507;
        color: #fff;
        text-shadow: 0 1px 1px rgba(0,0,0,0.5);
    }
    .button--type-teaser:hover,.button--type-teaser:focus
    {
        background: linear-gradient(to bottom, #bfe889 1px, #9fdb5a 1px, #57bd32);
        background: -webkit-linear-gradient(top, #bfe889 1px, #9fdb5a 1px, #57bd32);
        background-color: #57bd32;
    }
    .button--type-teaser:active
    {
        background: linear-gradient(to bottom, #3f8d10 1px, #5dab1d 1px, #3a8a0c);
        background: -webkit-linear-gradient(top, #3f8d10 1px, #5dab1d 1px, #3a8a0c);
        background-color: #3a8a0c;
    }
    
    .sideList-item {
        line-height:1.3em;
    }
    
    .navTrigger2-row, .navTrigger2, .nav2Morph, .nav2Morph button {
        height:30px !important;
        line-height:30px !important;
        padding:0px ;
    }
    
    .menu-list li a {
        padding:0px 8px;
    }
    
    .navTrigger2 {
        padding-left:8px;
    }
    
    .header-nav-count {
        line-height:10px;
        padding:5px !important;
    }
    
    .navTrigger2-row span {
        padding:8px;
    }
    .navTrigger2-row a {
        height:30px !important;
        line-height:30px !important;
        padding:0px 8px 0px 0px;
    }
    
    .navTrigger2-row a span {
        padding:8px;
    }
    
    .degreeVote-up, .degreeVote-down {
        height: 18px !important;
        padding:0px 5px !important;
    }

    .comments-item header .ico--type-like-blue {
        font-weight:bold;
        /*color: rgb(29, 69, 109) !important;*/
    }
    
    .bbcode_quote {
        margin-bottom: 0px !important;
    }
    .bbcode_quote_head {
        font-weight:bold;
        margin:0px !important;
    }
    
    .bbcode_quote_body br:empty {
        display:none;
    }
    
    .comment-options li button, 
    .thread-meta a, 
    footer button.link, footer a.link,
    .thread-title a {
        color: rgb(29, 69, 109) !important;
        padding:0px !important;
        margin:0px !important;
    }
    
    .degreeMeter, .degreeMeter-progress {
        height:18px;
    }
    
    .thread-degree .degreeValue {
        text-align:left;
        line-height:1em;
    }
    
    .thread-degree .degreeValue-unit {
        font-size:20px;
    }
    
    .degreeValue-new {
        height:17px;
        padding:2px 6px;
        font-size:12px;
    }
    
    .thread-deal .imageFrame {
        background:none !important;
        border:0px;
        height:160px;
        vertical-align:top !important;
    }
    
    .thread-deal .imageFrame img {
        padding-bottom:25px;
    }
    
    .thread-deal .button {
        margin-top:0px !important;
    }
    
    .section-sub {
        margin-right:5px;
    }
    
    .comments-footer {
        padding:10px;
    }
    
    .comments-body {
        min-height:52px;
    }

    
    /*optional*/
    
    .hList-item .fIcon--comments, .fIcon--shares, .topBar {
        display: none;
    }
    
    form.newsletter {
        display:none;
    }
    
    /*
    .input {
        border-radius:0px;
    }
    */
}
