css:
@-moz-document url-prefix('http://board.playtrickster.com'),
url-prefix('https://board.playtrickster.com')
{
::selection {
color: #000;
background: transparent;
}

a {
color: #333;
text-decoration: none;
}

#user_info_cell, #user_info_cell * {
color: #333 !important;
text-shadow: rgba(0,0,0,0.9) 0px 1px 8px;
}

#secondary_navigation #breadcrumb li>span {
padding-left: 4px;
background: none transparent;
  color: #bbb;
text-shadow: rgba(255,255,255,0.4) 0px 1px 1px;
}

body {
background-position: 50% 0;
background-attachment: fixed;
background-repeat: no-repeat;
background: url(http://i.cubeupload.com/GCFOOC.png);
font: 11px consolas;
position: relative;
padding-bottom: 20px;
  color: #555;
}

#secondary_navigation a {
color: #bbb;
line-height: 37px;
height: 37px;
}

.maintitle a {
color: #bbb;
}

tr {
display: table-row;
vertical-align: inherit;
border-color: transparent;
border-top-color: transparent;
border-right-color: transparent;
border-bottom-color: transparent;
border-left-color: transparent;
  background: url(http://i.cubeupload.com/xO0zHG.png);
}

#recentajaxcontent li, #idm_categories a, #idm_categories li.with_sub.open li, #index_stats .status_list li, #panel_files .file_listing li, #panel_screenshots #ss_linked li, .file_listing, #cart_totals td, div#member_filters li, #files li, .ipsType_sectiontitle, #order_review td, #package_details .package_info, .block_list li, .package_view_top, .member_entry, #help_topics li, .ipsBox_container .ipsType_pagetitle, .userpopup dl, #announcements td, .sideVerticalList li, fieldset.with_subhead ul, .ipsList_data li, .ipsList_withminiphoto li, .ipsList_withmediumphoto li, table.ipb_table td, .store_categories li, #mini_cart li, #index_stats div[id*="statusReply"], #ipg_category .ipg_category_row, .block_inner .ipb_table td, .gallery_pane h2, .status_feedback li[id*="statusReply"], .ipsSideMenu ul li, #usercp_content .ipsType_subtitle, .ipbfs_login_row, .articles .block-1, .articles .type-1x2x2 .article_row, #idm_category .idm_category_row, #category_list li a, .ipsComment, #forum_table.ipb_table tr:last-of-type td {
border-top: 1px solid transparent;
border-bottom: 1px solid transparent;
}

.border, .statistics, .post_block, .ipsComment, .popupInner, .no_messages, .general_box, .ipsSideBlock, .poll_question ol, .ipsBox_container, .ipsFloatingAction {
background: url(http://i.cubeupload.com/xO0zHG.png);
}

.removeDefault .ipsBox, .removeDefault.ipsBox {
padding: 0;
background: url(http://i.cubeupload.com/YKzI0f.png);
}

tbody {
display: table-row-group;
vertical-align: middle;
border-color: transparent;
}

#idm_category .idm_category_row, .store_categories li, #idm_categories a, .ipsList_data li, .ipsList_withminiphoto li, .ipsList_withmediumphoto li, table.ipb_table tr td {
background-repeat: repeat-x;
background-position: 0 0;
background: url(http://i.cubeupload.com/DvPPSv.png);
}

#user_navigation .user_photo {
position: relative;
vertical-align: top;
padding: 0;
height: 24px;
width: auto;
margin: 6px -6px 0px -6px;
  border-radius:50px;
}

.post_block h3, .post_block h3 a, .post_block .desc.blend_links a {
color: #bbb;
}

.post_block h3 {
border-top: 1px solid #333;
border-bottom: 1px solid #333;
text-shadow: rgba(0,0,0,0.4) 0px -1px 0px;
background: #484848 url(http://i.cubeupload.com/YKzI0f.png) repeat-x 0 -1px;
}

.ipsType_pagetitle, .ipsType_subtitle {
font: 300 26px/1.4 Helvetica,Arial,sans-serif;
color: #bbb;
  text-shadow: rgba(255,255,255,0.4) 0px 1px 1px;
}

.ipbfs_titlebox .ipsType_pagedesc, .ipbfs_titlebox .desc, .ipbfs_titlebox .desc a {
color: #bbb !important;
  text-shadow: rgba(255,255,255,0.4) 0px 1px 1px;
}

.maintitle {
color: #bbb;
text-shadow: rgba(255,255,255,0.4) 0px 1px 1px;
}

.pagination .page a, .pagination .back a, .pagination .forward a {
color: #fff;
text-shadow: rgba(255,255,255,0.4) 0px 1px 1px;
background: rgba(0,0,0,0.7);
box-shadow: inset rgba(0,0,0,0.7) 0px 1px 3px, rgba(255,255,255,0.3) 0px 1px 0px;
}

.pagination .pages li.active {
color: #fff;
text-shadow: rgba(255,255,255,0.4) 0px 1px 1px;
}       

#profile_photo.ipsUserPhoto {
-webkit-border-radius: 0px;
border-radius: 5px;
border: 1px solid #aaa;
padding: 1px;
background: #fff;
-webkit-box-shadow: rgba(0,0,0,0.4) 0px 1px 6px;
-moz-box-shadow: rgba(0,0,0,0.4) 0px 1px 6px;
box-shadow: rgba(0,0,0,0.4) 0px 1px 6px;
}
 }
