css:
@-moz-document domain("weblancer.net") {
div#header_content {
	display: none;
}
#top_menu_items {
	width: auto;
}
div#top_menu {
	float: right;
	clear: initial;
	height: 105px;
	border-bottom: 0;
	min-width: 0;
	margin-top: -105px;
}
#header {
	width: 100%;
	background-color: #588AAD;
}
#header_logo {
	background-color: #FFF;
}
div#top_menu div.tm_item {
	padding: 0;
}
div#top_menu div.tm_item a {
	padding: 41px 40px 42px;
	display: inline-block;
	text-decoration: initial;
	font-size: 14px;
}
div#top_menu div.tm_item a:hover {
	border-bottom: 5px solid #94A2EC;
	background-color: rgba(255, 255, 255, 0.1);
}
.pages_list a, .pages_list b {
	padding: 5px 7px;
	background-color: #E4E4E4;
	min-width: 20px;
	display: inline-block;
	text-align: center;
	text-decoration: initial !important;
	margin: -1px;
	font-size: 14px;
}
.pages_list a:hover {
	background-color: #6194AC;
	color: #fff;
}
#footer table {
	background-color: transparent;
}
#footer {
	color: #8A8A8A;
	background-color: #173E4B;
	border-top: 0;
}
#footer a {
	color: #AFAFAF;
	text-decoration: initial;
	border-bottom: 1px dotted #555;
	padding: 0 0px 2px;
	margin: 5px 5px;
}
div.pages_list.text_box {
	padding: 0;
	margin-top: 5px;
	margin-bottom: 5px;
	margin-left: 3px;
}
div.pages_list.text_box>span {
	display: none;
}
#left_panel, #left_panel_2 {
	background-color: rgba(131, 131, 131, 0.14);
	box-shadow: inset -5px 0px 12px -10px rgba(0, 0, 0, 0.4);
	position: relative;
}
div.featured_item {
	display: none;
}
div.up_item_2 {
	font-size: 20px;
	line-height: 10px;
	color: #60BAD6;
}
div.up_item_2>a {
	line-height: 10px;
	text-decoration: none;
	color: #455E69;
	border-bottom: 1px dotted #999;
}
div.informer {
	display: none;
}
#right_panel {
	border-left: 1px solid #fff;
}
#user_panel {
	border-top: 1px solid #D1D2D3;
	position: fixed;
	z-index: 100;
	left: 296px;
	bottom: 0;
	width: 100%;
	border-bottom: 0px solid #57553A;
}
div.rating {
	padding: 5px;
	background-color: rgba(255, 255, 255, 0.39);
	border-radius: 3px;
}
td.st_r {
	padding: 0;
}
.stats_table td {
	padding: 3px 0 5px;
}
.tabs_box {
	padding-top: 5px;
	padding-left: 296px;
}
.tab, .tab_active, .tabs_box td.tab, .tabs_box td.tab_active {
	background-image: none;
	min-width: 109px;
	text-align: center;
	padding: 5px;
}
td.il_small {
	font-size: 15px;
	text-align: center;
	color: #0395F7;
}
.il_main a.item {
	font-size: 23px;
	font-weight: 400;
	text-decoration: none;
	border-bottom: 1px dotted #BDDEEB;
	line-height: 30px;
}
.il_main a.item:hover {
	border-bottom: 1px dotted #000;
}
div.il_item_descr {
	font-size: 10px;
}
.st_n, .st_l, .st_r {
	border-bottom: 0px solid #000;
}

}
