css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("streamallthis.me"), domain("streamallthis.is"), domain("movieallthis.me")
{
    html
    {
        background-color: rgba(17,17,17,0.6) !important;
        background-image: url(data:image/png;base64,) !important;
        background-clip: border-box !important;
        background-origin: padding-box !important;
        background-repeat: repeat !important;
        background-size: auto !important;
        background-position: left top !important;
        background-attachment: scroll !important;
        display: block !important;
        height: 100% !important;
        width: 100% !important;
    }
    
    body > table
    {
        background-color: rgba(17,17,17,0.6) !important;
        width: 862px !important;
    }
    
    body > table:nth-child(1)
    {height: auto !important;}
    
    body > table:nth-child(2),
    body > table:nth-child(6)
    {display: none !important;}
    
    a, h1, .fb, .fc, .fh
    {color: #4183C4 !important;}
    
    a:hover
    {color: #0FECFE !important;}
    
    a:visited
    {color: #C080FF !important;}
}

@-moz-document regexp("^https?://(streamallthis.me|streamallthis.is|movieallthis.me)/?$")
{
    body > table:nth-child(4)
    {display: none !important;}
}

@-moz-document regexp("^https?://(streamallthis.me|streamallthis.is|movieallthis.me)/watch.*$")
{
    body > table:nth-child(3) > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(2),
    body > table:nth-child(4),
    .fd > iframe:nth-child(17)
    {display: none !important;}
}

@-moz-document regexp("^https?://(streamallthis.me|streamallthis.is|movieallthis.me)/watch/.*\.html$")
{
    body > table:nth-child(3) > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(1) > table:nth-child(1) > tbody:nth-child(1) > tr:nth-child(3) > td:nth-child(1)
    {text-align: center !important;}
}

@-moz-document regexp("^https?://(streamallthis.me|streamallthis.is|movieallthis.me)/movies/watch/.*\.html$")
{
    body > table:nth-child(3) > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(1) > table:nth-child(1) > tbody:nth-child(1) > tr:nth-child(3) > td:nth-child(1)
    {text-align: center !important;}
        
    body > table:nth-child(3) > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(2)
    {display: none !important;}
}
