css:
@-moz-document regexp("https://readmanga\\.me/.+/vol.+") {
* {
  font-family: 'Segoe WP', 'Droid Sans'
}

body {
  background: #000 !important;
}

.reader-bottom,
.bottom_block,
.top_ba {
  display: none !important;
}

.nav-main-menu,
.right-control {
  display: none !important;
}

.pageHeader {
  background: inherit !important;
  font-size: 9px !important;
  display: none !important;
}

.federation,
.navbar,
.federation-nav,
.second-nav {
  display: none !important;
}

.top-block {
  width: 100% !important;
  border-radius: 0px !important;
  background: rgba(0, 0, 0, 0.8) !important;
  opacity: 1 !important;
  color: #aaa !important;
  font-size: 13px !important;
  border: none !important;
  box-shadow: none !important;
  padding: 3px !important;
  margin: 0px !important;
  position: fixed !important;
  display: block !important;
  z-index: 10 !important;
}

.top-block input,
.top-block button {
  background: initial !important;
  border: inherit !important;
  -moz-appearance: toolbarbutton !important;
  -webkit-appearance: toolbarbutton !important;
  color: #aaa !important;
  height: 23px !important;
}

.pageSelector option {

  background: white !important;
  color: #000 !important;
  opacity: 1 !important;
}

.chosen-container-single .chosen-single,
.pageSelector {
  background: rgba(255, 255, 255, 0.1) !important;
  border: none !important;
  box-shadow: 1px 1px 1px rgba(150, 150, 150, 0.1) !important;
  border-radius: 2px !important;
  color: #666 !important;
  height: 24px !important;
  transition: background-color 0.2s, color 0.2s;
}

.chosen-container-single .chosen-single:hover,
.pageSelector:hover {
  background: rgba(255, 255, 255, 0.2) !important;
  color: #888 !important;
  transition: background-color 0.15s, color 0.15s;
}

.chosen-container-single .chosen-drop {
  -moz-appearance: listbox !important;
  -webkit-appearance: menuitem !important;
  border-radius: 0px !important;
  background: #000 !important;
  opacity: 0.9 !important;
  color: #ccc !important;
  top: 24px !important;
  padding: 0px !important;
  bottom: auto !important;
  border: none !important;
}

.chosen-container-single .chosen-drop .chosen-results {
  background: inherit !important;
  width: 390px !important;
  max-height: 250px !important;
}

.chosen-container-single .chosen-drop .chosen-results li {
  -moz-appearance: toolbarbutton !important;
  -webkit-appearance: toolbarbutton !important;
}

#mangaBox {
  border-radius: 0px !important;
  box-shadow: none !important;
  border: none !important;
  padding-top: 31px !important;
  background: initial !important;
}

#mangaBox h1 {
  display: none;
}

.page-footer {
  display: none;
}
}
