css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("bitfinex.com") {
    /** GLOBAL **/
    body {
        color: #ccc;
        background-color: #000;
    }
    input[type="text"],
    input[type="text"]:focus {
        background-color: #020202;
        color: #438DB6;
        border: none;
    }
    table {
        background-color: #333;
        border: none;
    }
    table tr.even, table tr.alt, table tr:nth-of-type(2n) {
        background-color: #4D4D4D;
    }
    
    
    /** FORM STUFF **/
    form.custom .custom.dropdown,
    form.custom .custom.dropdown ul {
        background: linear-gradient(to bottom, #191919 0%, #333333 100%) repeat scroll 0% 0% transparent;
        border: none;
    }
    form.custom .custom.dropdown a.current,
    form.custom .custom.dropdown ul li {
        color: #fff;
    }
    form.custom .custom.dropdown ul li.selected,
    form.custom .custom.dropdown ul li.selected:hover,
    form.custom .custom.dropdown ul li:hover {
        background-color: #191919;
        color: #fff;
    }
    
    
    /** HEADER **/
    #header {
        background: #438DB6;
    }
    
    /** SUB-HEADER **/
    #sub-header {
        background-color: #000;
    }
    #sub-header .row {
        background-color: #191919;
        padding-top: 14px;
        padding-bottom: 10px;
    }
    #sub-header h5,
    #sub-header h6 {
        color: #438DB6;
    }
    #sub-header table td {
        color: #b3b3b3;
    }
    #sub-header #lastprices .last-prices {
        border-right-color: #333;
    }
    
    
    /** FOOTER **/
    #menu-footer,
    #menu-footer .row,
    #menu-footer .row .top-bar,
    #menu-footer .row .top-bar-section ul,
    #menu-footer .row .top-bar-section ul li a:not(.button) {
        background: #438DB6;
        color: #fff;
    }
    #menu-footer .row .top-bar {
        border: none;
    }
    
    
    /** COMBINED STUFF **/
    #sub-header,
    #menu-footer {
       -webkit-box-shadow: none;
       -moz-box-shadow: none;
       -o-box-shadow: none;
       -ms-box-shadow: none;
       box-shadow: none;
       margin: 0px;
       padding: 0px;
    }
    
    
    /** MENU BARS **/
    #interface #menu.button-group .button {
        border-bottom: none;
    }
    #interface #sub-menu.button-group .button {
        border-top: none;
    }
    #interface #menu .button,
    #interface #sub-menu .button {
        background-color: #333;
    }
    nav.top-bar,
    nav.top-bar ul.right,
    nav.top-bar ul.right li a,
    nav.top-bar ul.left,
    nav.top-bar ul.left li a {
        background-color: black;
        border: none;
    }
    nav.top-bar ul.right {
        position: absolute;
    }
    div#boxes div.panel {
        background-color: #438DB6;
    }
    
    
    /** SIDEBAR **/
    #interface .wallet {
        background-color: #438DB6;
    }
    #interface .sidebar .links,
    #interface .sidebar .manage-wallet {
        background-color: #191919;
    }
    #interface .sidebar table tr td {
        background-color: #191919;
    }
    #interface .sidebar .links ul li {
        text-align: center;
        background-color: #438DB6;
    }
    #interface .sidebar .links ul li a {
        color: #fff;
    }
    #interface .sidebar .links ul li a:hover {
        background-color: #347091;
    }
    
    
    /** MAIN **/
    #interface {
        background-color: #191919;
    }
    #interface table tr td {
        color: #b3b3b3;
    }
    #interface .actions,
    #interface .actions .panel,
    #interface .actions .options .ordersize {
        background-color: #191919;
        border: none;
    }
    #interface #orders {
        border: none !important;
    }
    #interface section.active p.title {
        border: none;
    }
    
    
    /** ORDERBOOK **/
    #interface table thead th {
        background-color: #4d4d4d;
        color: #438DB6;
    }
    #interface table thead.first th {
        background-color: #333;
    }
    #interface #bids table thead.first th {
        color: #9DC24A;
    }
    #interface #asks table thead.first th {
        color: #C43C35;
    }
    #interface table tbody tr {
        background-color: #333 !important;
    }
    
    
    div#community {
        background-color: black;
    }
    div#community div.info,
    div#community div.info h4,
    div#community div.info a {
        background-color: #333;
    }
    div#community div.section-container section p.title a,
    div#community div.section-container section div.content,
    div#community div.section-container section div.content p {
        background-color: #666;
    }
    div#community div.section-container section div.content p {
        color: #9DC24A;
    }
    
    
    div#pre-footer div.row {
        background-color: #333;
        padding-top: 12px;
    }
    div#pre-footer div.row div.contacts div.panel {
        border: none;
        background-color: #333;
    }
}
