css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("dafilms.cz") {
    body{
        background: #070707;
    }
    img.abs.logo {
        display: none;
    }
    ul#nav.nav.abs.upper li.first.active{
        background: #777;
    }
    ul#nav.nav.abs.upper li{
        color: #333;
        background: #111;
    }
    ul#nav.nav.abs.upper li a{
        color: #333;
    }
    
    #article ul.nav.sub-nav.upper li.active{
        background: #777;
    }
    
    #article ul.nav.sub-nav.upper li{
        background: #111;
        border: #070707;
    }
    #article ul.nav.sub-nav.upper li a{
        color: #333;
    }
    
    p.abs.motto.upper{
        background: #070707;
        color: #555;
    }
    #footer .article a{
        color: #555;
    }
    table.movie-details tr.zebra td, table.movie-details tr.zebra  th{
        background: #222;
    }
    table.movie-details tr td, table.movie-details tr th{
        color: #ddd;
        background: #070707;
    }
    
    #article .article h3 a{
        color: #777;
    }
    #article .pagination a.page{
        color: #777;
        border: 1px solid #777;
    }
    #article .pagination a.next, a.prev{
        color: #777;
    }
}
