-webkit-box-shadow: 0 0px 0px #888;
box-shadow: 0 0px 0px #888;
border-radius: 0px 0px 0px 0px;
-webkit-border-radius: 0px;
-moz-border-radius: 0px;
}

.masthead-bar {
background: #efefef;
