/* "Google - Dusl-color header", based from "Google - Dark header"
   
   Localization issues? Leave feedback! */

@namespace url(http://www.w3.org/1999/xhtml);

/* Works on most Google addresses. */
@-moz-document regexp("https?:\/\/.*\.google\.*"){

/* Border rounding */
.gb_Fb{
border-radius: 0px 30px 30px 0px !Important;
padding-right: 16px !Important;
}
#gbqfb{
border-radius: 0px 15px 15px 0px !Important;
}

/* Color and border settings */
.gbqfqw{
border: 2px solid #4585f3 !important;
}
#gbqfb{
background-color: #4585f3 !important;
}
.gb_Oa /* Left */,
#gbq1.gb_f,
.gb_Fb /* Center */{
background: #2d2d2d !important;
}

}
