css:
@namespace url(http://www.w3.org/1999/xhtml);

/*
Simple Dark Google v1.0
*/

@-moz-document url-prefix(http://www.google),url-prefix(http://images.google), url-prefix(http://news.google),
               url-prefix(http://blogsearch.google),url-prefix(http://books.google), url-prefix(http://translate.google),
               url-prefix(http://video.google), url-prefix(https://encrypted.google),url-prefix(https://www.google){
    
    body{      
        background:#232323;
    }
    
    html{
        background:transparent !important;
    }
    
    a, font, span, p, div{
        color:#ADADAD !important;
    }
    
    /*Header*/ 
    .gb_Fb, #mngb{
        background:#181818;
        border-bottom:1px solid #000;
        box-shadow: 0 1px 0 0 #303030;
    }
    
    /*Big logo*/
    #hplogo{
        background:url("http://i.imgur.com/rshm8Mk.png") no-repeat scroll 0% 0% / 269px 95px transparent !important;
    }
    
    /*Small logo*/
    .gb_Pb > .gb_Ca{
        background:url("http://i.imgur.com/rshm8Mk.png") no-repeat scroll 0% 0% / 80px 35px transparent;
    }

    
    /*Footer*/
    #fbar{
        background:#181818;
        border-top:1px solid #000;
        box-shadow: inset 0 1px 0 0 #303030;
    }
    
    
    /*Search*/ 
    .gbqfqw{
        background:#303030;
        border:1px solid #000;
        color:red;
    }
    
    .gbqfif{
        color:#ADADAD;
    }
    
    /*Searchhint*/
    .gssb_m{
        background:#303030 !important;
        outline:1px solid #000;
    }
    
    .gsq_a{
        color:#ADADAD !important;
    }
    
    
    
    /*Buttons*/
    .gbqfba,.gb_Ub,.gbqfb{
        background:#161616 !important;
        border:1px solid #303030 !important;
        color:#ADADAD !important;
    }
    .gbqfba:hover{
        background:#202020;
        border:1px solid #333333;
        color:#ADADAD !important;
    }
    .gb_Ub:hover{
        background:#202020;
        border:1px solid #333333;
        color:#ADADAD !important;
    }
    .gbqfb:hover{
        background:#202020 !important;
        border:1px solid #333333 !important;
        color:#ADADAD !important;
    }

    
    /*Search results page*/
    #hdtbSum, #extabar{
        background:#232323;
        border-bottom:1px solid #000;
        box-shadow:  0 1px 0 0 #303030;
    }
    
    .g{
        border-bottom:1px solid #000;
        box-shadow:  0 1px 0 0 #303030;
    }
    
    /*Image search*/
    #rcnt, #cnt{
        background:#232323;
    }
    
    .rg_fbl, .nj{
        border:none;
    }
   
    #ifbd{
        background:#000;
        border-bottom:1px solid #303030;
    }
    
    
    /*Extra menu*/
    .gb_Ib, .gb_z, .gb_A, .gb_B, .gb_v{
        background:#202020;
    }
    
    .gb_C, .gb_Rb{
        background:#121212;
    }
    
    .gb_Va{
        outline:none;
        box-shadow:none;
    }
    
    .gb_l{
        border:1px solid #000;
        box-shadow:none;
    }

    
    

    /*Hide chrome advert*/
    .pmoabs{
       visibility:hidden;
    }
}
