css:
@-moz-document domain(baoz.me) {
@font-face {
  font-family: 'Open Sans';
  font-style: normal;
  font-weight: 300;
  src: local('Open Sans Light'), local('OpenSans-Light'), url(http://fontscontent.useso.com/static/fonts/opensans/v8/DXI1ORHCpsQm3Vp6mXoaTeY5mlVXtdNkpsMpKkrDXP4.woff) format('woff');
}
@font-face {
  font-family: 'Open Sans';
  font-style: normal;
  font-weight: 400;
  src: local('Open Sans'), local('OpenSans'), url(http://fontscontent.useso.com/static/fonts/opensans/v8/u-WUoqrET9fUeobQW7jkRbO3LdcAZYWl9Si6vvxL-qU.woff) format('woff');
}
@font-face {
  font-family: 'Open Sans';
  font-style: normal;
  font-weight: 600;
  src: local('Open Sans Semibold'), local('OpenSans-Semibold'), url(http://fontscontent.useso.com/static/fonts/opensans/v8/MTP_ySUJH_bn48VBG8sNSuY5mlVXtdNkpsMpKkrDXP4.woff) format('woff');
}
body{font-family:"Open Sans","helvetica neue" Tahoma, "Hiragino Sans GB","Microsoft Yahei" !important}
.webpage{max-width:100% !important}
.toolbar_bg,.view_toolbar_top {background:#f1f1f1 !important}
.container{box-shadow: 0 0 4px #D5CEB8 !important}
.content{box-shadow: 0 0 6px #999 !important;z-index:99 !important}
.menubar {background:#F3F0E6 !important;}
.menubar li a{background:#F3F0E6 !important;}
.menubar>div:last-child>div:first-child li {float:left;}
.menubar li.icon_user {height:auto !important;float:none !important;width: 100% !important}
.menubar .icon_user a {padding: 10px 10px 0 !important;height:auto !important}
.menubar .icon_user div.img_32 {float:none !important;width:auto !important;height: auto;}
.menubar .icon_user div.img_32 img {display:block !important;margin: 0px auto !important;width: 64px !important;;height: 64px !important;;border-radius: 100% !important;;}
.menubar .icon_user .title {float:none !important;height:auto;}
.menubar .icon_user div.title h3 {display: block !important;text-align: center !important;width: 120px !important;height: auto !important;padding: 10px 20px !important;}
.menubar li a:hover, .menubar li.active a:hover, .menubar li.active a {box-shadow:none !important;background-color:#FCFBF7 !important;}
.menubar>div:last-child>div:first-child ul:before,.menubar>div:last-child>div:first-child ul:after{ content:""; display:table; }
.menubar>div:last-child>div:first-child ul:after{ clear:both; }
.menubar>div:last-child>div:first-child li {float:left;width:30% !important}
.menubar>div:last-child>div:first-child li.icon_user {width: 100% !important}
.menubar>div:last-child>div:first-child li:nth-child(3n){width:40% !important}
.menubar>div:last-child>div:first-child li:nth-child(3n) sup {right:55px !important}
.menubar>div:last-child>div:first-child li .icon_32{width:25px;}
.menubar>div:last-child>div:first-child li .icon_32 b{font-size:14px;margin-left:5px !important}
.menubar li.setting{position:static !important;width:auto !important;border-bottom:1px solid #ddd !important}
.menubar .setting .icon_32 b {padding-left:0 !important;}
.menubar li.setting div.title h3{line-height:50px !important;}
.menubar li sup,.list li sup {font-family:"open sans";font-weight:300;font-size:12px !important;line-height:12px !important;padding:3px 6px 1px !important;}
.menubar_shadow{display:none !important}
.view_toolbar .icon_32{font-size:16px  !important;}
.list li a {background: #FCFBF7 !important}
.list li a:hover {background: #fbfbfb !important}
.toolbarright .icon_24{font-size:16px !important;width:16px !important}
.content .view_body .page {padding: 20px 50px !important; max-width:750px  !important}
.message cite {padding:0 5px  !important;background:#FCFBF7 !important;border-radius:30px !important}
}
