body.hp, body.vasq {
	background-image: url('/*[[image]]*/');
    background-repeat: no-repeat;
    background-size: cover;
}

body .content {
	background-image: url('/*[[image]]*/');
    background-repeat: no-repeat;
    background-attachment: fixed;
}

body.vasq #fbar {
    background: rgba(242,242,242,0.5);
    border: none;
}

body #hdtbSum {
    background: rgba(242,242,242,0.5);
    border: none;
}

body #hdtb {
    background: rgba(242,242,242,0);
