	color: black;
}

body [name=btnK], body [name=btnI] {
    background: rgba(245,245,245,0.3);
}

body [name=btnK]:hover, body [name=btnI]:hover {
    background: rgba(248,248,248,0.3);
    border-color: rgba(0,0,0,0.1);
}

body [name=btnK]:focus, body [name=btnI]:focus {
    -webkit-box-shadow: none;
	-moz-box-shadow: none;
	box-shadow: none;
}


