css:
@-moz-document url("http://www.omegle.com/") {
#sharebuttons * {
vertical-align: top;
  display: none;
}

.disconnectbtnwrapper.newbtn {
background: #409ffe;
background-image: -o-linear-gradient(top,#017ffe 0,#017ffe 100%);
background-image: -moz-linear-gradient(top,#017ffe 0,#017ffe 100%);
background-image: -webkit-gradient(linear,0 0,0 100%,from(#80c0ff),to(#017ffe));
background-image: -webkit-linear-gradient(top,#017ffe 0,#017ffe 100%);
background-image: -ms-linear-gradient(top,#017ffe 0,#017ffe 100%);
background-image: linear-gradient(top,#017ffe 0,#017ffe 100%);
color: white;
}

#header {
position: relative;
padding: 8px .5em 0 .5em;
background: white;
padding-bottom: 8px;
box-shadow: 0 0em 0em #CCC;
-moz-box-shadow: 0 0em 0em #CCC;
-webkit-box-shadow: 0 0em 0em #CCC;
transition: height 2s;
 -webkit-transition: height 2s;
}

.logwrapper {
border: 1px solid #CCC;
border-top-left-radius: .5em;
border-top-right-radius: .5em;
-moz-border-radius-topleft: .5em;
-moz-border-radius-topright: .5em;
-webkit-border-top-left-radius: .5em;
-webkit-border-top-right-radius: .5em;
background: white;
position: absolute;
left: .5em;
right: 0em;
bottom: 6em;
width: 99%;
_height: 250px;
}



body {
margin: 0;
padding: 0;
background: #ffffff;
height: 100%;
font-family: Helvetica,Arial,sans-serif;
}

#mobilesitenote img {
display: none;
}

#intro {
line-height: 1.5;
margin: .5em;
margin-top: 1em;
margin-right: 176px;
padding: .5em;
padding-bottom: 1em;
padding-top: .5em;
border: 0px solid #CCC;
background: white;
border-radius: .5em;
-moz-border-radius: .5em;
-webkit-border-radius: .5em;
box-shadow: inset 0 0 0em #BBB;
-webkit-box-shadow: inset 0 0 0em #BBB;
-moz-box-shadow: inset 0 0 0em #BBB;
width: 98%;
}


#monitoringnotice {
border-radius: 0em;
-moz-border-radius: 0em;
-webkit-border-radius: 0em;
border: 1px solid #CCC;
box-shadow: 0 0 0em #bfdeff;
-webkit-box-shadow: 0 0 0em #d9ebff;
-moz-box-shadow: 0 0 0em #d9ebff;
padding: 0;
margin: auto;
margin-top: .5em;
margin-bottom: 1em;
text-align: center;
max-width: 36em;
background: #d9ebff;
position: relative;
overflow: hidden;
font-family: 'Lucida Grande','Lucida Sans Unicode',Lucida,sans-serif;
}

#monitoringnotice h2 {
line-height: 1;
padding: .3134em 0;
font-size: 1.25em;
margin: 0;
background: #d9ebff;
}



#tagline img {
display: none;
}

#feedback.collapsed h2 {
display: none;
}

.chatmsg {
width: 100%;
background: white;
font-family: sans-serif;
font-size: 1em;
border: 0 none;
padding: 0;
margin: 0;
height: 4.5em;
font-size: 1em;
overflow: auto;
-webkit-appearance: none;
border-radius: 0;
-webkit-border-radius: 0;
}

.sendbtn {
font-family: sans-serif;
font-size: 1em;
background: white;
width: 7em;
height: 5em;
padding: 0;
margin: 0;
border: 0 none;
border-bottom-right-radius: .5em;
-moz-border-radius-bottomright: .5em;
-webkit-border-bottom-right-radius: .5em;
}


}
