css:
@namespace url(http://www.w3.org/1999/xhtml);


@-moz-document domain("ask.fm") {
  
.stream-profile {
    display: block;
    width: 60px;
    height: 60px;
    margin: 0px;
    padding: 0px;
    border: 0px none;
    border-radius: 60px;
}
    
.recommendedHead {
    display: block;
    float: left;
    height: 60px;
    width: 60px;
    border: 0px none;
    padding: 0px;
    margin: 0px 10px 0px 0px;
    opacity: 0.5;
    border-radius: 60px;
}
    
.recommendedHead:hover {
    opacity: 1;
}    

#profile-picture {
    display: block;
    width: 90px;
    height: 90px;
    margin: 0px;
    padding: 0px;
    border: 10px white;
    border-radius: 60px;
    float: left;
    clear: left;   
}  

#menuCenter {
   font-family: Tahoma, "Andale Mono",  Verdana, Arial, Helvetica, sans-serif;
    display: block;
    position: relative;
    width: 716px;
    height: 42px;
    margin: 0px auto;
    padding: 0px 17px 0px 0px;
}    
    
.username {
    display: inline;
    font-weight: normal;
    color: #777;
    visibility: hidden;
}
    
a.link-profile-name {
    color: black;
    font-weight: 100;
    font-size: 20px;
}    
    
#profile-name-container {
    display: block;
    margin: 0px;
    padding: 0px;
    line-height: 18px;
    font-size: 16px;
    font-weight: bold;
    clear: both;
    word-wrap: break-word;
}    
    
#leaderboard {
    display: block;
    position: relative;
    overflow: hidden;
    width: 0;
    height: 0;
    padding: 3px;
    margin: 0px auto 0px;
    background-color: #FFF;
    border: 0px none;
    border-radius: 5px;
    visibility: hidden;
}   
    
#menu {
    width: 100%;
    height: 82px;
    margin-bottom: 92px;
    padding: 0px;
    position: relative;
    background-image: url('http://img11.hostingpics.net/pics/6148249h33y6xm.png');
    background-repeat: repeat-x;
    background-position: left top;
    background-color: #1f3e5d;
}    
    
#logo {
    display: block;
    width: 100%;
    height: 40px;
    margin: 0px;
    padding: 0px 0px 0px 0px;
    border: 0px none;
    background: url('http://ask.fm/images/logo-green.png') no-repeat scroll center top transparent;    
}
    
#wrapper {
    display: block;
    width: 735px;
    height: auto;
    padding: 0px;
    overflow: hidden;
    background-color: #FFF;
    border: 0px none;
    border-radius: 5px;
    margin-top:35px;
}  
    
#flyout-replies-wrapper {
    width: 302px;
    margin: 0px;
    padding: 8px 0px 0px;
    background-image: url('http://ask.fm/images/other/flyout-pipsis.png');
    background-position: left top;
    background-repeat: no-repeat;
    display: block;
    overflow: hidden;
    position: absolute;
    top: 68px;
    left: 63px;
} 
    
#flyout-wrapper {
    width: 302px;
    margin: 0px;
    padding: 8px 0px 0px;
    background-image: url('http://ask.fm/images/other/flyout-pipsis.png');
    background-position: left top;
    background-repeat: no-repeat;
    display: block;
    overflow: hidden;
    position: absolute;
    top: 68px;
    left: -1px;
}    
    
.topFixer {
    width: 100%;
    position: fixed;
    z-index: 8000;
    top: 0px;
    height: 82px;
}    
}
