@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url("about:home") {
    
    html {
        background: #fff;
    }

    body {
        -moz-appearance: none !important;
        background: transparent !important;
    }

    #sync, #snippetContainer, #searchLogoContainer, #searchEngineLogo, #restorePreviousSessionSeparator {
        display: none !important;
    }

    #searchContainer {
        -moz-appearance: none !important;
        margin: 0 auto !important;
        width: 500px !important;
        padding: 30px 100px 120px 100px !important;
        border-radius: 4px !important;
    }

    .launchButton::before {
        display: none !important;
    }
    
    .launchButton {
        background: transparent !important;
        border: none !important;
        box-shadow: none !important;
    }

    #launcher, #restorePreviousSession {
        background: transparent !important;
        border: none !important;
        box-shadow: none !important;
    }

    #launcher {
        width: 40% !important;
        margin: 0 auto auto 0 !important;
        position: fixed !important;
        top: 0 !important;
        right: 0!important;
        display: block !important;
        text-align: center !important;
        white-space: nowrap !important;
    }

    #launcher:not([session]),
    body[narrow] #launcher[session] {
        text-align: initial;
    }

    #launcher button {
        border-radius: 100% !important;
        padding: 0 6px !important;
        font-family: Viner Hand ITC, sans-serif !important;
        font-size: 14px !important;
        color: #ccc !important;
    }

    #launcher button:hover {
        color: #333!important;
        text-decoration: underline;
    }

    #restorePreviousSession {
        background: none !important;
        padding-top: 6px !important;
    }

    #aboutMozilla {
        opacity: .2 !important;
    }

    #aboutMozilla,
    #aboutMozilla::before {
        position: fixed !important;
        bottom: 12px !important;
        top: auto !important;
    }

    #aboutMozilla:hover {
        opacity: 1 !important;
        -moz-transition: all 3s ease-out !important;
    }
}
