css:
@-moz-document domain("forum.blockland.us") {

body {
	/*[[width]]*/
	background-color: /*[[bg]]*/ !important;
	margin:0 auto !important;
	color: #222;
}

body > table > tbody > tr > td {
	background: none !important;
}

body > table > tbody > tr {
	background: none !important;
}

.tborder[cellpadding='0'] {
	margin-top: 10px;
	background-color: #fff;
	border: #444 1px solid;
}

div.smalltext {
	margin-top:16px !important;
}

.catbg {
	background: #546faa none !important;
}
  
input {
	border-width: 1px;
	box-shadow: 1px 1px 0px rgba(0,0,0,0.2);
	padding: 2px;
	outline: none;
}

textarea {
	border: 1px solid #555; 
}

input:focus {
	background-color: #bcccdb;
}

img {
	max-width: 95%;
}

.windowbg {
  box-shadow: inset 1px 1px 1px 0px rgba(255,255,255,0.6);
}

}
