css:
@-moz-document url-prefix("http://linux.org.ru"), url-prefix("https://linux.org.ru"), url-prefix("http://www.linux.org.ru"), url-prefix("https://www.linux.org.ru") {
div#hd
{
	position: fixed;
	top     : 0;
	bottom  : 0;
	width   : 70px;
	z-index : 99991;
  	margin  : 0 !important;
  	padding : 0 !important;
  
  	background-color : #f2f2f2;
}

div#hd::after,
div#hd::before,
div.menu::after,
div.menu::before
{
 	display : none !important; 
}

div.menu
{
  	display : block;
  	width   : 66px;
  	height  : 70px;
  
  	border-left-style : solid;
  	border-left-width : 4px;
  	border-left-color : rgba(0, 0, 0, 0.4);
  
  	margin     : 0    !important;
  	margin-top : 70px !important;
  	padding    : 0    !important;
  	
  	background-color : #ebe9e9;
 	background-image: url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAEIAAABGCAYAAAB4xUL+AAAAd0lEQVR42u3VsQmAMBRF0aeFi2YFwcLaQnCFLKqNTUZIBOGcAT7hJ+EmAAAAAAAA8EdTz2GllCPJmmTpfM47yVVr3UctYu48bxuwhLSZ68gXMfsUYxZxtmfc25Pkcl0gn/Ipn/IpnyCf8imf8imfAAAAAAAA8L0X/voeGfHycY0AAAAASUVORK5CYII=); 
}

div#hd ul
{
	position     : fixed;
	top          : 0;
	bottom       : 0;
	width        : 200px;
	z-index      : 9990;
  
    -webkit-box-shadow: inset 5px 0px 3px -5px rgba(0,0,0,0.1);
    -moz-box-shadow: inset 5px 0px 3px -5px rgba(0,0,0,0.1);
    -o-box-shadow: inset 5px 0px 3px -5px rgba(0,0,0,0.1);
    box-shadow: inset 5px 0px 3px -5px rgba(0,0,0,0.1);
  	
  	margin       : 0 !important;  	
  	margin-left  : 66px !important;
  	padding      : 0 !important;  	
  	padding-top  : 50px !important;
  
  	background-color : #ebe9e9;
  
   	background-image: url(data:image/png;base64,), url(data:image/png;base64,);  
  background-repeat : no-repeat, no-repeat;
  background-position : 0 0, 0 322px;
}

div.menu ul li:nth-child(5)
{
 	position     : absolute;
  	margin-top   : 330px !important;  	
}

div#loginGreating
{
  	display : block;
  	width   : 70px;
  	height  : 70px;
  
  	margin  : 0 !important;
    padding : 0 !important;
  	margin-top : 70px !important;
  
  	background-image: url(data:image/png;base64,);
}

div#loginGreating:hover
{  
  	background-color : #ebe9e9;
}

div#loginGreating:before
{
 	display : none;
  	position : absolute;	
  	z-index      : 9995;
  	top : 0;
  	bottom : 0;
  	width : 200px !important;
  
  	margin-left : 70px;
  
	content : " ";
  
  
    -webkit-box-shadow: inset 5px 0px 3px -5px rgba(0,0,0,0.1);
    -moz-box-shadow: inset 5px 0px 3px -5px rgba(0,0,0,0.1);
    -o-box-shadow: inset 5px 0px 3px -5px rgba(0,0,0,0.1);
    box-shadow: inset 5px 0px 3px -5px rgba(0,0,0,0.1);
  	
  	background-color : #ebe9e9;
}

div#loginGreating:hover:before
{
 	display : block; 
}

div#loginGreating:hover a[href^="/people/"],
div#loginGreating:hover div#regmenu,
div#loginGreating:hover form#regform label,
div#loginGreating:hover form#regform input
{
	display : block !important;
}

div#loginGreating a[href^="/people/"]
{
  	display : none;
  	position : absolute;
  	left : 95px;
  	top : 20px;
  	width : 150px;
  	text-align : left !important;
  
  
	color: #404040 !important;
	font-size: 20px !important;
	font-weight: bold;
	text-decoration: none !important;
	font-style: normal;
	vertical-align: baseline;
	outline: 0;
  	overflow : hidden;
  	text-overflow : clip;
  	font-family:"Courier New", sans-serif;
  
  	z-index : 9999;
}

form#regform
{
 	display : none;
  	position : absolute;
  
  	left : 70px;
  	top  : 100px;
  
  	z-index : 9999;
}

form#regform label,
form#regform input
{
 	display : none; 
}

form#regform label
{
  	font-size: 14px;
	font-weight: bold;
	padding: 10px 25px;
	text-decoration: none !important;
	position: relative; 
  	font-style: normal;
	vertical-align: baseline;
	outline: 0;
  	text-align : left !important;
  	font-family: "PT Sans",sans-serif;
}

form#regform label input[type="text"]
{
 	background-color : #ebe9e9 !important;
}

form#regform input[type="submit"],
form#regform input[type="button"]
{
 	width : 138px;
  	margin-left : 28px;
}

div#regmenu
{
 	display : none !important; 
  	font-size : 0;
}

div#loginGreating a[href="https://www.linux.org.ru/register.jsp"],
div#loginGreating a[href="https://www.linux.org.ru/login.jsp"]
{
  	display : block;
  	position : absolute !important;
  	left : 70px;
  	top : 20px;
  	width : 150px;
  	text-align : left !important;
  
  
	display: block;
	color: #8c8c8b !important;
	font-size: 14px;
	font-weight: bold;
	padding: 10px 25px;
	text-decoration: none !important;
	position: relative; 
  	font-style: normal;
	vertical-align: baseline;
	outline: 0;
  	font-family: "PT Sans",sans-serif;
  
  	z-index : 9999;
}

div#loginGreating a[href="https://www.linux.org.ru/login.jsp"]
{
  	top : 60px !important;
}

div#loginGreating a[href="https://www.linux.org.ru/register.jsp"]:hover,
div#loginGreating a[href="https://www.linux.org.ru/login.jsp"]:hover
{
  	text-decoration: none !important;
	background: #d8d7d7 !important;
	color: #1f2336 !important;
}

div.menu ul li
{
  	display : block;
  	width   : 200px;
  	height  : 36px;
}

div.menu ul li a:hover
{  
  	text-decoration: none !important;
	background: #d8d7d7 !important;
	color: #1f2336 !important;
}

div.menu ul li a
{
 	display: block;
	color: #8c8c8b !important;
	font-size: 14px;
	font-weight: bold;
	padding: 10px 25px;
	text-decoration: none !important;
	position: relative; 
  	font-style: normal;
	vertical-align: baseline;
	outline: 0;
  	font-family: "PT Sans",sans-serif;
}

div.menu ul
{
 	display : none; 
}

div.menu:hover ul
{
  	display : block;
}	

a#sitetitle
{
  	display : block;
    width   : 70px;
  	height  : 70px;
  	padding : 0 !important;
    margin  : 0 !important;
  
  	font-size : 0;
  
   	background-color : #729fcf; /*rgba(200, 200, 255, 1);*/
  
  	background-image: url(data:image/png;base64,);
}


    
div#bd,
#ft
{
	padding-left : 80px;
}

div#bd
{
 	padding-top : 10px; 
}
}
