css:
@charset "utf-8";
/*Autor: skorki */
@namespace url(http://www.w3.org/1999/xhtml); 
@-moz-document domain("youtube.com"),domain("www.youtube.com") {
/*strona główna*/
.video-masthead,
#premium-yva,
#header #ad_creative_1,
#alerts {
display: none !important;
}
#yt-masthead-container, #masthead-container, #ticker {
background-color: rgba(0,0,0,0.1) !important;
box-shadow: inset 0 50px rgba(42,255,0,0.2),
			inset 0 -15px 30px rgba(42,255,0,0.4),
			0 5px 10px rgba(42,255,0,0.5) !important;
z-index: 9999 !important;
background: -moz-linear-gradient(top, rgba(67,67,67,0) 0%, rgba(67,67,67,0.8) 31%, rgba(67,67,67,1) 36%, rgba(67,67,67,1) 37%, rgba(95,95,95,1) 50%, rgba(67,67,67,1) 65%, rgba(67,67,67,1) 68%, rgba(67,67,67,0.8) 74%, rgba(67,67,67,0) 100%)!important; /* FF3.6+ */
background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,rgba(67,67,67,0)), color-stop(31%,rgba(67,67,67,0.8)), color-stop(36%,rgba(67,67,67,1)), color-stop(37%,rgba(67,67,67,1)), color-stop(50%,rgba(95,95,95,1)), color-stop(65%,rgba(67,67,67,1)), color-stop(68%,rgba(67,67,67,1)), color-stop(74%,rgba(67,67,67,0.8)), color-stop(100%,rgba(67,67,67,0)))!important; /* Chrome,Safari4+ */
background: -webkit-linear-gradient(top, rgba(67,67,67,0) 0%,rgba(67,67,67,0.8) 31%,rgba(67,67,67,1) 36%,rgba(67,67,67,1) 37%,rgba(95,95,95,1) 50%,rgba(67,67,67,1) 65%,rgba(67,67,67,1) 68%,rgba(67,67,67,0.8) 74%,rgba(67,67,67,0) 100%)!important; /* Chrome10+,Safari5.1+ */
background: -o-linear-gradient(top, rgba(67,67,67,0) 0%,rgba(67,67,67,0.8) 31%,rgba(67,67,67,1) 36%,rgba(67,67,67,1) 37%,rgba(95,95,95,1) 50%,rgba(67,67,67,1) 65%,rgba(67,67,67,1) 68%,rgba(67,67,67,0.8) 74%,rgba(67,67,67,0) 100%)!important; /* Opera 11.10+ */
background: -ms-linear-gradient(top, rgba(67,67,67,0) 0%,rgba(67,67,67,0.8) 31%,rgba(67,67,67,1) 36%,rgba(67,67,67,1) 37%,rgba(95,95,95,1) 50%,rgba(67,67,67,1) 65%,rgba(67,67,67,1) 68%,rgba(67,67,67,0.8) 74%,rgba(67,67,67,0) 100%)!important; /* IE10+ */
background: linear-gradient(to bottom, rgba(67,67,67,0) 0%,rgba(67,67,67,0.8) 31%,rgba(67,67,67,1) 36%,rgba(67,67,67,1) 37%,rgba(95,95,95,1) 50%,rgba(67,67,67,1) 65%,rgba(67,67,67,1) 68%,rgba(67,67,67,0.8) 74%,rgba(67,67,67,0) 100%)!important; /* W3C */
}
.yt-ui-ellipsis { 
background-color: transparent !important;
}
.yt-lockup-deemphasized-text, .yt-lockup a.yt-lockup-deemphasized-text { 
color: #D9D9D9 !important;
}
.guide-flyout, .browse-list-item-container.feed-item-container.branded-page-box, .compact-shelf.branded-page-box,
.channel-header .primary-header-contents, .branded-page-v2-subnav-container, #video-page-content, #playlist-page-content, #channel-page-content,
#channels-browse-content-grid.grid-lockups-container, .branded-page-v2-body, .site-center-aligned .yt-card, 
#footer .yt-picker, .DJa, .yt-uix-expander-ellipsis, #watch7-sidebar, .watch-wide #watch7-sidebar, .watch-wide #watch7-preview {
-webkit-border-radius: 10px !important;
-moz-border-radius: 10px !important;
border-radius: 10px !important;
}

/*borders*/
div, td {
border: none !important;
}

/*background*/
body, #page, #body-container, .footer-container , body #footer-container, #content-container, #body-containe { 
background: #000000 !important;
}
 .guide-flyout,
 .channel-header .primary-header-contents, .branded-page-v2-subnav-container, #video-page-content, #playlist-page-content, #channel-page-content,
 #channels-browse-content-grid.grid-lockups-container, .branded-page-v2-body, .site-center-aligned .yt-card, #footer .yt-picker, .yt-uix-expander-ellipsis { 
box-shadow: inset 0 -15px 30px rgba(42,255,0,0.4),
			0 5px 10px rgba(42,255,0,0.5) !important;
background-color: rgba(0,0,0,0.2) !important;
border: none !important;
}
.browse-list-item-container.feed-item-container.branded-page-box, .compact-shelf.branded-page-box {
background-color: rgba(42,255,0,0.1) !important;
box-shadow: inset 0 -15px 30px rgba(42,255,0,0.4),
			0 5px 10px rgba(42,255,0,0.5) !important;
border: none !important;
}
#guide-container, .feed-item-container  {
box-shadow: inset 0 -15px 30px rgba(42,255,0,0.6),
			0 5px 10px rgba(42,255,0,0.8) !important;
background-color: rgba(42,255,0,0.1) !important;
border: none !important;
-webkit-border-bottom-right-radius: 15px !important;
-webkit-border-bottom-left-radius: 15px !important;
-moz-border-radius-bottomright: 15px !important;
-moz-border-radius-bottomleft: 15px !important;
border-bottom-right-radius: 15px !important;
border-bottom-left-radius: 15px !important;
}

/*progress*/
#progress { 
background: rgba(42,255,0,0.3) !important;
box-shadow: inset 0 50px rgba(255,255,255,0.2),
			inset 0 -15px 30px rgba(0,0,0,0.4),
			0 5px 10px rgba(0,0,0,0.5) !important;
padding-top: 48px !important;
}
#progress dd, #progress dt {
display: none !important;
}

/*małe przyciski*/
.shelves-play , .yt-uix-button-default, button,
.compact-shelf .yt-uix-button-shelf-slider-pager, .yt-uix-button-dark, #appbar-guide-button, #upload-btn, 
.yt-uix-button-primary, a.yt-uix-button.yt-uix-button-epic-nav-item, #masthead-utility a.yt-uix-button, .guide-item.guide-item-selected { 
border: none !important;
box-shadow: inset 0 50px rgba(42,255,0,0.2),
			inset 0 -15px 30px rgba(42,255,0,0.4),
			0 5px 10px rgba(42,255,0,0.5) !important;
background-color: rgba(0,0,0,0.1) !important;
text-decoration: none !important;
color: #ffffff !important;
}
.guide-item.guide-item-selected:hover, .shelves-play:hover , .yt-uix-button-default:hover, button:hover, .compact-shelf .yt-uix-button-shelf-slider-pager:hover, #appbar-guide-button:hover, #upload-btn:hover, 
 .yt-uix-button-primary:hover, a.yt-uix-button.yt-uix-button-epic-nav-item:hover, #masthead-utility a.yt-uix-button:hover,
a.yt-uix-button-epic-nav-item.partially-selected:hover, button.yt-uix-button-epic-nav-item:hover, .epic-nav-item:hover, .epic-nav-item.selected, .epic-nav-item.yt-uix-button-toggled, .epic-nav-item-heading { 
background: rgba(42,255,0,0.3) !important; 
box-shadow: none !important;
color: #ffffff !important;
border: none !important;
text-decoration: underline !important;
}
button:selected, button:togled{
background: rgba(42,255,0,0.3) !important; 
box-shadow: none !important;
color: #ffffff !important;
font-weight: 900 !important;
border: none !important;
}
#yt-masthead #search-btn, #masthead #search-btn {
border: none !important;
background: none !important;
box-shadow: none !important;
opacity: 0.5 !important;
}
#yt-masthead #search-btn:hover, #masthead #search-btn:hover {
border: none !important;
background: none !important;
box-shadow: none !important;
opacity: 1 !important;
}

/*kategorie*/
#masthead-appbar, #appbar-nav {
background-color: rgba(0,0,0,0.1) !important;
box-shadow: inset 0 50px rgba(42,255,0,0.2),
			inset 0 -15px 30px rgba(42,255,0,0.4),
			0 5px 10px rgba(42,255,0,0.5) !important;
}

/*menu przycisków*/
.yt-uix-button-menu  {
background: rgba(0,0,0,0.4) !important;
box-shadow: inset 0 -15px 30px rgba(42,255,0,0.4),
			0 5px 10px rgba(42,255,0,0.5) !important;
border: none !important;
}
.yt-uix-button-menu .yt-uix-button-menu-item:hover {
background: rgba(0,0,0,0.6) !important;
}


/*kategorie*/
.branded-page-related-channels-item .yt-lockup .yt-lockup-meta, .yt-lockup-mini .yt-lockup-title  {
background: transparent !important;
margin-top: 10px !important;
}
.shelf-description { 
color: #FFFFFF !important;
background: transparent !important;
}

/*profil użytkownika*/
.feed-item-container.legacy-style .feed-item-main {
border: none !important;
}

/*lewe menu*/
.guide-item:hover {
border: none !important;
background: rgba(42,255,0,0.4) !important; 
box-shadow: none !important;
text-decoration: none !important;
}
#appbar-guide-menu {
background: #000000 !important;
border: none !important;
}

/*czcionki*/
.lohp-video-link { 
color: #FCFCFC !important;
}
.lohp-video-metadata { 
color: #EEEEEE !important;
}
.lohp-video-metadata .view-count { 
color: #FFFFFF !important;
font-weight : bold !important;
}
#footer-links-primary a:hover, #footer-links-secondary a:hover , .footer-secondary a:hover{
color: #4d4d4d !important;
text-shadow: none !important;
}
.footer-secondary .footer-secondary-info a {
text-shadow: none !important;
}
a.yt-uix-button-epic-nav-item.selected, a.yt-uix-button-epic-nav-item.yt-uix-button-toggled, button.yt-uix-button-epic-nav-item.selected, button.yt-uix-button-epic-nav-item.yt-uix-button-toggled,
.epic-nav-item.selected, .epic-nav-item.yt-uix-button-toggled, .epic-nav-item-heading {
color: #FFFFFF !important;
font-weight : 700 !important;
}
.channel-header .branded-page-header-title .branded-page-header-title-link { 
font-size: 23px !important;
}
.branded-page-module-title {
font-size: 18px !important;
}
#watch7-headline .yt-uix-expander-head, .watch-view-count, .video-extras-likes-dislikes, 
.video-extras-likes-dislikes .dislikes , .video-extras-likes-dislikes, .video-extras-likes-dislikes .dislikes, 
a, span, div, h3, h4, p, li, ol.yt, ul.yt, .yt-text ol, .yt-text ul, .branded-page-module-title {
color: #FFFFFF !important;
text-shadow: 0px 0px 20px #00ff00 !important;
}
a:hover, .title:hover, #watch7-headline .yt-uix-expander-head:hover {
color: #e3e3e3 !important;
}

/*tooltips*/
.yt-uix-range-tooltip-tip-content, .yt-uix-tooltip-tip-content {
background: rgba(0,0,0,0.3) !important;
box-shadow: inset 0 -15px 30px rgba(42,255,0,0.4),
			0 5px 10px rgba(42,255,0,0.5) !important;
border: none !important;
}

/*subskrybuj*/
.yt-uix-button-subscribe-unbranded,  .yt-uix-button-subscribe-branded, .yt-uix-button-subscribe-unbranded[disabled] { 
border: none !important;
box-shadow: inset 0 50px rgba(42,255,0,0.2),
			inset 0 -15px 30px rgba(42,255,0,0.4),
			0 5px 10px rgba(42,255,0,0.5) !important;
background-color: rgba(0,0,0,0.1) !important;
text-decoration: none !important;
background: none !important;
}
.yt-uix-button-subscribe-unbranded[disabled]:active, .yt-uix-button-subscribe-unbranded[disabled]:focus {
background: rgba(42,255,0,0.2) !important;
}
.yt-uix-button-subscribe-unbranded:hover,  .yt-uix-button-subscribe-branded:hover { 
background: rgba(42,255,0,0.2) !important; 
box-shadow: none !important;
}
.yt-subscription-button-subscriber-count-branded-horizontal, .yt-subscription-button-subscriber-count-branded-vertical, .yt-subscription-button-subscriber-count-unbranded-horizontal, .yt-subscription-button-subscriber-count-unbranded-vertical {
color: #000000 !important;
}

/*licznik subskrybcji*/
.yt-subscription-button-subscriber-count-branded-horizontal, .yt-subscription-button-subscriber-count-unbranded-horizontal {
background: rgba(0,0,0,0.3) !important;
box-shadow: inset 0 -15px 30px rgba(42,255,0,0.4),
			0 5px 10px rgba(42,255,0,0.5) !important;
border: none !important;
color: #ffffff !important;
}

/*wideo*/
 #watch7-user-header {
background: transparent  !important;
box-shadow: inset 0 2px rgba(42,255,0,0.2),
			inset 0 -15px 30px rgba(42,255,0,0.4),
			0 5px 10px rgba(0,0,0,0.5) !important;
border-right: none !important;
}
#watch7-main {
background: transparent !important;
box-shadow: inset 0 2px rgba(42,255,0,0.2),
			inset 0 -15px 30px rgba(42,255,0,0.4),
			0 5px 10px rgba(0,0,0,0.5) !important;
background-color: rgba(42,255,0,0.1) !important;
border-right: none !important;
}
#watch-discussion {
background: transparent !important;
box-shadow: inset 0 2px rgba(42,255,0,0.2),
			inset 0 -15px 30px rgba(42,255,0,0.4) !important;
background-color: rgba(0,0,0,0.1) !important;
border: none !important;
}
#watch7-user-header, #watch7-views-info {
padding-top: 15px !important;
}
#watch7-sidebar {
background: transparent !important;
box-shadow: inset 0 2px rgba(42,255,0,0.2),
			inset 0 -15px 30px rgba(42,255,0,0.4),
			0 5px 10px rgba(0,0,0,0.5) !important;
}
#watch7-sidebar-contents {
background: transparent !important;
box-shadow: inset 0 2px rgba(42,255,0,0.2),
			inset 0 -15px 30px rgba(42,255,0,0.4),
			0 5px 10px rgba(42,255,0,0.5) !important;
margin-top: 0 !important;
padding-top: 15px !important;
padding-bottom: 15px !important;
}
#watch7-content  {
background: transparent !important;
}
#watch-discussion {
background: transparent !important;
}
.watch7-action-panel-footer { 
background-color: transparent !important;
border-right: none !important;
}
#watch7-headline  {
background-color: transparent !important;
border: none !important;
height: 23px !important;
}
.video-extras-sparkbar-likes { 
background: #13c900 !important;
}
#action-panel-details .action-panel-content{
padding: 10px 18px !important;
background-color: transparent!important;
border: none !important;
}
#watch7-action-buttons {
padding: 10px 18px !important;
background-color: transparent !important;
border: none !important;
}
#watch7-action-panels, #watch7-action-panel-footer .yt-horizontal-rule  {
background: transparent !important;
border-right: none !important;
}
#watch7-action-panel-footer {
display: none !important;
}

/*wideo paski, cienie*/
.not-watch8 #watch7-user-header,
.not-watch8 #watch7-headline,
.not-watch8 #watch7-notification-area,
.not-watch8 #watch7-action-buttons,
.not-watch8 #action-panel-details.action-panel-content {
box-shadow: inset 0 -15px 30px rgba(42,255,0,0.4),
			0 5px 10px rgba(42,255,0,0.5) !important;
}

/*ExtendTube*/
.ext-lyrics .yt-uix-expander-head, .ext-actions-container, .yt-uix-button-panel {
background: transparent !important;
border: none !important;
}

/*playlista*/
#watch-appbar-playlist .playlist-videos-list{
box-shadow: inset 0 -15px 30px rgba(42,255,0,0.6),
			0 5px 10px rgba(42,255,0,0.8) !important;
background-color: rgba(0,0,0,0.2) !important;
}
#watch-appbar-playlist .playlist-videos-list > li:hover, #watch-appbar-playlist .playlist-videos-list > li.currently-playing {
box-shadow: inset 0 -15px 30px rgba(42,255,0,0.6),
			0 5px 10px rgba(42,255,0,0.8) !important;
background-color: rgba(0,0,0,0.2) !important;
}
#watch-appbar-playlist { 
box-shadow: inset 0 -15px 30px rgba(42,255,0,0.6),
			0 5px 10px rgba(42,255,0,0.8) !important;
background-color: rgba(0,0,0,0.2) !important;
}
#watch-appbar-playlist .playlist-video h4 { 
color: #ffffff  !important;
}
#watch-appbar-playlist .playlist-videos-list > li:hover .yt-ui-ellipsis, #watch-appbar-playlist .playlist-videos-list .yt-ui-ellipsis ,
#watch-appbar-playlist .playlist-videos-list > li.currently-playing .yt-ui-ellipsis  {
background: transparent !important;
}

/*playlist top*/
.pl-video, .pl-video .pl-video-handle {
border: none !important;
}

/*wyszukiwarka i input*/
#masthead-search-terms, #channel-search.expanded .search-field, #masthead-search-term, .yt-uix-form-input-fluid { 
border-radius: 15px !important;
background-color: rgba(0,0,0,0.2) !important;
}
.gssb_m, .gssb_i {
background-color: rgba(67,67,67,0.7) !important;
box-shadow: inset 0 -15px 30px rgba(42,255,0,0.6),
			0 5px 10px rgba(42,255,0,0.8) !important;
}
.yt-uix-form-input-fluid .yt-uix-form-input-text, .yt-uix-form-input-fluid .yt-uix-form-input-textarea {
background-color: rgba(67,67,67,0.7) !important;
box-shadow: inset 0 -15px 30px rgba(42,255,0,0.6),
			0 5px 10px rgba(42,255,0,0.8) !important;
}
#masthead-search {
max-width: 900px !important;
}
#masthead-search-terms input,
.yt-uix-form-input-select, .yt-uix-form-input-text, .yt-uix-form-input-textarea { 
color: #FFFFFF !important;
}
.gssb_e, .gssb_i td, .gssb_a, .gssb_a td { 
background-color: rgba(67,67,67,0.1) !important;
}


/*developers, about, informations, advertise etc.*/
#yt-microsite .yt-card-light, .yt-card-light .yt-card-title a, #yt-microsite-features .yt-microsite-spotlight, .yt-advertise-whyitworks-header,
.yt-advertise-cta-container, .yt-tile-static, .yt-tile-visible, .yt-tile-default:hover, #yt-sidebar li.selected a, .html5-compatibility-table li  {
background-color: rgba(0,0,0,0.1) !important;
box-shadow: inset 0 50px rgba(42,255,0,0.2),
			inset 0 -15px 30px rgba(42,255,0,0.4),
			0 5px 10px rgba(42,255,0,0.5) !important;
border: none !important;
}
.yt-nav-dark, #yt-main-nav-container { 
background-color: rgba(0,0,0,0.1) !important;
box-shadow: inset 0 50px rgba(42,255,0,0.2),
			inset 0 -15px 30px rgba(42,255,0,0.4),
			0 5px 10px rgba(42,255,0,0.5) !important; 
}
#yt-main-nav a:active, #yt-main-nav a.active { 
background-color: rgba(0,0,0,0.1) !important;
box-shadow: inset 0 50px rgba(42,255,0,0.2),
			inset 0 -15px 30px rgba(42,255,0,0.4),
			0 5px 10px rgba(42,255,0,0.5) !important;
}
#yt-main-nav a:hover, #yt-sidebar li a:hover  { 
background-color: rgba(0,0,0,0.1) !important;
box-shadow: inset 0 50px rgba(42,255,0,0.2),
			inset 0 -15px 30px rgba(42,255,0,0.4),
			0 5px 10px rgba(42,255,0,0.5) !important;
color: #ffffff !important;
}

/*scrollbars*/
.yt-scrollbar-dark ::-webkit-scrollbar-thumb  {
background: #12c400 !important;

}
.yt-scrollbar-dark ::-webkit-scrollbar-track {
background: #0c7b00 !important;
box-shadow: inset 0 50px rgba(42,255,0,0.2),
			inset 0 -15px 30px rgba(42,255,0,0.4),
			0 5px 10px rgba(42,255,0,0.5) !important;
}

/*share*/
.watch7-hovercard,.yt-uix-form-input-select, .yt-uix-form-input-text, .yt-uix-form-input-textarea, .yt-ui-menu-content  {
background: #676767 !important;
box-shadow: inset 0 -15px 30px rgba(42,255,0,0.4),
			0 5px 10px rgba(42,255,0,0.5) !important;
border: none !important;
}

/*komentarze full*/
#watch-response {
box-shadow: inset 0 -15px 30px rgba(42,255,0,0.4),
			0 5px 10px rgba(42,255,0,0.5) !important;
background-color: rgba(42,255,0,0.1) !important;
border: none !important;
-webkit-border-radius: 10px !important;
-moz-border-radius: 10px !important;
border-radius: 10px !important;
}
#watch-response-header {
border: none !important;
-webkit-border-radius: 10px !important;
-moz-border-radius: 10px !important;
border-radius: 10px !important;
}
#watch-response-content {
border: none !important;
}
/*przegladaj kanały*/
.yt-gb-shelf-main-content {
box-shadow: inset 0 -15px 30px rgba(42,255,0,0.4),
			0 5px 10px rgba(42,255,0,0.5) !important;
}
/*progress bars*/
.ytp-load-progress  {
background: #0d8a00 !important; 
}
.ytp-play-progress {
box-shadow: inset 0 50px rgba(42,255,0,0.2),
			inset 0 -15px 30px rgba(42,255,0,0.4),
			0 5px 10px rgba(42,255,0,0.5) !important;
background-color: rgba(0,0,0,0.1) !important;
}

/*html5 player*/
.light-theme .html5-player-chrome, .html5-player-chrome {
background-color: rgba(0,0,0,0.1) !important;
box-shadow: inset 0 50px rgba(42,255,0,0.2),
