css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("reddit.com") {

body{
margin:0 auto !important;
max-width:1600px !important;
}
div.content{
border-left:1px solid #cccccc !important;
border-right:1px solid #cccccc !important;
margin:0 !important;
padding:10px 20px 0 10px !important;
}
div.content a,.morelink a:hover,.sr-bar a,.tabmenu .selected a,.titlebox a,.md a,.shortlink a{
color:#336699 !important;
}
div.content a.admin{
color:#ff0000 !important;
}
div.entry{
border-bottom:1px dotted #cccccc !important;
padding:0 0 5px !important;
}
div.md{
max-width:1400px !important;
}
div.md a:visited{
color:#713192 !important;
}
div.md blockquote{
border-left:6px solid #cccccc !important;
margin:0 0 5px 20px !important;
padding:0 0 0 10px !important;
}
div.md blockquote{
color:#555555 !important;
font-family:Verdana,sans-serif !important;
font-size:12px !important;
}
div.md li{
font:13px/1.2 Georgia,serif !important;
margin:2px 0 6px !important;
}
div.md p{
font-family:Georgia,serif !important;
font-size:13px !important;
line-height:1.6 !important;
margin:5px 0 10px 0 !important;
}
input{
font-family:Verdana,sans-serif !important;
font-size:12px !important;
line-height:1.6 !important;
}
input[type="text"]{
width:98% !important;
}
p.title a{
color:#336699 !important;
font-family:Georgia,serif !important;
font-size:15px !important;
}
p.title a:visited,ul.flat-list li.first a:visited{
color:#653399 !important;
}
textarea{
background:#eeeeee !important;
border:1px solid #cccccc !important;
font-family:Verdana,sans-serif !important;
font-size:12px !important;
line-height:1.6 !important;
padding:3px !important;
width:98% !important;
}
ul.flat-list a{
font-size:11px !important;
font-weight:normal !important;
}
.bottommenu,.gadget,.footer *,.commentarea h1,.panestack-title,.nub,#ad-frame{
display:none !important;
}
.child .usertext-edit textarea{
height:260px !important;
}
.commentarea .md h1{
display:block !important;
}
.fancy-toggle-button a{
color:#ffffff !important;
}
.formtabs-content{
width:1220px !important;
}
.friend{
background:#dff4d5;
}
.infobar{
background-color:#eeeeee !important;
border:1px solid #cccccc !important;
color:#666666 !important;
font-size:11px !important;
}
.lightdrop,.tagline{
font-size:11px !important;
}
.link.last-clicked{
border:none !important;
}
.menuarea{
border-bottom:1px solid #cccccc !important;
padding:0 0 10px !important;
}
.morelink{
background:none !important;
border:none !important;
text-align:left !important;
}
.pagename{
font-variant:normal !important;
}
.pagename a{
color:#336699 !important;
text-shadow:0 0 .2em #ffffff,0 0 .2em #ffffff,0 0 .2em #ffffff !important;
}
.pagename a:hover{
text-decoration:none !important;
text-shadow:0 0 .6em #ffffff,0 0 .6em #ffffff,0 0 .6em #ffffff !important;
}
.roundfield{
width:1200px !important;
}
.side{
background:none !important;
padding:0 !important;
}
.sr-bar .selected a{
font-weight:bold !important;
}
.submitter{
background-color:#ffffcc !important;
}
.thing.spam{
background-color:#ffdddd !important;
}
.title{
font-size:12px !important;
}
.titlebox,.titlebox h1,.titlebox .karma{
color:#666666 !important;
font-size:12px !important;
}
.usertext-edit{
width:auto !important;
}
.usertext-edit textarea{
height:80px !important;
}
#noresults,.downvotes,.linkinfo,.linkinfo *{
color:#666666 !important;
}
#title-field textarea{
height:60px !important;
}

}
