css:
@-moz-document url-prefix("http://user.qzone.qq.com/"){
  
body.os-win7.bg-body div#layBackground>div.layout-background div#pageContent>div#leftMenu>div.mod-side-nav-recently-used,
body.os-win7.bg-body div#layBackground div.layout-head,
body.os-win7.bg-body div#pageContent div.col-main-sidebar,
div#idQbossHotbar{display:none!important}

body.os-win7.bg-body div.top-fix-wrap{width:90%!important}

body#QZ_Body#layBackground div#headContainer.lay_headContainer{height:50px!important}

body.os-win7.bg-body>div#layBackground>div.layout-nav
{height:0!important}
body.os-win7.bg-body div#layBackground div.layout-nav>div.layout-nav-inner
{height:150px!important;width:90%!important;padding:10px 0!important;position:fixed!important}
body.os-win7.bg-body div#layBackground div.layout-nav>div.layout-nav-inner>div.head-avatar
{left:72px!important}

body.os-win7.bg-body div#layBackground>div.layout-background div#pageContent
{width:90%!important}

body.os-win7.bg-body div#layBackground>div.layout-background div#pageContent>div#leftMenu{padding-top:420px!important;float:left!important;position:fixed!important;width:144px!important}

body.os-win7.bg-body div#layBackground>div.layout-background div#pageContent>div#main_feed_container
{width:inherit!important}
body.os-win7.bg-body div#layBackground>div.layout-background div#pageContent>div#main_feed_container>div.col-main-feed
{padding-left:10px!important;
width:inherit!important;}

body div#headContainer.lay_headContainer a#view_like_list
{border:0 rgba(0,0,0,0)!important;color:darkgray!important}

body.os-win7.bg-body>div.fix-layout{right:40px!important}

.col-main-feed{
width:905px !important;
margin-left:20px !important;
border: 1.5px solid #F0F0F0 !important;
padding:13px !important;
border-radius:10px;
box-shadow: 0px 3px 3px #333;
}

/*屏蔽某人可以重复复制下一个，但是中间必须添加半角逗号,*/
    [id*="1126647597"]{display:None !important;}  
  
/*头像圆角*/
.head-avatar,
.card-user-pic,
.avatar_box,
a[href="javascript:;"][onclick="popupEditor();return false;"]{
    background:none !important;
    border-color:transparent !important;
    border:0 !important;
    box-shadow:none !important;
}
#QM_OwnerInfo_Icon,
#mod-comments a img,
.card-user-pic a img,
#homepage_visitor_ul a img,
#QM_3_Body img,
#user_icon{
    border-radius:800px;
    transform:scale(0.9) rotate(0deg);
    transition:transform 0.25s linear;
    -webkit-transition:transform 0.25s linear;
}

a.user_head_50{
    display: block !important;
    overflow: hidden !important;
    border-radius: 18px !important;
    box-shadow: 0px 0px 1px rgba(0, 0, 0, 0.3)!important;
}
div.user-info .user-avatar{
    width: 24px !important;
    height: 24px !important;
    display: block !important;
    overflow: hidden !important;
    border-radius: 12px !important;
    box-shadow: 0px 0px 1px rgba(0, 0, 0, 0.3)!important;
}
div.qz_dialog_layer.j-comment-loaded div.ui_avatar,
#layBackground #event_content div.ui_avatar,
div.mod_wrap.bg div.ui_avatar,
#js-cmt-wrap > div > div.figure-interactive div.ui_avatar,
#ifeedsContainer div.ui-avatar,
#feed_friend div.ui-avatar,
#feed_me div.ui-avatar,
#care_friend_container div.ui-avatar,
a.ui_avatar.q_namecard,
.age_option .avatar,
.add_preview,
.add_with_wrap,
.bor3.panel.panel_tab.photo_panel_recentvisitor.js_unbind_hideinpy .ui_avatar,
div.user-list img.q_namecard,
div.friends-results-topbar img[src^="http://qlogo"],
li.skan-item a img,
[id^=period_list_] img.avatar,
div.comments-item-bd div.ui-avatar,
div.comments_list div.ui_avatar{
    width: 30px !important;
    height: 30px !important;
    display: block !important;
    overflow: hidden !important;
    border-radius: 15px !important;
    box-shadow: 0px 0px 1px rgba(0, 0, 0, 0.3)!important;
}
div.mod_wrap.bg.mod-wrap div.ui_avatar,
#commentListContainer div.ui_avatar,
p.mod_comment_avatar,
#div_all > div.sidebar #user_head_img,
#friendSongList p.cover{
    width: 35px !important;
    height: 35px !important;
    display: block !important;
    overflow: hidden !important;
    border-radius: 17.5px !important;
    box-shadow: 0px 0px 1px rgba(0, 0, 0, 0.3)!important;
}
a.f-user-avatar.q_namecard.f-s-a,
a.ui_avatar.q_namecard,
a.user_head_50.q_namecard,
ul#my_care_list a.user-avatar,
ul#feed_me_list div.f-user-pto,
li.img-txt-item div.img-wrap,
li a.ui_avatar,
a.img_side,
#user_head,
#ulCommentList p.avatar,
div.item_avatar,
ul#othervisitor_list img[src^="http://qlogo"],
ul#othervisitor_list img[src^="http://cnc.qzonestyle.gtimg.cn"],
#fr-mod-maykonw a.avatar.q_namecard,
#id_favorite_list_container > li > div.f-aside > a,
#msgList div.avatar,
ul.feed_sdc_list div.qz_feed_avatar.f_user_head a{
    width: 50px !important;
    height: 50px !important;
    display: block !important;
    overflow: hidden !important;
    border-radius: 25px !important;
    box-shadow: 0px 0px 1px rgba(0, 0, 0, 0.3)!important;
}
.avatar_box img#avatar{
    display: block !important;
    overflow: hidden !important;
    border-radius: 50px !important;
    box-shadow: 0px 0px 1px rgba(0, 0, 0, 0.3)!important;
}
div#head1 img.user-avatar,
div#head2 img.user-avatar{
    display: block !important;
    overflow: hidden !important;
    border-radius: 63px !important;
    box-shadow: 0px 0px 1px rgba(0, 0, 0, 0.3)!important;
}
ul#special_promote_ul div.user-avatar,
div.mod-visitor li.user-item img{
    width: 68px !important;
    height: 68px !important;
    display: block !important;
    overflow: hidden !important;
    border-radius: 34px !important;
    box-shadow: 0px 0px 1px rgba(0, 0, 0, 0.3)!important;
}
div.main-area img[src^="http://qlogo"]{
    width: 70px !important;
    height: 70px !important;
    display: block !important;
    overflow: hidden !important;
    border-radius: 35px !important;
    box-shadow: 0px 0px 1px rgba(0, 0, 0, 0.3)!important;
}
div.sidebar.lite_hide .avatar,
div.fr-mod-request a.avatar.q_namecard{
    width: 80px !important;
    height: 80px !important;
    display: block !important;
    overflow: hidden !important;
    border-radius: 40px !important;
    box-shadow: 0px 0px 1px rgba(0, 0, 0, 0.3)!important;
}
#app_mod > div.info_side.bg3.bor3 > div.portrait_container #user_icon,
a.avatar.q_namecard,
p.avatar,
div.col-main-friends div.img-wrap,
div.user_info a.avatar.q_namecard,
div.page_main div.user_area img{
    width: 100px !important;
    height: 100px !important;
    display: block !important;
    overflow: hidden !important;
    border-radius: 50px !important;
    box-shadow: 0px 0px 1px rgba(0, 0, 0, 0.3)!important;
}
div.col-main-friends span.img-type{
    position: relative !important;
    top: -20px !important;
    left: 20px !important;
}
    
    /*换图位，复制图片链接可换*/
  
    html{background:url(http://imgsrc.baidu.com/forum/wh%3D1024%2C768/sign=4ef4c023acc379317d3d8e28dbf68369/25ec6c2762d0f703a78b97f90bfa513d2797c5dd.jpg
    ) !important; 
    background-repeat:no-repeat!important;background-size: cover!important;
    background-position:center right!important;background-attachment:fixed!important;

    }
  
                        /*透明*/

    .top-fix-inner,.homepage-link.a-link{background:transparent !important;}
    .top-fix-bar.body,#layBackground,body{background:transparent !important;}
    .layout-background{background:transparent !important;}
    .layout-nav{background:transparent !important;}
    .qz-poster-ft{background:transparent !important;}
    .rm-change{background:transparent !important;}
    [class*="mod"]{background:transparent !important;}
    #feed_friend_updatetips,#aIcenter{background:transparent !important;}
    #feed_tab_hover{background:transparent !important;}
    #tb_setting_panel,#tb_logout{background:transparent !important;}
    #feed_friend,#div:nth-child(1){background:transparent !important;}
    [class*="mod"]{background:transparent !important;box-shadow: 0px 0px 1px #333333;}  
  
  	                        /*列表透明*/

    [class*="area"]{background:rgba(228,228,228,0.2)!important;}

    .bor2.bg2.txt{background:transparent !important;}
    #pageIndex_input2{background:transparent !important;}
    .qz-poster-ft{background:transparent !important;}
    .qz-inputer.bor2{background:transparent !important;box-shadow: 0px 0px 1px #333333;}
    .textinput.textinput_default.bor2{background:transparent !important;}
    .btn-insert-img.bg{background:transparent !important;}
    .btn-quick-comment.bg{background:transparent !important;}
    .funny.evt_hover,.other,.pic,.attach{background:transparent !important;}
    .item.bor3.bg4.item-funny{background:transparent !important;}
    .item.bor3.bg4.item-other{background:transparent !important;}
    .item.bor3.bg4.item-pic.evt_hover{background:transparent !important;}
    #pager_go_0,.textinput{background:transparent !important;}
    .info_side.bg3.bor3{background:transparent !important;}
    #feed_link,#like_link,#info_link{background:transparent !important;}
    [class*="f-single"]{background:transparent !important;}
    .f-detail,.mod-comments{background:transparent !important;}
    .f-op-wrap{background:transparent !important;}

  
                    /*透明度降低*/
    .layout-body,.nav-drop-down{
    background:rgba(228,228,228,0.1)!important;}
    .fn-feed-control-v2.clearfix{background:rgba(228,228,228,0.5)!important;}
    .fn-checkin-btn.deep,.layout-head,.html,#QZ_Body{background:rgba(228,228,228,0.1)!important;}
   #hex_346616333_217_3_1395923672_1_1 > div:nth-child(2) > div:nth-child(1) > div:nth-child(1){background:rgba(228,228,228,0.5)!important;}
    .user-home{background:rgba(228,228,228,0.1)!important;}
    #aMyFriends{background:rgba(228,228,228,0.1)!important;}
   #aAppstore,#layBackground{background:rgba(228,228,228,0.1)!important;}
   #aMall{background:rgba(228,228,228,0.1)!important;}
   .textinput,.textarea.c_tx3{background:rgba(228,228,228,0.2) !important;}
   .qz-grid{background:rgba(228,228,228,0.1)}
   .qz-grid{background:rgba(228,228,228,0.1) !important;}   /*左边列表*/
   #site_hot_btn,#goto_top_btn,#feed_tab_my,#feed_tab_participate{
   background:rgba(228,228,228,0.3)!important;}
   .music-play,.music-dynamic{background:rgba(228,228,228,0.6) !important;}
  .hover,[class*="search"]{background:rgba(228,228,228,0.5) !important;}
  .background-music,.setting-box{background:rgba(228,228,228,0.9) !important;}
   .funny,.evt_hover,.item,.bor3,.bg4,.item-funny{background:rgba(228,228,228,0.1)!important;}
   .textinput-default,.bor2{background:rgba(228,228,228,0.1)!important;}
   #QZ_Toolbar_Container{background:rgba(228,228,228,0.3)!important;}
   .nav-drop-down{background:rgba(228, 228, 228,0.2)!important;}
    .avatar-edit-list,.none{background:rgba(228,228,228,0.2) !important;}

    

    
                   /*悬浮模块*/
    div.mod-side-nav:nth-child(1){float: Right;}
    div.mod-side-nav:nth-child(1){
    width:46px !important;
    height:auto !important;
    top:80px !important;
    border:#fff solid 1px !important;
    border-radius:5px !important;
    padding-bottom:5px;
    background:rgba(255,255,255,.1) !important;
    box-shadow: -1px -1px 2px rgba(20,15,55,0.6) !important;
    -moz-transition: all 0.25s linear;}
    div.mod-side-nav:nth-child(1):hover{width:130px !important;-moz-transition: all .25s linear;}

                /*屏蔽头像*/
    body.os-win7.bg-body div#layBackground div.layout-nav>div.layout-nav-inner
{visibility:hidden !important;} 
    
}
