css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("lichess.org") {
    
    
    
    body.dark{

color: #DBC73C !important;



background-repeat:repeat !important;}



body.dark a, body.dark a:visited {

    color: #DBC73C !important;}



body.dark input, body.dark textarea {

    background: none repeat scroll 0 0 #1A1A1A !important;

    color: #CBBDAF !important;}



body.dark div.lichess_chat, body.dark div.undertable, body.dark div.lichess_board_wrap, body.dark div.lichess_table .lichess_button, body.dark div.lichess_goodies div.box, body.dark div.lichess_table, body.dark div.undergame_box, body.dark div.clock, body.dark #GameText, body.dark #GameBoard table.boardTable, body.dark .button.strong, body.dark .button.shadowed, body.dark #tournament_side, body.dark #friend_box, body.dark div.content_box, body.dark #powerTip {

    box-shadow: none !important;

}



body.dark #top, body.dark #reconnecting, body.dark #connecting, body.dark div.lichess_chat_top, body.dark div.lichess_chat .lichess_messages, body.dark div.undertable_top,  body.dark div.lichess_table .lichess_button, body.dark div.lichess_chat form input, body.dark div.lichess_board_wrap, body.dark #translation_call, body.dark div.footer_wrap, body.dark div.game_config .optional_config, body.dark .ui-state-default, body.dark .ui-widget-content, body.dark div.lichess_goodies div.box, body.dark div.lichess_table, body.dark div.undergame_box, body.dark div.clock, body.dark .button, body.dark #GameText, body.dark #tournament_side, body.dark #GameBoard table.boardTable, body.dark div.shortcuts .title, body.dark div.content_box, body.dark div.content_box_top, body.dark div.content_box_top a, body.dark div.content_box_inter, body.dark div.content_box_inter a.active,  body.dark div.game_row, body.dark form.search_user_form input, body.dark div.user_show div.boxed_data, body.dark #lichess_forum div.post, body.dark #lichess_forum form.wide input, body.dark #lichess_message tr, body.dark #lichess_message div.thread_message, body.dark #lichess_message textarea, body.dark #lichess_message input, body.dark .content_box form input, body.dark div.progressbar, body.dark form.translation_form div.messages, body.dark form.translation_form input, body.dark div.locale_menu a, body.dark #adv_chart, body.dark #top .dropdown, body.dark div.lichess_overboard p.explanations, body.dark div.search_status, body.dark table.slist, body.dark table.slist thead th, body.dark #notifications > div, body.dark form.wide input[type="text"], body.dark form.wide textarea, body.dark #team .team-left, body.dark #team .team-right, body.dark #team h2, body.dark .leaderboard_title, body.dark #friend_box, body.dark div.user_show div.mod_zone, body.dark div.user_show div.user-infos, body.dark div.game_config input[type="text"], body.dark #powerTip, body.dark #powerTip > .title,  body.dark #hooks_wrap > div.tabs > a, body.dark #hooks_chart > div.grid {

    border: 1px solid #272A3D !important;

}



body.dark div.lichess_table_not_started{

border: none !important;

}



body.dark div.lichess_separator{

   border: 1px dotted #272A3D !important;

}



body.dark #hooks_wrap > div.tabs > a, body.dark #top div.auth .links a:hover, body.dark #top ul.language_links a:hover, body.dark #top ul.language_links a.current {

    background-color: #858585 !important;

    color: #1A1A1A !important;

}



body.dark #top, body.dark div.lichess_chat_top, body.dark #friend_box .title, body.dark div.undertable_top, body.dark .button, body.dark .button:visited, body.dark .ui-state-default, body.dark div.content_box_top, body.dark #translation_call, body.dark #notifications > div, body.dark div.locale_menu a, body.dark table.slist thead, body.dark #hooks_wrap > a.no_hook {

    background: -moz-linear-gradient(center top , #474D6E, #474D6E) repeat scroll 0 0 #858585  !important;

    color: #FFE746 !important;

}



body.dark  div.table_with_clock{

    background-color: #404040 !important;

}



body.dark .mini_board {

    box-shadow: none !important;

}

body.dark div.lmcs.black {

    background-color: #77A26D !important;

}

body.dark div.lmcs.white {

    background-color: #C8C365 !important;

}



body.dark div.undertable tr:nth-child(2n) td, body.dark #hooks_wrap, body.dark #hooks_wrap > div.tabs > a:hover, body.dark #hooks_wrap > div.tabs > a.active {

    background: none repeat scroll 0 0 #858585 !important;

}



body.dark div.lichess_table a.lichess_button:hover, body.dark div.lichess_table a.lichess_button.active {

    border: 1px solid #FFE746 !important;

    box-shadow: 0 0 1px #FFE746 !important;

}



body.dark div.lichess_table_not_started {

    box-shadow: none !important;

}



body.dark div.lichess_overboard.game_config {

    background: #5D5D5D !important;

    box-shadow: 0 0 1px #FFE746 !important;

}

body.dark div.lichess_overboard {

    background: none repeat scroll 0 0 #5D5D5D !important;

}

body.dark div.undertable tr:nth-child(2n) td, body.dark #hooks_wrap, body.dark #hooks_wrap > div.tabs > a:hover, body.dark #hooks_wrap > div.tabs > a.active {

    background: none repeat scroll 0 0 #696969 !important;

}



body.dark #hooks_table tr:nth-child(2n) td {

    background: none repeat scroll 0 0 rgba(70, 70, 70, 0.5) !important;

}

body.dark #hooks_table tr:nth-child(2n+1) td {

    background: none repeat scroll 0 0 rgba(91, 91, 91, 0.6) !important;

}

body.dark #hooks_table tr.cancel td {

    background: none repeat scroll 0 0 rgba(0, 80, 0, 0.7) !important;

}

body.dark #hooks_table tr:hover td {

    background: none repeat scroll 0 0 rgba(74, 77, 110, 0.8) !important;

}

body.dark #hook_filter {

    background: none repeat scroll 0 0 rgba(93, 93, 93, 0.8) !important;

}

body.dark #hook_filter td > label {

    color: #FFE746 !important;

}

body.dark #hook_filter td > label.hover:hover {

    background: none repeat scroll 0 0 #474D6E !important;

}

body.dark div.lichess_chat .lichess_messages, body.dark div.undertable_inner, body.dark div.lichess_goodies div.box, body.dark div.undergame_box, body.dark div.undertable td, body.dark div.lichess_table_wrap div.clock, body.dark #friend_box .content a:hover, body.dark div.footer_wrap {

    background: none repeat scroll 0 0 #404040 !important;

}



body.dark .ui-slider, body.dark div.progressbar, body.dark #translation_call div.progressbar, body.dark #top div.themepicker.shown div.theme:hover {

    background-color: #575757 !important;

}

body.dark #hooks_wrap > div.tabs > a, body.dark #top div.auth .links a:hover, body.dark #top ul.language_links a:hover, body.dark #top ul.language_links a.current {

    background-color: #575757 !important;

    color: #FFE746 !important;

}

body.dark #top ul.language_links a.accepted {

    color: #FFE746 !important;

}

body.dark #site_title span.extension, body.dark div.sub_elos h3 {

    color: #FFE746 !important;

}



body.dark .lines{

font-size:15px!important;

}



body.dark #top a.goto_nav:hover, body.dark #top a.toggle:hover, body.dark #top a.bgpicker:hover, body.dark a#sound_state:hover, body.dark #top .dropdown, body.dark #GameText a:hover, body.dark #hooks_wrap a.filter:hover, body.dark #hooks_wrap a.filter.active, body.dark #top .shown a.toggle {

    background: none repeat scroll 0 0 #2B2F43 !important;

}



body.dark #top a.goto_nav.active, body.dark .button.active, body.dark .button.active:hover, body.dark .ui-state-active, body.dark .ui-widget-header, body.dark div.pagination span.current, body.dark #top span.new_messages, body.dark .button.strong:hover, body.dark div.progressbar.flashy div, body.dark div.locale_menu a.active, body.dark #top a.signin {

    background: -moz-linear-gradient(center top , #3A3F5A, #2B2F43) repeat scroll 0 0 #2B2F43 !important;

    color: #E2D400 !important;

}



body.dark #lichess_forum textarea body.dark {

 color: #4D4D4D !important;

 }



body.dark #top a.goto_nav.current {

    background: -moz-linear-gradient(center top , #3A3F5A, #2B2F43) repeat scroll 0 0 #2B2F43 !important;

    color: #E2D400 !important;

}



body.dark #site_title, body.dark #site_baseline, body.dark div.lichess_chat span,  body.dark div.new_posts li span, body.dark #team .forum a.user_link, body.dark span.board_mark, body.dark div.user_show div.content_box_top > span {

    color: #FFE746 !important;

}



body.dark div.lichess_table_wrap div.clock.running {

    background: none repeat scroll 0 0 #474D6E !important;

    color: #FFE746 !important;

    border: 1px solid #FFE746 !important;

}



body.dark div.lichess_table_wrap div.clock.running.emerg, body.dark div.lichess_table_wrap div.clock.outoftime {

    background-color: #A00000 !important;

    color: #FFE746 !important;

    border: 1px solid #FFE746 !important;

}



div.lichess_table a.lichess_button:hover, div.lichess_table a.lichess_button.active {

    border: 1px solid #474D6E !important;

    box-shadow: 0 0 9px #474D6E !important;

}



#premove_alert, #dont_touch {

    background: none repeat scroll 0 0 #474D6E  !important;

    border: 1px solid #FFE746 !important;

    color: #FFE746 !important;



}

div.lichess_chat a.user_link, body.dark #site_header div.side_menu a.active,  body.dark #site_header, body.dark #timeline a, body.dark #timeline time {

    color: #CBBEB0 !important

}



div.clock {

    border: 1px solid #474D6E !important;

    box-shadow: 0 0 7px #D0D0D0 !important;

    display: inline-block !important;

    font-family: monospace !important;

    font-size: 22px !important;

    font-weight: bold !important;

    padding: 4px 10px 2px !important;

}



div.lichess_current_player div.lichess_player p {

    color: #E2CB00 !important;

    font-size: 15px;

    font-weight: bold;

    height: 64px;

    line-height: 64px;

    margin: 0 0 0 64px;

}



body.dark div.lichess_chat form input {

color: #ff0000 !important;

}







.grey .is2d cg-board {

background-image: url(data:image/png;base64,)!important;

}


.purple .is2d cg-board {

background-image: url(data:image/png;base64,)!important;

}





  

body.dark div.onbg div.bishop.black, div.bishop.black  {

background-image: url(data:image/png;base64,)!important;    

}



piece.pawn.white {

background-image: url(data:image/png;base64,)!important;}



piece.bishop.white {

background-image: url(data:image/png;base64,)!important;}



piece.knight.white {

background-image: url(data:image/png;base64,)!important;}



piece.rook.white {

background-image: url(data:image/png;base64,)!important;}



piece.queen.white {

background-image: url(data:image/png;base64,)!important;}



piece.king.white {

background-image: url(data:image/png;base64,)!important;}



piece.pawn.black {

background-image: url(data:image/png;base64,)!important;}



piece.knight.black {

background-image: url(data:image/png;base64,)!important;}



piece.rook.black {

background-image: url(data:image/png;base64,)!important;}



piece.queen.black {

background-image: url(data:image/png;base64,)!important;}



piece.king.black {

background-image: url(data:image/png;base64,)!important;

}
    
    

}
