css:
@-moz-document url-prefix('https://www.goodreads.com/list') {
  .rightContainer .clearFloats.bigBox{
	display: none;
  }
  span[itemprop="author"]+span,span[itemprop="author"]+span+div{
	display: none;
  }
  .rightContainer .mediumText~.mediumText{
	position: absolute;
	top: -30px;
	left: -100%;
  }
}
