@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("fantasysports.yahoo.com") {


/*
,
h4[class^="ysptitlebar yspsplit"],
div[id^="noteContainer-ft"],

       { display: none !important;  }
*/

/*General Ad Blocking*/
div[id^="yspadMH"]
       { display: none !important }



/*Overview Page*/
div[class^="details"] { min-height: 74px !important }
div[id^="gamepromo"],
div[id^="ffl_promo"],
div[id^="flash-promo-container"],
div[id^="home-promo"],
div[id^="yspadLREC"],
div[id^="yspadNT1"]
           {display: none !important }
table[id^="scheduletable"]>tbody>tr>td[class^="first"] {width: 180px !important }
td[class^="scorethin last"]>table>tbody>tr>td[class^="score"] {width: 30px !important }
td[class^="scorethin last"]>table>tbody>tr>td[class^="matchup"] {text-align: left !important }

		   
/*League Page*/
td[class^="first rank"] {width: 25px !important }
div[id^="recentmessages"] {width: 954px !important }
div[id^="commishnote"] {width: 946px !important }
#recentmessages.coke_zero h4 span {background: none !important }
div[class^="feature-promo"]
           {display: none !important }

/*My Team Screen*/
div[id^="notetoself"] {width: 62% !important }
div[id^="notetoselfcontent"] {width: 100% !important }
		   
/*Player Note Popup*/
div[id^="noteContainer"] { height: 264px !important }
div[id^="noteContainer-hd"] { height: 264px !important }
div[id^="playerinfo"] { vertical-align: top !important }
div[class^="noteitem first"] { width: 210px !important }
div[id^="playernotesponsor"] { display: none !important }

/*Player Notes Screen*/
body[id^="yspplayernotes"]>div>div>div[id^="yui-main"] {width: 930px !important}
body[id^="yspplayernotes"]>div>div>div>div[id^="yspmain"] { width: 100% !important }
body[id^="yspplayernotes"]>div>div>div>div>div>div>div[class^="playercontent"] { width: 870px !important }

/*Injury Report Screen*/
body[id^="yspinjuries"]>div>div>div[id^="yui-main"] {width: 930px !important}
body[id^="yspinjuries"]>div>div>div>div[id^="yspmain"] { width: 100% !important }
body[id^="yspgamedaycalls"]>div>div>div[id^="yui-main"] {width: 930px !important}
body[id^="yspgamedaycalls"]>div>div>div>div[id^="yspmain"] { width: 100% !important }

/*League Leaders Screen*/
body[id^="yspleaders"]>div>div>div[id^="yui-main"] {width: 930px !important}
body[id^="yspleaders"]>div>div>div>div[id^="yspmain"] { width: 100% !important }

/*NFL Roster Changes Screen*/
body[id^="yspplayerchanges"]>div>div>div[id^="yui-main"] {width: 930px !important}
body[id^="yspplayerchanges"]>div>div>div>div[id^="yspmain"] { width: 100% !important }
table[id^="cantcutlisttable0"]>tbody>tr>td[class^="player first"] {width: 250px !important}
table[id^="cantcutlisttable0"]>tbody>tr>td[class^="owner"] {width: 100px !important}

/*Can't Cut List Screen*/
body[id^="yspcantcutlist"]>div>div>div[id^="yui-main"] {width: 930px !important}
body[id^="yspcantcutlist"]>div>div>div>div[id^="yspmain"] { width: 100% !important }
table[id^="cantcutlisttable0"]>tbody>tr>td[class^="player"] {width: 250px !important}


/*Matchup Screen*/
body[id^="yspmatchup"]>div>div>div>div[id^="yspmain"] {width: 930px !important }
div[id^="matchupheader1"] {width: 450px !important }
div[id^="matchupcontent1"] {width: 462px !important }
div[id^="smackcontent1"] {width: 100% !important }
div[id^="matchupheader2"] {width: 450px !important }
div[id^="matchupcontent2"] {width: 462px !important }
div[id^="smackcontent2"] {width: 100% !important }
body[id^="yspmatchup"]>div>div>div>div>div>div[class^="yui-u"] {width: 457px !important}
#matchuppoll h3 {background: none !important }
div[id^="matchup-wall-coke-zero-header"]>div[class^="bd"],
div[id^="yspadSKY"] {display: none !important }


/*Players Screen*/
div[id^="yspadREC"] { display: none !important;  }
.free #playerfilter  { padding: 0 10px 13px 10px !important }

/*Draft Central Screen*/
#page_title_sponsor {background: none !important}

/*Draft Results Screen */
body[id^="yspdraftresults"]>div>div>div[id^="yui-main"] {width: 930px !important}
body[id^="yspdraftresults"]>div>div>div>div[id^="yspmain"] { width: 100% !important }

/*Draft Analysis Screen */
body[id^="yspdraftanalysis"]>div>div>div[id^="yui-main"] {width: 930px !important}
body[id^="yspdraftanalysis"]>div>div>div>div[id^="yspmain"] { width: 100% !important }
table[id^="draftanalaysistable"]>tbody>tr>td[class^="player first"] {width: 250px !important}
table[id^="draftanalaysistable"]>tbody>tr>td[class^="pick sorted"] {width: 60px !important}
table[id^="draftanalaysistable"]>tbody>tr>td[class^="round"] {width: 60px !important}

/*Pre-Draft Rankings Screen */
body[id^="yspprerank"]>div>div>div[id^="yui-main"] {width: 930px !important}
body[id^="yspprerank"]>div>div>div>div[id^="yspmain"] { width: 100% !important }

/*Draft Kit Screen */
body[id^="yspdraftkit"]>div>div>div[id^="yui-main"] {width: 930px !important}
body[id^="yspdraftkit"]>div>div>div>div[id^="yspmain"] { width: 100% !important }
table[id^="filetable"]>tbody>tr>td[class^="first"] {width: 100px !important}

/*Messages Screen*/
#coke-zero-leaguemessages-header span,
#coke-zero-leaguemessages-footer span,
#yspmain #coke-zero-leaguemessages-header h1 { background: none !important }
div[id^="coke-zero-leaguemessages-footer"] {display: none !important }
div[id^="ysf-messages"] { margin-bottom: 10px !important }
body[id^="yspmessages"]>div>div>div[id^="yui-main"] {width: 930px !important}
body[id^="yspmessages"]>div>div>div>div[id^="yspmain"] {width: 100% !important}

/*Email League Screen*/
body[id^="yspemailleague_1"]>div>div>div[id^="yui-main"] {width: 930px !important}
body[id^="yspemailleague_1"]>div>div>div>div[id^="yspmain"] { width: 100% !important }
form[id^="email-league-form"] {width: 100% !important }
#email-league-form #em_subject, 
#email-league-form #em_msg { width: 609px !important }
div[id^="email-league-instructions"] {height: 559px !important }

/*Managers Screen*/
body[id^="yspteams"]>div>div>div>div[id^="yspmain"] { width: 934px !important }

/*Rosters Screen*/
div[id^="starterssubnav"] {width: 100% !important }
body[id^="yspstarters"]>div>div>div>div[id^="yspmain"] { width: 934px !important }
body[id^="yspstarters"]>div>div>div>div>div[id^="starters"]>table>tbody>tr>td[class^="first"] { width: 30px !important }
body[id^="yspstarters"]>div>div>div>div>div[id^="starters"]>table>tbody>tr>td[class^="first owner"] { width: 100px !important }

/*Transactions Screen */
body[id^="ysptransactions"]>div>div>div[id^="yui-main"] {width: 930px !important}
body[id^="ysptransactions"]>div>div>div>div[id^="yspmain"] { width: 100% !important }
body[id^="ysptransactions"]>div>div>div>div[id^="yspmain"] { margin-bottom: 10px !important }

/*Trading Block Screen */
body[id^="ysptradingblock"]>div>div>div[id^="yui-main"] {width: 930px !important}
body[id^="ysptradingblock"]>div>div>div>div[id^="yspmain"] { width: 100% !important }
body[id^="ysptradingblock"]>div>div>div>div[id^="yspmain"] { margin-bottom: 10px !important }

/*Record Book Screen */
div[id^="research-promo"] {display: none !important }
p[class^="promo-description"] {width: 100% !important }

/*Commish Tools Screen */
body[id^="yspcommishhome"]>div>div>div[id^="yui-main"] {width: 930px !important}
body[id^="yspcommishhome"]>div>div>div>div[id^="yspmain"] { width: 100% !important }

/*Matchup Ratings Screen */
body[id^="yspplayermatchups"]>div>div>div[id^="yui-main"] {width: 930px !important}
body[id^="yspplayermatchups"]>div>div>div>div[id^="yspmain"] { width: 100% !important }

/*Player Ranker Screen */
body[id^="ysprankerresults"]>div>div>div[id^="yui-main"],
body[id^="ysprankerroster"]>div>div>div[id^="yui-main"],
body[id^="ysprankerfreeagents"]>div>div>div[id^="yui-main"] {width: 930px !important}
body[id^="ysprankerresults"]>div>div>div>div[id^="yspmain"],
body[id^="ysprankerroster"]>div>div>div>div[id^="yspmain"],
body[id^="ysprankerfreeagents"]>div>div>div>div[id^="yspmain"] { width: 100% !important }

/*MVP's Screen */
body[id^="yspkeystosuccess"]>div>div>div[id^="yui-main"] {width: 930px !important}
body[id^="yspkeystosuccess"]>div>div>div>div[id^="yspmain"] { width: 100% !important }

/*Scoring & Settings Screen */
body[id^="yspsettings"]>div>div>div[id^="yui-main"] {width: 930px !important}
body[id^="yspsettings"]>div>div>div>div[id^="yspmain"] { width: 100% !important }



}




@-moz-document domain("sports.yahoo.com") {

div[id^="nt1"],
div[id^="storepromo"],
td[class^="ysprtcol1"]
   { display: none !important }

table[class^="yspcontent"]>tbody>tr>td[width^="800"] { width: 100% !important}
}


@-moz-document domain("profiles.sports.yahoo.com") {

div[id^="yspadSKY"]
   { display: none !important }
div[id^="yspmain"] { width: 100% !important }
div[id^="statsbio"] {width: 480px !important }
div[class^="archivegroup-bd"]>table>tbody>tr>td[class^="game"] {width: 100px !important }

}
