css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("okcupid.com") {
    
    body#p_home div.tabbed_heading h1, body#p_upgrade div.tabbed_heading h1, body#p_upgrade_redesign div.tabbed_heading h1, body#p_presspage div.tabbed_heading h1, body#p_aboutus div.tabbed_heading h1, body#p_jobs div.tabbed_heading h1, body#p_stalkers div.tabbed_heading h1, body#p_contactus div.tabbed_heading h1 {
        padding: 0px 10px 11px;
    }
    body {
        background-color: #0C235C;
    }
    
    body#p_signup_new_inga.splash #main_content #signup_wrapper {
        background: transparent !important;
    }
    #windowshade, #search_overlay_wrapper, #search_overlay_wrapper .search_overlay_content.header, #uploadpicker {
        background: none repeat scroll 0 0 rgba(44, 44, 44, .94);
    }
    .modal, #action_bar {
        background-color: #212121 !important;
    }
    #search_overlay_wrapper #close_search_overlay:hover:before {
        background-color: transparent;
    }
    
    #main_content, #ratings_heading, .page_tabs, #tabbed_heading, #rate, #info_blocks, #right .box, div.upgrade_ad.filters a.wrapper,
    #filter_controls header, #nomessages, .nomessagesbox, #mailbox_heading, #filter_controls ul, .monolith div#right_column .body,
    #visitors_heading, div.user_row_item, #matches_heading, form.styled div.form_section, .match_card_wrapper .match_card,
    form.styled div.form_contents, div.tabbed_heading, #profile_heading, #profile_thumbs, #basic_info, #basic_info_sn,
    .tabbed_heading.heading_big #basic_info h1:after, #aso_loc, #settings_heading, .tabbed_heading, #action_bar #action_bar_interior,
    div.big_dig #questions .question.not_answered, div.big_dig #questions .question, div.big_dig > div.right ul.lined li,
    div#right_column.personality div.body, div#main_column.personality div.pt_row, div.tabbed_heading, body.tabs_redesign div.tabbed_heading,
    #right #qsettings.box, .monolith #right_column .body.dummy, #dummy_cover, body.tabs_redesign .monolith #improve_your_matches,
    #matches_block #matchphotobrowser_loading, #matches_block #matchphotobrowser .photobrowser, #matches_block #matchphotobrowserbg,
    #matches_block .item, li.thread .inner, #message_heading, #hidden_users_form, #right_bar div.body, #improve_your_matches,
    .splash #header, .splash.sidebar_update #header {
        background-color: #212121 !important;
        text-shadow: none;
        border: 0px !important;
        border-radius: 0px;
    }
    
    div#matchNoResults {
        background: url("http://i.imgur.com/AHrlWAw.png") no-repeat scroll 20px top rgba(0, 0, 0, 0);
    }
    
    form.styled div.form_element p.button {
        background: url("http://i.imgur.com/tYNMWaT.png") no-repeat scroll left top rgba(0, 0, 0, 0);
    }
    
    form.styled div.form_element p.button a{
        background: url("http://i.imgur.com/x0NqiDK.png") no-repeat scroll right top rgba(0, 0, 0, 0);
    }
    
    form.styled div.form_element ul {
        background: url("http://i.imgur.com/0RbM3s9.png") no-repeat scroll left bottom rgba(0, 0, 0, 0);
    }
    
    form.styled div.form_element .drop_wrap {
        background: url("http://i.imgur.com/OVQEgA3.png") no-repeat scroll right bottom rgba(0, 0, 0, 0);
    }
    
    form.styled span.remove {
        background: url("http://i.imgur.com/miir61O.png") no-repeat scroll 0 0 rgba(0, 0, 0, 0);
    }
    
    #thread li.to_me .message, div#main_column div.deads div.dead:hover, #splash input.package:checked + label, div.big_dig #questions .question .footer,
    #album_list div.album a.img div.inner, #album_list div.album div.overlay, .match_card_wrapper .user_hidden_overlay, .card hover, 
    #recent_activity .card .user_info, body#p_signup_new_inga.splash #main_content .speech_bubble, div.big_dig > div.right ul.lined.match li.category .outer {
        background-color: #333;
    }
    
    body#p_signup_new_inga.splash #main_content .speech_bubble .tail {
        border-color: transparent transparent #333;
    }
    
    #thread li.to_me:after {
        border-top: 17px solid #333;
    }
    
    #thread li.from_me .message, #album_list div.album .outer, #album_list div.album div.matte, #album_list div.button a.img:hover div.inner {
        background-color: #444;
    }
    
    #thread li.from_me:after {
        border-top: 17px solid #444;
    }
    
    #thread li .message .message_body {
        color: #777; 
    }
    
    #matches_block #matchphotobrowser_loading, #matches_block #matchphotobrowser .photobrowser, #matches_block #matchphotobrowserbg,
    .match_card_wrapper .match_card, #recent_activity .card .image {
        box-shadow: none;
    }
    
    div.big_dig #questions .question .qtext p, div.big_dig #questions .question .title p {
        color: #5E6589;
    }
    
    div.tabbed_heading ul.page_tabs li.active, .photobrowser .scrollbar {
        background-color:#444;
        border:0px;
    }
    
    .photobrowser .scrollbar .handle {
        background-color:#666;
    }
    
    div.tabbed_heading ul.li a {
        text-shadow:none;
    }
    
    #left p, #right .box, .text {
        color:#888;
    }
    
    #oknotice_success, #whosIntoYouUpgrade {
        background-color:#183270;
    }
    
    #leaderboard_container, #leaderboard_wrapper, #quickmatch_ad, .okad, .upgrade_ad, .adbeaveritem {
        display:none !important;
    }
    #page > div:nth-child(4) > div:nth-child(1) {
        visibility:hidden;
    }
    
    .block_wrapper, #skyscraper_floater_wrapper2, .side_skyscraper_ad, .sky_ad, .oknotice {
        visibility: hidden;
        max-height: 1px;
        max-width: 1px;
        background-color: #0C235C;
    }
    
    div.seemore a, #right #qsettings.box .button_container a, .monolith div#right_column .button_container .flatbutton {
        background: transparent;
        border: 1px solid #ccd0d9;
        color: #949aa6;
        line-height: 33px;
    }
    
    div.seemore a:hover, #right #qsettings.box .button_container a:hover, .monolith div#right_column .button_container a:hover,
    #what_i_want.self:hover, #the_skinny_self_wrapper:hover, div.big_dig > div.right ul.lined.match li.category:hover a {
        background-color: rgba(174,180,191,0.1)
    }
    
    aside.grayaside, #flagmodform {
        background: transparent !important;
    }
    
    #dummy_cover, #right_column.photos .photo, #question_search_suggestions, div.big_dig > div.header #search_container #question_search_suggestions li.section span {
        background: #222 !important;
    }
    
    
    /*NEW CHANGES 15.10.25*/
    .qm-card.qm-card--waiting, .qm-card.qm-card-index--0, .cardactions {
        background-color: #333 !important;
        border-top-width: 0 !important;
    }
    
    .carouselcard .carouselimg {
        background-color: #151515 !important;
    }
    
    .chosen-container .chosen-drop, #match-filters .match-filters-in-results .order-by .order-by-select, #match-filters .match-filters-in-results .order-by .chosen-container, #match-filters .chosen-container .chosen-single {
        background-color: #333 !important;
        color: #888 !important;
    }
    
    #match-filters .chosen-container .chosen-drop .chosen-results li {
        color: #888 !important;
    }
    
    #match-filters .chosen-container .chosen-drop .chosen-results li:hover {
        color: #BBB !important;
    }
    
    .match_card_wrapper .match_card::after {
        background-color: #333 !important;
        border-bottom: 1px solid #333;
    }
    
}
