css:
@-moz-document domain(forums.somethingawful.com) {
    
*
  { text-shadow: none !important; background-image:none !important; border:0 !important; box-shadow:none !important;} 

#container
  { padding:50px 0 0 !important; }  
  
ul.navigation
  { margin-bottom:0; padding-top:20px !important;}

ul#navigation.navigation { border-radius:0; box-shadow:0 0 5px rgba(0,0,0,.3) !important; padding-top:0; padding:10px 0 !important; position:fixed; top:0; width:100%; z-index:9;}
  #nav_purchase a, #nav_purchase { color:#0088cc;}
  #nav_purchase a:hover {color:#a7392b; }
  
#usercpnav { background-color: transparent !important; }

#buddylist {
    background: none repeat scroll 0 0 #ffffff;
    margin: 0 1em 0 0;
    position: absolute;
    right: 0;
    top: 100px;
    width: 13em;
}  
  
#buddylist h2 { display: none;}  
  
#forum td.title, #forum td.icon, #forum td.icon2, #forum td.star, .privfolder #content table tr td.status, .privfolder #content table tr td.icon, #usercpnav, #forum td.replies, #forum td.rating, .threads_editthread #content table#main_wide.standard tr:last-child td, #forum td.author, #forum td.views, #forum td.lastpost
  
  { background: #f1f1f1 !important; }
  
td.userinfo   { background:none !important;}
  
  
/*#forum tr.seen td.title, .altcolor2 td.userinfo, .altcolor2 td.postdate, .altcolor1 td.userinfo, .altcolor1 td.postdate, #forum td.icon, #forum td.icon2, #forum td.star, .privfolder #content table tr td.status, .privfolder #content table tr td.icon, #usercpnav, #nav_purchase, #forum td.replies, #forum td.rating, .threads_editthread #content table#main_wide.standard tr:last-child td, #forum td.author, #forum td.views, #forum td.lastpost
  
  { background: #f1f1f1 !important; }  ***/
  
  
#forum tr.thread.category0 td.title, #forum tr.thread.category0 td.star, #forum tr.thread.category0 td.icon, #forum tr.thread.category0 td.icon2, #forum tr.thread.category0 td.author, #forum tr.thread.category0 td.views, #forum tr.thread.category0 td.lastpost, #forum tr.thread.category0 td.button_remove, #forum tr.thread.category0 td.replies, #forum tr.thread.category0 td.rating, #forum tr.thread.category0 td.votes,
  
#forum tr.thread.category1 td.title, #forum tr.thread.category1 td.star, #forum tr.thread.category1 td.icon, #forum tr.thread.category1 td.icon2, #forum tr.thread.category1 td.author, #forum tr.thread.category1 td.views, #forum tr.thread.category1 td.lastpost, #forum tr.thread.category1 td.button_remove, #forum tr.thread.category1 td.replies, #forum tr.thread.category1 td.rating, #forum tr.thread.category1 td.votes,
  
#forum tr.thread.category2 td.title, #forum tr.thread.category2 td.star, #forum tr.thread.category2 td.icon, #forum tr.thread.category2 td.icon2, #forum tr.thread.category2 td.author, #forum tr.thread.category2 td.views, #forum tr.thread.category2 td.lastpost, #forum tr.thread.category2 td.button_remove, #forum tr.thread.category2 td.replies, #forum tr.thread.category2 td.rating, #forum tr.thread.category2 td.votes
  
{ background: none; } 
  
#forum tr.thread.category0.seen td.title, #forum tr.thread.category0.seen td.star, #forum tr.thread.category0.seen td.icon, #forum tr.thread.category0.seen td.icon2, #forum tr.thread.category0.seen td.author, #forum tr.thread.category0.seen td.views, #forum tr.thread.category0.seen td.lastpost, #forum tr.thread.category0.seen td.button_remove, #forum tr.thread.category0.seen td.replies, #forum tr.thread.category0.seen td.rating, #forum tr.thread.category0.seen td.votes,
  
#forum tr.thread.category1.seen td.title, #forum tr.thread.category1.seen td.star, #forum tr.thread.category1.seen td.icon, #forum tr.thread.category1.seen td.icon2, #forum tr.thread.category1.seen td.author, #forum tr.thread.category1.seen td.views, #forum tr.thread.category1.seen td.lastpost, #forum tr.thread.category1.seen td.button_remove, #forum tr.thread.category1.seen td.replies, #forum tr.thread.category1.seen td.rating, #forum tr.thread.category1.seen td.votes,
  
#forum tr.thread.category2.seen td.title, #forum tr.thread.category2.seen td.star, #forum tr.thread.category2.seen td.icon, #forum tr.thread.category2.seen td.icon2, #forum tr.thread.category2.seen td.author, #forum tr.thread.category2.seen td.views, #forum tr.thread.category2.seen td.lastpost, #forum tr.thread.category2.seen td.button_remove, #forum tr.thread.category2.seen td.replies, #forum tr.thread.category2.seen td.rating, #forum tr.thread.category2.seen td.votes
  
{ background-color:#e8f0f8 !important; }
  
#forum tr.thread.category0.seen:hover td.title, #forum tr.thread.category0.seen:hover td.star, #forum tr.thread.category0.seen:hover td.icon, #forum tr.thread.category0.seen:hover td.icon2, #forum tr.thread.category0.seen:hover td.author, #forum tr.thread.category0.seen:hover td.views, #forum tr.thread.category0.seen:hover td.lastpost, #forum tr.thread.category0.seen:hover td.button_remove, #forum tr.thread.category0.seen:hover td.replies, #forum tr.thread.category0.seen:hover td.rating, #forum tr.thread.category0.seen:hover td.votes,
  
#forum tr.thread.category1.seen:hover td.title, #forum tr.thread.category1.seen:hover td.star, #forum tr.thread.category1.seen:hover td.icon, #forum tr.thread.category1.seen:hover td.icon2, #forum tr.thread.category1.seen:hover td.author, #forum tr.thread.category1.seen:hover td.views, #forum tr.thread.category1.seen:hover td.lastpost, #forum tr.thread.category1.seen:hover td.button_remove, #forum tr.thread.category1.seen:hover td.replies, #forum tr.thread.category1.seen:hover td.rating, #forum tr.thread.category1.seen:hover td.votes,
  
#forum tr.thread.category2.seen:hover td.title, #forum tr.thread.category2.seen:hover td.star, #forum tr.thread.category2.seen:hover td.icon, #forum tr.thread.category2.seen:hover td.icon2, #forum tr.thread.category2.seen:hover td.author, #forum tr.thread.category2.seen:hover td.views, #forum tr.thread.category2.seen:hover td.lastpost, #forum tr.thread.category2.seen:hover td.button_remove, #forum tr.thread.category2.seen:hover td.replies, #forum tr.thread.category2.seen:hover td.rating, #forum tr.thread.category2.seen:hover td.votes{ background: #d4e1ee !important; }  

#forum tr.seen td.title, #forum tr.seen td.icon, #forum tr.seen td.icon2, #forum tr.seen td.star, #forum tr.seen td.author, #forum tr.seen td.views, #forum tr.seen td.lastpost,#forum tr.seen td.replies, #forum tr.seen td.rating, #forum tr.seen td.votes { background-color:#e8f0f8 !important;}  
  
  
#forum tr:hover td { background: #e3e3e3 !important;}  

#forum tr.seen:hover td { background: #d4e1ee !important;}  

  
.thread td.title_sticky a.thread_title { background: url("http://fi.somethingawful.com/sticky.png") no-repeat scroll left center rgba(0, 0, 0, 0) !important;}
  
tr.category0 td.star div, td.star.bm0 div { background:url("http://fi.somethingawful.com/images/stars/bookmark-star-tan.png") !important;}  
  
tr.category0 td.star div, td.star.bm0 div { background:url("http://fi.somethingawful.com/images/stars/bookmark-star-tan.png") !important;}  
  
tr.category1 td.star div, td.star.bm1 div { background:url("http://fi.somethingawful.com/images/stars/bookmark-star-red.png") !important;} 
  
tr.category2 td.star div, td.star.bm2 div { background:url("http://fi.somethingawful.com/images/stars/bookmark-star-yellow.png") !important;}   
  
#thread dl.userinfo dt.platinum { background: initial !important; background-image: url("http://downloads.somethingawful.com/misc/gren-left.svg") !important; background-size: 11px auto !important; background-repeat: no-repeat !important;}  
#thread dl.userinfo dt.role-admin { background: initial !important;     background-image: url("http://downloads.somethingawful.com/misc/red_star.svg") !important;  background-size: 15px auto !important;  background-repeat: no-repeat !important;}  
  
  
#forum td.title div.lastseen{ background: none; border:0;  }
#forum td.title div.lastseen a.x{ background: none;  border:1px solid #a82c2c !important; border-radius: 20px; color:#a82c2c;; padding:3px 6px; text-shadow: none;}
    
#forum td.title div.lastseen a.x:hover { background: #a82c2c; color:#fff; }    
    
#forum td.title div.lastseen a:after { content: "";}

    #forum td.title div.lastseen a.count { min-width: 0; padding:3px 6px; width:auto; background:none; border:1px solid #0088cc !important; border-radius: 20px; color:#0088cc !important; margin-left:4px;}    
#forum td.title div.lastseen a.count:hover { background:#0088cc; color:#fff !important;}  
  
  
  
  
#forum th, div.threadbar, div.forumbar, #forums th, #forums th a, #forums th a:visited, #pm th, #pm th a, #pm th a:visited, #pm th a:hover, #info th, #forum th a, #forum th a:visited, .standard h2, table.standard th, .standard h2 a, .standard h2 a:visited, .getinfo .standard th a, .getinfo .standard th a:visited, .gloryhole table.stats th, .privfolder tfoot tr td
  { background:#006699;}
  
td.postdate a, td.postlinks ul.profilelinks li a, .dictnav a, #main_full td a, .searchresults #main_full tr .smalltext a, .newthread #content table.standard tr.altcolor2 td:last-child a, .newreply #main_full div.postinfo div.save-state a, .newreply #main_full tr.altcolor2 td:last-child a, .newthread #content table.standard tr:first-child a, .newreply #content table.standard tr:first-child a, .threads_editthread table#main_wide.standard td.user_loggedin a
  { border: 1px solid #ccc; }
  
.altcolor2 td.userinfo, .altcolor2 td.postdate, .altcolor1 td.userinfo, .altcolor1 td.postdate, #forum td.icon, #forum td.icon2, #forum td.star, .privfolder #content table tr td.status, .privfolder #content table tr td.icon, #usercpnav, #nav_purchase
  { background: none; }

table.post tr.seen1 td.userinfo, table.post tr.seen1 td.postdate
  { background: #dae8f6 !important; }  
  
table.post tr.seen2 td.userinfo, table.post tr.seen2 td.postdate
  { background: #c5d9ef !important; }  
  
  
body.forum_219 table.post tr.seen1 td.userinfo, body.forum_219 table.post tr.seen1 td.postdate, body.forum_219 table.post tr.seen2 td.userinfo, body.forum_219 table.post tr.seen2 td.postdate
  { background: rgba(0,0,0,0) !important;}

  
  
  
  ul.rating_buttons { background: url("http://fi.somethingawful.com/ui/rating-bg.png") no-repeat scroll center center !important; padding-top:3px !important; }
   ul.rating_buttons li { border: 1px solid #fff !important; color:#fff !important; text-shadow: 0 0 2px rgba(0,0,0,.9) !important;}
  
  .userquoted { border:1px solid #e8d6f1 !important; border-radius:3px;  background: #f0e2f8 !important;}
  .bbc-block { border-radius: 3px; }
  
  
  #copyright { background: #eeeeee; margin-top:0; padding:10px 0 20px;}
  body.forum_219 #copyright { background: #000 !important; margin-top:0; padding:10px 0 20px;}
  
}
