css:
@-moz-document domain("youtube.com") {

.watch-large #player-mole-container #player-api {
    position:fixed;
  top:0px;
  left:0px;
  z-index:999!important;
  width:1680px;

  width:100%!important;
  height:945px;
      height:90%!important;
       min-height:90%!important;

}
 
 .watch-wide #watch7-main-container #watch7-main #watch7-sidebar #watch7-sidebar-playlist {
  position:fixed;
  z-index:9999!important;
  float:left;
  left:0px;
  top:945px;
    top:90%;
width:1680px;
  width:100%!important;
  max-height:105px!important;
    max-height:10%!important;
}
   .watch-wide #watch7-main-container #watch7-main #watch7-sidebar  #watch-appbar-playlist > div > div.playlist-videos-container.yt-scrollbar-dark.yt-scrollbar > ol li.yt-uix-scroller-scroll-unit {
    display:inline-block;
    width:24%;
  }
   
  .watch-wide #watch7-main-container #watch7-main #watch7-sidebar #watch-appbar-playlist > div > div.playlist-videos-container.yt-scrollbar-dark.yt-scrollbar > ol {
max-height:105px!important;
   overflow:visible;
  overflow-y:scroll;
}
.watch-wide #watch7-main-container #watch7-main #watch7-sidebar #watch-appbar-playlist #watch7-sidebar-contents, 
  .watch-wide #watch7-main-container #watch7-main #watch7-sidebar #watch-appbar-playlist #watch7-content,
  .watch-wide #watch7-main-container #watch7-main #watch7-sidebar #watch-appbar-playlist .playlist-header {
  display:none;
}
  
    #yt-masthead-container {
      position: relative;

  display:flex;
  background: transparent!important;
border:none!important;
margin-top:-50px;

}
#yt-masthead-container:hover  {
      position: relative;

  display:flex;
  background: transparent!important;
border:none!important;
margin-top:0px;

}
  body, #body-container {
  background-color:000!important;
  
overflow:hidden;  
}
}
