css:
@-moz-document domain("n0l.ch") {
html, body, footer, #blotter {
	background-color: #212121 !important;
	color: #698CC0 !important;
}
span.omitted, .hyphenate, p.intro {
	font-size: 16px !important;
}
* {
	font-family: "Trebuchet MS",Trebuchet,tahoma,serif !important;
	text-shadow: none !important;
}
a {
	color: #00FF00 !important;
  	text-decoration: underline !important;
}
a:hover {
  	color:#66cc66 !important;
  	text-decoration: underline !important;
}
a.post_no, ul#linklist a {
	color: #00FF00 !important;
  	text-decoration: none !important;
}
a.post_no {
	font-weight: bold !important;
}
a.post_no:hover, ul#linklist a:hover {
  	color:#66cc66 !important;
  	text-decoration: none !important;  	
}
ul#linklist a:hover {
	background: black !important;
}
a.quick-reply-btn {
	text-decoration: none !important;
	color: #00FF00 !important;
}
a.quick-reply-btn:hover {
	text-decoration: none !important;
	color: #66cc66 !important;
}
a pre.unimportant {
	color:#00FF00 !important;
  	text-decoration: none !important; 
	-moz-text-shadow:0 0 15px #00FF00 !important;
	-webkit-text-shadow:0 0 15px #00FF00 !important;
	text-shadow:0 0 15px #00FF00 !important;
}
a.selected{
	font-weight: bold !important;
	color: #698CC0 !important;
}
a.close-btn, a.selected {
	text-decoration: none !important;
}
a.post_no {
	font-size: 13px !important;
}
a.hide-thread-link, a.unhide-thread-link {
	font-size: 0px !important;
}
a.hide-thread-link:before {
	content: url(http://i64.fastpic.ru/big/2014/0705/44/cf9123c36033f851c848ebb6796d7e44.png) !important;
}
a.unhide-thread-link:before {
	content: url(http://i62.fastpic.ru/big/2014/0705/95/1aa938b5944937432d01850573170995.png) !important;
}
span.quote {
	background: inherit;
	color: #789922;
}
p.fileinfo a {
	color: #789922 !important;
}
span.quote span.spoiler:hover {
	color: #90B52D !important;
}
span.hint {
	text-align: left !important;
}
div.post.reply {
	background: /*[[corner-image]]*/ !important ;
	background-color: #2C2C2C !important;
	color: #698CC0 !important;
	border: solid 1px #575757 !important;
	margin-top: 0.5em !important;
	-moz-border-radius: 5px !important;
	-webkit-border-radius: 5px !important;
	border-radius: 5px !important;
}
div.post.reply.highlighted, div.post.reply.post-hover {
	background: /*[[corner-image]]*/ !important;
	background-color: #2C2C2C !important;
	color: #698CC0 !important;
	border: solid 1px #575757 !important;
	margin-top: 0.5em !important;
	-moz-border-radius: 5px !important;
	-webkit-border-radius: 5px !important;
	border-radius: 5px !important;
  	-moz-box-shadow: #006699 -1px -1px 1em,#006699 1px 1px 2em!important;
	-webkit-box-shadow: #006699 -1px -1px 1em,#006699 1px 1px 2em!important;
	box-shadow: #006699 -1px -1px 1em,#006699 1px 1px 2em!important;
}
div.post.reply.highlighted a.post_no, a.post_no:hover, a:hover pre.unimportant {
	color:#66cc66 !important;
  	text-decoration: none !important; 
	-moz-text-shadow:0 0 15px #66cc66 !important;
	-webkit-text-shadow:0 0 15px #66cc66 !important;
	text-shadow:0 0 15px #66cc66 !important;
}
div.post.reply.highlighted a.post_no, a.post_no:hover {
  	font-weight: bold;
}
span.spoiler {
	background: #575757;
	color: #575757;
}
span.spoiler a {
	color: #575757 !important;
}
span.spoiler a:hover {
  	color:#66cc66 !important;
}
span.spoiler:hover {
	color:#48B0FD !important;
}
span.mark {
  	background-color: #6dd !important;
	color: black !important;
}
span.spoiler span.mark {
	background-color: #575757 !important;
	color: #575757 !important;
}
span.spoiler:hover span.mark, span.mark span.spoiler:hover {
  	color: black !important;
  	background: yellow !important;
}
span.spoiler a {
	color: #575757;
  	background: #575757 ;
}
span.spoiler a:hover {
	color:#66cc66 !important;
}
span.subject {
	background: inherit;
	font-size: 1.2em !important;
	color: #3941AC !important;
	font-weight: bold;
}
code {
	background: #2C2C2C !important;
	color: #CCCCCC !important;
}
h1 {
	clear: both !important;
	font-size: 2.8em !important;
	font-weight: bold !important;
	color: #FFAB3F !important;
  	font-family: "Trebuchet MS";
}
header div.subtitle {
	clear: both !important;
  	font-weight: bold !important;
	color: #FFAB3F !important;
  	font-family: "Trebuchet MS";
}
h2 {
	color: red !important;
  	font-family: "Trebuchet MS";
  	clear: both !important;
  	font-weight: bold !important;
}
pre.unimportant, pre#ascii, code {
	font-family: monospace !important;
}
pre#ascii, pre.unimportant, span.user-stats {
	color: #00FF00 !important;
}
pre.unimportant, pre#ascii {
	-moz-text-shadow:0 0 15px #00FF00 !important;
	-webkit-text-shadow:0 0 15px #00FF00 !important;
	text-shadow:0 0 15px #00FF00 !important;
}
div#body-border-right.body-borders, div#body-border-left.body-borders {
	background-color: transparent !important;
  	width: 3px !important;
  	background: /*[[border-v]]*/ !important;
}
hr {
	height: 3px;
	border-width: 0px;
	background: /*[[border-h]]*/ !important;
}
div.banner {
	width: 95% !important;
	background: #2C2C2C !important;
	text-align: cente !important;
	padding: 2px !important;
	color: #006AB9 !important;
	clear: both !important;
	font-weight: bold !important;
	margin-bottom: .5em !important;
	border: solid 1px #575757 !important;
	-moz-border-radius: 5px !important;
	-webkit-border-radius: 5px !important;
	border-radius: 5px !important;
}
div.banner, div.banner a{
	font-size: 16px !important;
  	font-family: Arial !important;
}
p.fileinfo, p.fileinfo span.unimportant, p.fileinfo span.unimportant * {
	font-size: 13px !important;
}
p.fileinfo span.unimportant * {
	text-decoration: none !important;
}
p.fileinfo, span.omitted {
	color: #999999 !important;
}
p.fileinfo {
	background-color: #212121 !important;
	border: solid 1px #575757 !important;
	padding: 2px !important;
	-moz-border-radius: 8px !important;
	-webkit-border-radius: 8px !important;
	border-radius: 8px !important;
    max-width: 500px !important;
}
span.image_id:before {
	content: " "url(http://i61.fastpic.ru/big/2014/0704/91/d34ce7d6c96f680e7cead9ad71c5fe91.gif)": " !important;
}
span.image_id a[href*=iqdb]:before {
	content: url(http://i61.fastpic.ru/big/2014/0704/8f/2284891c6f172e92853f3eafab38bd8f.png)" " !important;
}
span.image_id a[href*=google]:before {
	content: url(http://i64.fastpic.ru/big/2014/0704/a7/4ee13c4d6c590d4e8963739bb44f2ca7.png)" " !important;
}
span.image_id a[href*=imgops]:before {
	content: url(http://i63.fastpic.ru/big/2014/0704/42/9eb611f98d5001e6a751d2b47ec80042.png)" " !important;
}
span.image_id a[href*=tineye]:before {
	content: url(http://i64.fastpic.ru/big/2014/0704/67/84487a2a51c385bbcc046c33f9739267.png)" " !important;
}
span.image_id a[href*=regex]:before {
	content: url(http://i61.fastpic.ru/big/2014/0704/c9/55312ae522c42e9d57d1773cf2086fc9.png)" " !important;
}
span.image_id a[href*=google]:after, span.image_id a[href*=imgops]:after, span.image_id a[href*=tineye]:after, span.image_id a[href*=regex]:after {
	content: "; " !important;
	color: #999999 !important;
}
span.image_id a[href*=google], span.image_id a[href*=imgops], span.image_id a[href*=tineye], span.image_id a[href*=iqdb], span.image_id a[href*=regex] {
	color: #00FF00 !important;
}
span.image_id a[href*=google]:hover, span.image_id a[href*=imgops]:hover, span.image_id a[href*=tineye]:hover, span.image_id a[href*=iqdb]:hover, span.image_id a[href*=regex]:hover {
	color: #66cc66 !important;
}
div.pages.top, div.pages {
	border-style: none !important;
	background-color: #212121 !important;
	color: #698CC0 !important;
}
div.pages {
	top: -24px !important;
}
div.pages.top {
	top: 0 !important;
}
unimportant {
	font-size: 12px !important;
}
form table tr th {
  	background: none !important;
  	color: #0061AA !important;
    font-size: 14px !important;
	font-weight: bold !important;
	padding: 2px 5px !important;
  	vertical-align: top !important;
}
span#toggle-gallery, input[type="submit"], input[type="button"], span#toggle-frame {
	-o-appearance: none !important;
	-webkit-appearance: none !important;
	-moz-appearance: none !important;
	appearance: none !important;
	background-color: #333333 !important;
	background-image: -moz-linear-gradient(center top,#4A4A4A,#292929,#4A4A4A) !important;
	background-image: -o-linear-gradient(rgb(74,74,74),rgb(41,41,41),rgb(74,74,74)) !important;
	background-image: -webkit-gradient(linear,center top,center bottom,from(#4A4A4A),color-stop(25%,#313131),color-stop(50%,#292929),color-stop(75%,#313131),to(#4A4A4A)) !important;
	-webkit-border-radius: 5px !important;
	-moz-border-radius: 5px !important;
	border-radius: 5px !important;
	border-bottom: 1px solid #151515 !important;
	border-top: 1px solid #151515 !important;
	border-left: 1px solid #000000 !important;
	border-right: 1px solid #000000 !important;
	color: #AAAAAA !important;
	font-weight: bold !important;
}
span#toggle-frame:hover, span#toggle-gallery:hover {
	opacity: 1 !important;
}
span#toggle-frame:hover, span#toggle-gallery:hover, span#toggle-frame, span#toggle-gallery {
	height: 19px !important;
    font-size: 13px !important;
}
span#toggle-gallery:hover , input[type="submit"]:hover, input[type="button"]:hover, span#toggle-frame:hover {
	background-color: #212121!important;
	background-image: -moz-linear-gradient(center top,#151515,#505050,#151515)!important;
	background-image: -o-linear-gradient(rgb(21,21,21),rgb(80,80,80),rgb(21,21,21))!important;
	background-image: -webkit-gradient(linear,center top,center bottom,from(#151515),color-stop(25%,#222222),color-stop(50%,#505050),color-stop(75%,#222222),to(#151515))!important;
	border: 1px solid #000066!important;
	color: #CCCCFF!important;
	box-shadow: inset 0 0 4px 1px #48B0FD;
}
input[type="text"], input[type="password"], input[type="url"], select, textarea  {
	-moz-box-sizing: content-box !important;
	-webkit-box-sizing: content-box !important;
	box-sizing: content-box !important;
	-moz-appearance: none !important;
	-webkit-appearance: none !important;
	appearance: none !important;
	background-color: #111111 !important;
	color: #CCCCCC !important;
	border: 2px solid #545454 !important;
	-moz-border-radius: 5px !important;
	-webkit-border-radius: 5px !important;
	border-radius: 5px !important;
}
textarea:hover, textarea:focus, select:hover, select:focus, input[type="text"]:hover, input[type="password"]:hover, input[type="url"]:hover, input[type="text"]:focus, input[type="password"]:focus, input[type="url"]:focus {
	background-color: #151515 !important;
	color: #EEEEEE !important;
}
#quick-reply input[type="url"], #quick-reply select, #quick-reply textarea {
	-moz-box-sizing: border-box !important;
	-webkit-box-sizing: border-box !important;
	box-sizing: border-box !important;
}
}
