{
 width: 990px !important; 
}

.blst_thumb
{
 margin: 7px;
 width: 32px;
 height: 33px;
 position: absolute;
 overflow: hidden;
 left: 202px;
} 

.blst_title
{
  font-size: 14px;
}

.blst_row
{
 padding: 4px 0px;
 margin: 0px 10px 0px 50px; 
}

.blst_other {
