}

#page_body, #blst_cont
{
  background: #FFFFFF;
}

#header
{
  width: 784px;
  background: #f2f2f2;
  border: none;
}

.summary_tabs, .tabs.t_bar
{
  width: 784px;
}

.summary_tabs
{
 padding: 5px 10px; 
}

.header_top
{
  font-size: 14px;
}

