css:
@-moz-document domain("fritz.box") {
body, #intro_bar, 
#page_content, #login_content, 
.menu_body_outer, 
.area_head, 
#nav_box, #foot_box, 
#menucontent li.submenu_last ul, #menucontent li.submenu ul, hr, div#sso_dropdown_list, 
td[style="width:376px;background-color:#faf8f2;"], 
td[style="width:378px;background-color:#faf8f2;"], 
td[style="width:1px;background-color:#faf8f2;"], 
#tTab_Container2, #tTab_Container1, 
#tTab_Container1 tbody tr td:nth-child(2) {
  background-color: transparent !important;
}

body, #intro_bar, #intro_title_box, 
#intro_bar_left, #intro_bar_middle_info, #intro_back,  #intro_bar_middle, #intro_bar_right, 
#mm_links, #mm_rechts, #mm_mitte_background, #mm_mitte, 
.area_head, 
.blue_bar_left_edge, .blue_bar_right_edge, .blue_bar_back, 
.page_left_bottom, .page_right_bottom, .page_back_bottom, 
#menucontent li.selected, 
.small_bar_left_head, .menu_bar_left_head, 
.small_bar_right_head, .menu_bar_right_head, 
.small_bar_back_head, .menu_bar_back_head, 
.page_left_bottom, .small_bar_left_bottom, .menu_bar_left_bottom, 
.page_right_bottom, .small_bar_right_bottom, .menu_bar_right_bottom, 
.page_back_bottom, .small_bar_back_bottom, .menu_bar_back_bottom {
  background-image: none !important;
}

ul.tabs li.active, ul.tabs li, 
ul.tabs li.active a, ul.tabs li a {
  background: none !important;
}
  
.menu_bar_left_head, 
.menu_bar_right_head, 
.menu_bar_back_head, 
.menu_bar_left_bottom, 
.menu_bar_right_bottom, 
.menu_bar_back_bottom
{
  height: 20px;
}

#page_content, #login_content, .menu_body_outer, div#sso_dropdown_list, span#sso_dropdown.showlist {
  border: none !important;
}




*, .overview_tabs tr, .overview_tabs th, .overview_tabs td {
  font-family: sans-serif !important;
  font-size: 12px;
  font-weight: 400;
  line-height: 1.6;
}

a, #menucontent ul li.selected ul li a {
  color: rgb(89,133,178);
}

#menucontent a {
  font-size: 14px;
  font-weight: 700;
}

body {
  background: #DDD !important;
}

body:before {
  content: "Fist!Box";
  display: block;
  text-align: center;
  font-size: 40px;
  font-weight: 700;
  line-height: 1;
  position: absolute !important;
  top: 0;
  left: 0;
  right: 0;
  padding: 10px 15px;
  text-shadow: #000 0px 1px 0px;
}

body:before, 
.blue_bar_back, .area_head {
  border-bottom: 1px solid rgba(0,0,0,0.5);
  background: #444 !important;
}

body:before, 
.blue_bar_back, .area_head {
  color: #EEE;
}
      
.area_head div.details div {
  background-color: #EEE;
}
      
.area_head {
  height: auto;
}

.area_head div, .area_head div.details div {
  display: inline-block;
  vertical-align: middle;
  margin-top: 0;
}

#intro_bar_box {
  height: 60px;
  overflow: hidden;
}

#nav_box {
  top: 60px;
  height: 30px;
}

.blue_bar_back h2 {
  line-height: 1;
}

#tTab_Container2 th.home_head {
  background-color: transparent;
}

#callsArea, #answerArea, #nasArea, #homeautoArea, 
#page_content_no_menu_box, 
#page_content_menu_box, #tTab_Container2 th.home_head, 
#menucontent .menu_body_outer > ul > li.selected {
  background-color: #EEE !important;
}

#callsArea, #answerArea, #nasArea, #homeautoArea, 
.area_head {
  border-radius: 0;
}

.blue_bar_left_edge, .blue_bar_right_edge {
  display: none;
}

.blue_bar_back {
  margin: 0;
  height: auto;
  padding: 5px 10px 3px 10px;
}

.blue_bar_back h2 {
  margin: 0;
  padding: 0;
  line-height: 1;
}

#tNetdiv, #tCallsdiv, #tFonDevicesdiv, #tFonbookdiv, tTamcallsdiv, #tNet3div {
  height: auto;
}

#menucontent .menu_bar_left_head, 
#menucontent .menu_bar_right_head, 
#menucontent .menu_bar_back_head {
  height: 0;
}

#menucontent .small_bar_left_head {
  width: 0;
}

#menucontent ul, #menucontent li, 
#menucontent .small_bar_back_head {
  margin: 0 !important;
  padding: 0 !important;
}

#menucontent li {
  padding: 3px 6px !important;
}

#menucontent ul li ul li a {
  font-size: 12px;
  font-weight: 400;
}

#menucontent li.selected a, 
#menucontent li.selected li.selected a {
  color: #444;
}

#menucontent > div > ul > li {
  margin-bottom: 5px !important;
}

#menucontent > div > ul > li.selected {
  border-bottom: 1px solid rgba(0,0,0,0.2) !important;
}

hr, 
#menucontent ul ul, 
div.btn_form_foot, #btn_form_foot {
  border-top: 1px solid rgba(0,0,0,0.2) !important;
}

#menucontent ul li ul li.selected {
  left: 0;
}

input[type="button"], input[type="submit"], input[type="reset"], button {
  padding-top: 3px;
  padding-bottom: 3px;
}

ul.tabs li.active {
  background-color: #EEE !important;
}

ul.tabs li {
  border: 1px solid rgba(0,0,0,0.2) !important;
  border-bottom: none !important;
}

select {
  padding: 3px 10px 3px 3px;
  border: none;
  outline: 1px solid rgba(0,0,0,0.3);
}

div#sso_dropdown_list {
  background-color: #FFF !important;
}

.globe_green, .globe_gray, .globe_online, .led_gray, .led_green, .led_red {
  background-position: center center;
}

#into_app_logo {
  top: 5px;
}

#page_content {
  top: 90px;
}

div#sso_dropdown_list {
  -ms-box-shadow: 0 0 3px rgba(0,0,0,0.5);
  -moz-box-shadow: 0 0 3px rgba(0,0,0,0.5);
  -webkit-box-shadow: 0 0 3px rgba(0,0,0,0.5);
  -khtml-box-shadow: 0 0 3px rgba(0,0,0,0.5);
  -o-box-shadow: 0 0 3px rgba(0,0,0,0.5);
  box-shadow: 0 0 3px rgba(0,0,0,0.5);
}

#page_content_no_menu_box {
  -ms-box-shadow: 0 0 30px rgba(0,0,0,0.2);
  -moz-box-shadow: 0 0 30px rgba(0,0,0,0.2);
  -webkit-box-shadow: 0 0 30px rgba(0,0,0,0.2);
  -khtml-box-shadow: 0 0 30px rgba(0,0,0,0.2);
  -o-box-shadow: 0 0 30px rgba(0,0,0,0.2);
  box-shadow: 0 0 30px rgba(0,0,0,0.2);
}

}
