css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.taringa.net") {
#counter-newsfeed {
    color: #fff !important;
}

#menu-section-mi {
    background: url(http://k45.kn3.net/9/F/F/A/D/F/5BF.png) no-repeat 15px 19px !important;
    color: transparent !important;
    text-shadow: none !important;
}

#tool-profile {
    -webkit-box-shadow: 0 6px 12px rgba(0,0,0,.175) !important;
    background-color: #fff !important;
    border: 1px solid rgba(0,0,0,.15) !important;
    border-radius: 4px !important;
    box-shadow: 0 6px 12px rgba(0,0,0,.175) !important;
    font-size: 14px !important;
    list-style: none !important;
    text-align: left !important;
    top: 55px !important;
}

#tool-profile ul li a {
    background-color: #fff !important;
    clear: both !important;
    color: #333 !important;
    display: block !important;
    font-weight: 400 !important;
    height: 17px !important;
    line-height: 1.42857143 !important;
    padding: 7px 20px !important;
    white-space: nowrap !important;
}

#tool-profile ul li a:hover {
    background-color: #f5f5f5 !important;
    color: #262626 !important;
    text-decoration: none !important;
}

#v6search {
    border-left: 1px solid #f2f2f2 !important;
    color: #cccccc !important;
    font-family: 'Georgia' !important;
    font-size: 12px !important;
    font-style: italic !important;
    height: 37px !important;
    top: -2px !important;
}

.ico-notificaciones, .ico-mensajes, .ico-favoritos {
    display: block !important;
    margin: 0px 3px !important;
}

.ico-notificaciones, .ico-mensajes, .ico-favoritos:before {
    top: 5px !important;
}

.loading div.activity-element {
    left: -10px !important;
    min-width: 660px !important;
    text-align: left !important;
}

.nav-principal ul li.active {
    background-color: rgba(0,0,0,0.2) !important;
    box-shadow: inset 0 0 4px rgba(0,0,0,0.2) !important;
    height: 52px !important;
}

.nav-principal ul li:nth-child(1) {
    margin-left: 45px !important;
}

.nav-principal ul li:nth-child(2) {
    margin-left: -100px !important;
    margin-right: 30px !important;
    width: 49px !important;
}

.player-post {
    display: none !important;
}

.user-name {
    font-weight: bold !important;
    margin-top: 6px !important;
}

.v6 #tool-shout {
    left: 185px !important;
    top: 58px !important;
    z-index: 99 !important;
}

.v6 .ico-shout {
    background: url('http://k36.kn3.net/D/8/3/F/6/0/6E9.png') no-repeat 14px 18px #27994d !important;
    border-radius: 0px !important;
    float: right !important;
    height: 52px !important;
    width: 40px !important;
}

.v6 .ico-shout:before {
    content: "" !important;
    font-family: none !important;
    top: 12px !important;
}

.v6 .login .user-actions {
    float: right !important;
}

.v6 .login .user-actions img.user-picture {
    height: 52px !important;
    width: 52px !important;
}

.v6 .nav-principal > ul > li > a {
    color: #fff !important;
    font-size: 13px !important;
    line-height: 51px !important;
    text-shadow: 0 -1px 0 #072636 !important;
}

.v6 .nav-principal > ul > li > a .badge {
    background: none repeat scroll 0% 0% rgba(52, 121, 179, 1) !important;
    margin-left: 15px !important;
    margin-top: 110px !important;
    opacity: 0.8 !important;
    position: absolute !important;
}

.v6 .nav-principal > ul > li.active > a#menu-section-mi {
    left: 2px !important;
    width: 23px !important;
}

.v6 .nav-user {
    padding-top: 0 !important;
}

.v6 .nav-user .login {
    float: left !important;
    right: 211px !important;
    width: 450px !important;
}

.v6 .nav-user .login .user-actions {
    background-color: transparent !important;
    height: 45px !important;
    margin: 0 !important;
    padding-top: 0 !important;
}

.v6 .nav-user .login .user-actions .user-action .ico-favoritos:before {
    background: url(http://k32.kn3.net/B/0/B/3/3/8/F39.png) no-repeat 1px -1px !important;
    content: "" !important;
}

.v6 .nav-user .login .user-actions .user-action .ico-mensajes:before {
    background: url(http://k32.kn3.net/1/1/D/5/C/4/8E9.png) no-repeat 1px 0 !important;
    content: "" !important;
}

.v6 .nav-user .login .user-actions .user-action .ico-notificaciones:before {
    background: url(http://k31.kn3.net/D/4/2/1/5/E/DD2.png) no-repeat 1px 0 !important;
    content: "" !important;
}

.v6 .nav-user .login .user-actions .user-action > a[class^="ico"] .count {
    background: url(http://k43.kn3.net/F/7/8/4/7/8/84D.png) repeat-x !important;
    border: 1px solid #6d0000 !important;
    border-radius: 20px !important;
    font-size: 11px !important;
    font-weight: bold !important;
    height: 10px !important;
    padding: 3px 3px 0px 2px !important;
    top: 0px !important;
    width: 14px !important;
    z-index: 99999 !important;
}

.v6 .nav-user .login .user-actions .user-action > a[class^="ico"] .count:after, .v6 .nav-user .login .user-actions .user-action > a[class^="ico"] .count:before {
    display: none !important;
}

.v6 .nav-user .login .user-actions .user-action > a[class^="ico"]:before {
    padding: 1px 10px !important;
}

.v6 .nav-user .login .user-actions .user-action>a {
    padding: 4px 3px !important;
}

.v6 .nav-user form.buscar {
    background: none !important;
    border: none !important;
    float: right !important;
    margin-right: 0px !important;
    margin-top: 0px !important;
}

.v6 .nav-user form.buscar p button:before {
    color: #d9d9d9 !important;
    font-size: 12px !important;
}

.v6 .tools#tool-profile:after {
    display: none !important;
}

.v6 header .v6-wrapper {
    height: 52px !important;
    max-width: 1000px !important;
    min-width: 1000px !important;
    width: 1000px !important;
}

.v6 header h1 {
    margin-top: 5px !important;
}

.v6 header h1 a {
    background: url(http://k40.kn3.net/4/B/8/C/D/D/AA4.png) no-repeat 0 9px !important;
    height: 42px !important;
}

body.v6-beta {
    padding-top: 52px !important;
}

header {
    background: url(http://k45.kn3.net/5/7/D/D/0/6/AA8.png) #226599 !important;
    border-bottom: none !important;
    border-top: none !important;
    height: 52px !important;
}

nav.nav-user {
    width: 239px !important;
}

span.badge {
    background: none repeat scroll 0% 0% rgba(52, 121, 179, 1) !important;
    left: 9px !important;
    opacity: 0.8 !important;
    top: -99px !important;
}

span.title {
    display: none !important;
}

div.Feed-controls.cont-filterH.recent {
    background-color: #f7f7f7 !important;
    background-image: none !important;
    border-radius: 3px !important;
    border: 1px solid #c7c7c7 !important;
    box-shadow: none !important;
}

.cont-filterH ul li a.active {
    background-image: none !important;
    background: #dfdfdf !important;
    box-shadow: none !important;
    color: #5f5f5f !important;
    text-shadow: 0 1px 0 #fff !important;
    height: 31px !important;
    border-right: 1px solid #b8b8b8 !important;
}

.cont-filterH ul li {
    background: none !important;
    width: 25% !important;
}

.cont-filterH ul li a {
    border-right: 1px solid #b8b8b8 !important;
    color: #8c8c8c !important;
    padding: 13px 0 0px !important;
    text-align: center !important;
    height: 32px !important;
    width: 100% !important;
}


.my-shout-attach-options li a {
    padding: 7px 10px !important;
    background: none !important;
    box-shadow: none !important;
    border: 1px solid rgba(0,0,0,0.3) !important;
}

.my-shout-attach-options li.video a:hover {
    border: 1px solid #e66254 !important;
}

.my-shout-attach-options li.image a:hover {
    border: 1px solid #2ba1e5 !important;
}

.my-shout-attach-options li.link a:hover {
    border: 1px solid #ffa200 !important;
}

.my-shout-attach {
    top: -5px !important;
}

li.juegos a:before {
    content: none !important;
}

.v6 .nav-principal>ul>li.juegos {
    background: none !important;
}
.my-shout-attach-options li a div {
    line-height: 0 !important;
}


}
