css:
/*
 * MyDealz & hukd Wide Style (1280px)
 * Author      : Zwergie
 * Created     : 05.07.2014
 * Version     : 1.0
 * basiert auf dem Style von IJay "MyDealZ.de + HUKD Redesign Fixes"
*/

@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document
    domain("mydealz.de") {

html * {
    font-family:Arial !important;
    font-size: 16px;
    line-height: 1.2em;
}
    
.page-content {
    min-width:1280px !important;
}
    
h1, .thread-degree .degreeValue-unit {
    font-size: 20px !important;
}
    
h2 * {
    font-size: 20px !important;
}

#main {
    background-color: #FEFEFE;
} 

.pagi-link, .avatar-link {
    font-size: 1em !important;
}

.degreeValue {
    font-size: 28px;
}
    
.button--type-teaser {
    color: white;
    text-shadow: none;
}

.section-subTitle * {
    font-size: 22px;
    font-weight: unset;
}

* {
    font: initial;
}
        
.hList-item {
    margin-bottom: 0.4em;
}

.section--paddedSlim {
    padding: 10px 370px !important;
}
        
li.comments-item {
    padding: 15px 15px 0px 15px !important;
}

.thread {
    padding: 8px 0px 0px 20px !important;
}
    
.comments-head {
    padding: 0px 10px 0px 0px;
}
    
.comments-footer, .thread-main footer {
    text-align: center !important;
    padding-top: 4px !important; }

.space--right-7 {
    padding-right: 0.5em;
}

.section--divided + .section--divided {
    margin-top: -4px;
}    
.comments-footer button, .reasons em {
    font-weight: normal;
    font-size: 12px;
    }

.reasons br {
    display:none;
}

.reasons {
    margin-top:5px;
}
    
.pagi-link {
    padding:5px;
}
    
.button--type-teaser, .button--type-teaser:hover, .button--  type-teaser:focus, .button--type-teaser:active {
    background: none;
    background-color: #439719;
    border-color: rgb(55, 133, 7);
    line-height:17px;
}
.button--type-teaser .button-icon {
    line-height:24px;
    width:12px;
}
    
.sideList-item {
    line-height:1.3em;
}
    
.navTrigger2-row, .navTrigger2, .nav2Morph, .nav2Morph button, .navTrigger2-row a {
    height: 35px !important;
    line-height: 35px !important;
}

.degreeVote-up, .degreeVote-down {
    height: 12px;
    line-height: 15px;
}
    
.comments-item header .fIcon--like {
    font-weight:bold;
    color: rgb(29, 69, 109);
}
    
.bbcode_quote {
    margin-bottom: 0px !important;
}
.bbcode_quote_head {
    font-weight:bold;
    margin:0px !important;
}
    
.bbcode_quote_body br:empty {
    display:none;
}
    
.comment-options li button, .thread-meta a, footer button.link, footer a.link, .thread-title a {
    color: rgb(29, 69, 109) !important;
}
    
.degreeMeter, .degreeMeter-progress {
    height:17px;
}
    
.thread-degree .degreeValue {
    text-align:left;
    line-height:5px;
}
    
.thread-degree .degreeValue-unit {
    font-size:20px;
}
    
.degreeValue-new {
    height:17px;
    padding:2px 6px;
    font-size:12px;
}
    
.thread-deal .imageFrame {
    background:none !important;
    border:0px;
    height:160px;
    vertical-align:top !important;
}
    
.thread-deal .imageFrame img {
    padding-bottom:25px;
}
    
.thread-deal .button {
    margin-top:0px !important;
}
    
.section-sub {
    margin-right:5px;
}
    
.comments-footer {
    padding:10px;
}

    
    /*optional*/
    
.hList-item .fIcon--comments, .fIcon--shares, .topBar {
    display: none;
}
    
form.newsletter {
    display:none;
}
    
    /*
.input {
    border-radius:0px;
}
    */
}
