css:
@-moz-document domain("n0l.ch") {
html, body, footer, div#menu.menu li {
	background:#0f0f0f !important;
 	color:#969696 !important;
   	font-family:sans-serif !important;
}
* {
	text-shadow: none !important;
}
*:not(em) {
	font-style: normal !important;
}
div.body.hyphenate, div#menu.menu li, textarea {
  	font-size: 12px !important;
  	font-family: sans-serif !important;
}
p.fileinfo, .intro, .omitted, summary, .toolong {
   	font-size:11px !important;
}
span.omitted, summary, .toolong {
	color: #666 !important;
}
span.subject {
	font-size: 16px !important;
  	color: #2d9ee9 !important;
}
span.mentioned.unimportant:before {
	content: " Replies:" !important; 
}
span.mark, span.mark code {
	color: black !important;
  	background: #6dd !important;
}
span.spoiler, span.mark span.spoiler, span.spoiler code, span.spoiler span.mark {
	background: black !important;
  	color: black !important;
}
h2 {
	color: red !important;
}
span.spoiler:hover, span.spoiler:hover code {
	background: black !important;
  	color: white !important;
}
span.spoiler:hover span.mark, span.mark span.spoiler:hover {
	color: white !important;
  	background: brown !important;
}
span.spoiler a {
	background: black !important;
  	color: black !important
}
span.spoiler a:hover {
	color:#2d9ee9 !important;
}
code {
	color: white !important;
}
div.post.reply.highlighted, div.post.reply.post-hover {
  	-moz-box-shadow: #e9782d -1px -1px 1px,#e9782d 1px 1px 1px!important;
	-webkit-box-shadow: #e9782d -1px -1px 1px,#e9782d 1px 1px 1px!important;
	box-shadow: #e9782d -1px -1px 1px,#e9782d 1px 1px 1px!important;
}
a, h1, div.subtitle {
	color:#e9782d !important;
   	text-decoration:none !important;
}
a:hover, a.post_no:hover {
  	color:#2d9ee9 !important;
  	letter-spacing: 0px !important;
}
a.selected {
        color: #969696 !important;
}
ul#linklist a:hover {
	background:#161616 !important;
}
span.quote, span.quote span.spoiler:hover {
 	color:#98e82c !important;
}
hr {
	border-top:#070707 1px solid !important;
  	border-bottom:#262626 1px solid !important;
  	height:0px !important;
}
div.post.reply, div.banner, tr th, div.pages {
  	font-family:sans-serif !important;
  	color: #969696 !important;
  	background:#161616 !important;
 	border:1px solid #0d0d0d !important;
  	-webkit-box-shadow:#0f0f0f 0 0 20px 1px inset;
   	-moz-box-shadow:#0f0f0f 0 0 20px 1px inset;
   	box-shadow:#0f0f0f 0 0 20px 1px inset;
}
div.post.reply {
	-moz-border-radius: 0px !important;
	-webkit-border-radius: 0px !important;
	border-radius: 0px !important;
}
div.pages {
	top: -24px !important;
}
div.pages.top {
	top: 0px !important;
}
body.noborder li, div#blotter.blotter {
	color: #2d9ee9 !important;
  	font-size: 14px !important;
  	background-color: #0f0f0f !important;
}
.filetitle {
  	color:#2d9ee9 !important;
}
#quick-reply input[type="url"], #quick-reply select, #quick-reply textarea {
	-moz-box-sizing: border-box !important;
	-webkit-box-sizing: border-box !important;
	box-sizing: border-box !important;
}
span#toggle-frame:hover, span#toggle-gallery:hover, span#toggle-frame, span#toggle-gallery {
	height: 19px !important;
    font-size: 13px !important;
}
span#toggle-frame:hover, span#toggle-gallery:hover {
	opacity: 1 !important;
}
textarea:hover, textarea:focus, select:hover, select:focus, input[type="text"]:hover, input[type="password"]:hover, input[type="url"]:hover, input[type="text"]:focus, input[type="password"]:focus, input[type="url"]:focus, span#toggle-gallery:hover , input[type="submit"]:hover, input[type="button"]:hover, span#toggle-frame:hover {
	background-color: #161616 !important;
	color: #969696 !important;
}
input[type="text"], input[type="password"], input[type="url"], select, textarea, span#toggle-gallery, input[type="submit"], input[type="button"], span#toggle-frame  {
	-moz-box-sizing: content-box !important;
	-webkit-box-sizing: content-box !important;
	box-sizing: content-box !important;
	-moz-appearance: none !important;
	-webkit-appearance: none !important;
	appearance: none !important;
	background-color: #0f0f0f !important;
	color: #969696 !important;
	border: #262626 1px solid !important;
	-moz-border-radius: 0px !important;
	-webkit-border-radius: 0px !important;
	border-radius: 0px !important;
}
}
